input:hover {
    border-color: #00C853;
}
.form-control {
    background-color: #616161;
    color: #eee;
}
.dropdown-menu {
    background-color: #616161;
}
.dropdown-menu>li>a {
    color: #ddd;
}
.dropdown ul.dropdown-menu li.name a {
    color: #00C853;
}
.dropdown ul.dropdown-menu li a:hover {
    background-color: #00C853;
    color: #fff;
}
.dropdown ul.dropdown-menu li a i {
    color: #2979FF;
}
.modal-content {
    background-color: #616161;
}
#modalNotification .modal-dialog .modal-content .modal-header #notification-clear {
    background-color: #4d4d4d;
}
#notification-modal .notification-modal-single-header-username {
    color: #00C853;
}
#notification-modal .notification-modal-single-description {
    color: #eee;
}
#notification-modal .notification-modal-single:hover .notification-modal-single-description {
    color: #3a3e64;
}
.stream-menu {
    background-color: #616161;
}
.stream .form-content {
    background-color: #616161;
}
.comments.account-comments .comments-single,
.comments.stream-post .comments-single {
    background-color: #616161;
}
.stream .single-post {
    background-color: #616161;
}
.user-header-menu {
    background: #616161;
    border: 1px solid #424242;
}
.user-header-menu a {
    color: #eee;
}
.user-header-menu a.active,
.user-header-menu a:hover {
    color: #00C853;
}
.user-header-menu a .count {
    color: #f5f5f5;
}
.post-single .re-strim {
    color: #ddd;
}
/*#5C6BC0*/

.comments-single-re-title a {
    color: #03a9f4;
}
.comments-single-re-title a:hover {
    color: #5C6BC0;
}
.page {
    background-color: #616161;
}
.comments-single-content-vote-link a {
    color: #ddd;
}
.comments-single-vote-count.upVote,
.comments-single-vote-worth-count.upVote {
    background-color: #00C853;
}
.user-follow-single {
    background-color: #616161;
}
.user-follow-single-name {
    color: #00C853;
}
.user-follow-single-username {
    color: #03a9f4;
}
.user-follow-single-username:hover {
    color: #5C6BC0;
}
.page .account-balances-single .name {
