}
.table-hover>tbody>tr:hover {
    background-color: #ddd;
    color: #3a3e64;
}
.stream-menu a.active,
.stream-menu a:hover {
    color: #00C853;
}
.box {
    background-color: #616161;
}
.box.login .title {
    color: #eee;
}
.box.login .new-account-title {
