}
.page-content-preview .content-left .image-title,
.page-content-preview-text .content-left .image-title {
    color: #ddd;
}
.page-content-preview .content-left .input-title,
.page-content-preview-text .content-left .input-title {
    color: #ddd;
}
.dropdown-menu.textcomplete-dropdown .textcomplete-item a {
    color: #00C853;
}
.dropdown-menu.textcomplete-dropdown .textcomplete-item a .count {
    color: #ddd;
}
.post-single .vote-content .voteUp .count {
    color: #fff;
}
.stream .form-content {
    border: 1px solid #424242;
}
#notification-modal .notification-modal-single:nth-child(2n) {
    background-color: #616161;
}
