css:
@-moz-document domain("forum.football365.com"){
/**** FANCY FONTS ****/

@font-face {
  font-family: 'Lato';
  font-style: normal;
  font-weight: 300;
  src: local('Lato Light'), local('Lato-Light'), url(https://fonts.gstatic.com/s/lato/v14/22JRxvfANxSmnAhzbFH8PgLUuEpTyoUstqEm5AMlJo4.woff2) format('woff2');
}
@font-face {
  font-family: 'Lato';
  font-style: normal;
  font-weight: 400;
  src: local('Lato Regular'), local('Lato-Regular'), url(https://fonts.gstatic.com/s/lato/v14/MDadn8DQ_3oT6kvnUq_2r_esZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
}    
@font-face {
  font-family: 'Lato';
  font-style: normal;
  font-weight: 900;
  src: local('Lato Black'), local('Lato-Black'), url(https://fonts.gstatic.com/s/lato/v14/lEjOv129Q3iN1tuqWOeRBgLUuEpTyoUstqEm5AMlJo4.woff2) format('woff2');
}

html,
body {
    max-width: 1000px;
    margin: auto;
    background-color: #0e3a53;
}
/* GETTING RID OF ADVERTS*/

.fixedfluidfixed-content {
    width: calc(100%);
    float: left;
}
.fixedfluidfixed-right {
    display: none !important;
}
#page-footer > div:first-child {
    display: none !important;
}

/**** HOME THREAD TITLES & PAGINATION ****/

ul.topiclist.topics .pagination::before {
    content: "(";
    color: #777777;
}
ul.topiclist.topics .pagination::after {
    content: ")";
    color: #777777;
}
ul.topiclist.topics .pagination li a::after {
    content: ",";
}
ul.topiclist.topics .pagination li a {
    border: 0 none;
    font-size: 1em !important;
    font-weight: 400;
    background-color: transparent;
}
ul.topiclist.topics .pagination li a:hover {
    background-color: transparent;
    font-weight: bold;
    color: #0e3a53;
    text-decoration: underline;
}
.pagination li:last-of-type a:after {
    content: "" !important;
}
ul.topiclist.topics a.topictitle {
    font-size: 1.1em;
}
/**** END HOME THREAD TITLE & PAGINATION ****/

/*** MAKING FORUM PRETTY AGAIN ***/

* {
 font-family: Lato !important;
}
 ul.topiclist.topics .row .list-inner, ul.topiclist.cplist .row .list-inner {
     padding-top: 5px;
     padding-bottom: 5px;
 }   
.forumbg {
    border-radius: 0;
    margin-bottom: 5px;
}
.forumbg,
.forabg {
    border: 0px solid #f2f2f2;
    padding: 0px;
}
.forumbg.announcement {
    margin-top: 7px;
}
.body {
    border-radius: 0;
}
a.topictitle,
a.forumtitle {
    line-height: 20px;
    text-decoration: none;
}
h2.forum-title {
    display: none;
}
.headerbar {
    display: none;
}
.navbar {
    background-color: #d9e6f2;
    border-radius: 0;
}
.panel {
    border-radius: 0 !important;
}
a {
    color: #0e3a53;
    font-family: Lato !important;
}
div.forumbg li.row div.list-inner div.responsive-hide {
    font-size: 10px;
    padding-top: 0px;
}
.forumbg .topiclist .header,
.forabg .topiclist .header {
    background-color: #0e3a53;
    font-size: 11px;
    font-weight: 300;
    padding-bottom: 5px;
    padding-left: 23px;
    padding-top: 8px;
}
a.topictitle {
    font-weight: 900;
    font-size: 13px !important;
}
ul.topiclist .row .topic_read a.topictitle,
ul.topiclist .row .topic_read_hot a.topictitle,
ul.topiclist .row .topic_read_hot_mine a.topictitle,
ul.topiclist .row .topic_read_mine a.topictitle {
    font-weight: normal !important;
}
ul.topiclist .row.bg1 {
    background-color: #ffffff !important;
}
ul.topiclist .row.bg2 {
    background-color: #ffffff !important;
}
ul.topiclist .row .sticky_read,
ul.topiclist .row .sticky_unread,
ul.topiclist .row .sticky_read_mine,
ul.topiclist .row .sticky_unread_mine,
ul.topiclist .row .sticky_unread_locked,
ul.topiclist .row .sticky_read_locked {
    background-color: #d9e6f1;
}
ul.topiclist .row .sticky_read a.topictitle,
ul.topiclist .row .sticky_read_mine a.topictitle {
    font-weight: normal !important;
}
ul.topiclist li.row {
    border-bottom: none;
    border-top: none;
}
li.row dd {
    border-color: transparent;
}
li.row:hover dd {
    border-color: transparent;
}
 /**** REMOVAL OF ICONS EVERYWHERE ****/
 .forum_unread, .forum_read {
     background-image: none !important;
 }

 ul.topiclist .row .topic_read, ul.topiclist .row .topic_unread, ul.topiclist .row .topic_unread_hot, ul.topiclist .row .topic_read_hot, ul.topiclist .row .topic_unread_hot_mine, ul.topiclist .row .topic_read_hot_mine, ul.topiclist .row .topic_unread_mine, ul.topiclist .row .topic_read_mine, ul.topiclist.topics .sticky_read_mine, ul.topiclist.topics .sticky_unread, ul.topiclist.topics .sticky_unread_mine, ul.topiclist.topics .sticky_unread_locked, ul.topiclist.topics .sticky_read_locked, ul.topiclist.topics .topic_unread_locked, ul.topiclist.topics .topic_read_locked, ul.topiclist.topics .topic_read_locked_mine, ul.topiclist.topics .topic_unread_locked_mine, ul.topiclist.cplist .sticky_read_mine, ul.topiclist.cplist .sticky_unread_mine, ul.topiclist.topics .sticky_read, ul.topiclist.cplist .global_read_locked, ul.topiclist.cplist .global_unread_locked {
     background-image: none !important;
 }
 ul.topiclist.topics dt, ul.topiclist.cplist dt {
     background-image: none !important;
 }
 ul.topiclist.topics .row .list-inner, ul.topiclist.cplist .row .list-inner {
     padding-left: 15px;
     padding-top: 3px;
     padding-bottom: 3px;
 }
 .announcement ul.topiclist.topics .list-inner {
     padding-left: 45px !important;
 }
 /**** END ICON REMOVAL ****/

/* FRONT FORUM PAGE */
a.forumtitle{
    font-size: 14px;
    font-weight: 900;
}
ul.topiclist.forums .row {
     background-color: #d9e6f2;
     padding-top: 10px;
     padding-bottom: 10px;
 }
 .forabg {
     border-radius: 0;
 }
 .forabg .topiclist .header {
     padding-left: 10px;
 }
 .stat-block, form.headerspace {
     font-family: Lato !important;
     font-size: 11px !important;
     ;
 }

 /* INSIDE THREAD STUFF */

 .post {
     border-top: 0px solid #b5cee3;
     border-left: 10px solid #b5cee3;
     border-radius: 0;
     margin-top: 10px;
 }
.post .content {
    font-family: Lato !important;
    font-size: 12 !important;
    line-height: 1.5 !important;
}
h2 {
    font-family: Lato !important;
    font-weight: 300 !important;
    padding: 12px 0 12px 0;
    color: #0e3a53;
}
.panel {
    background-color: #e8f2f8 ;
}
.content h2, .panel h2 {
    color: #0e3a53;
    border-bottom-color: #b5cfe1;
}
    fieldset.polls dl {
    border-top-color: #b5cfe1 !important;
    border-style: dotted;
    border-top-width: 1px !important;
    color: #666666;
    padding: 15px 0px 5px 10px ;
}

 .postbody h3 {
     font-size: 13px;
 }
 .postbody h3.first {
     font-size: 13px;
 }
a.username {
     font-size: 12px;
 }
 .post.bg1 {
     background-color: #e8f2f9;
 }
 .post.bg2 {
     background-color: #e8f2f9;
 }
 .content blockquote {
     background-color: #d9e6f1 !important;
     border-left: 2px solid #b5cfe1;  
     border-right: 0px solid #b5cfe1;
     border-top: 0px solid #b5cfe1;
     border-bottom: 2px solid #b5cfe1;
     padding: 12px;
 }
blockquote cite {
     padding-top: 2px;
     padding-bottom: 14px;
}
 blockquote.uncited {
     padding-top: 24px !important;
}
.notice {
    border-top-color: #b5cfe1 !important;
    border-top: 1px dotted;
    color: #0e3a53;
}
/*** WRITE A POST STUFF ***/
h2.posting-title{
    font-family: Lato !important;
    font-size: 13 !important;
    font-weight: 300 !important;
    padding-bottom: 12px;
}
#postingbox.panel {
    background-color: transparent;
}
#message-box textarea { 
	font-family: Lato !important;
    background-color: transparent;
    border: 1px solid #b5cee3;
    margin-top: 8px;
}
.inputbox {
    font-family: Lato !important;
    background-color: transparent;
    border: 1px solid #b5cee3;
}
.inputbox:hover, .inputbox:focus {
    border: 1px solid #b5cee3;
    outline-style: none;
}
.bg2 {
    background-color: #d9e6f1;}
.bg3 {
    background-color: #d9e6f1;}
#tabs {
    display: none !important;}
dt label {
    font-family: Lato !important;
    font-weight: 900;
    text-align: left;
}
ul.navlinks {
    border-top: 0px solid transparent;
}
ul.linklist.bulletin > li:before {
    color: #0e3a53;
}
.breadcrumbs .crumb:before {
    color: #0e3a53;
}
.postprofile {
    border: 0px;
    color: #0e3a53;
}
.postprofile strong {
    font-weight: bold;
    color: #0e3a53;
}
p.author {
    padding: 5px 0 9px 0;
    color: #0e3a53;
}
hr {
    border:0px;
}


/*** FIX FOR OLE MEDIA BEING MASSIVE TWATS ***/
a#logos {
   display:none;
}

.headerbar {
    display:block;
    padding:0px !important;   
}

.fixedfluidfixed-content {
   width: calc(100%);
   float: left;
}
     
.fixedfluidfixed-right {
   display: none !important;
}
     
#page-footer > div:first-child {
   display: none !important;
}

#wrap {
    border:none;
}
}
