css:
@-moz-document domain("tcl.tk") {
body {
    background: #6d6d6d;
/*    color: #888888; */
    margin-left: 2%;
    margin-right: 2%;
    font-family: Verdana, Arial, "Lucida Sans", sans-serif;
}
h1.title {
    color: #888888;
    font-size: x-large;
    text-align: center;
}
h2, h3, h4 {
    background-color: #888888;
    font-size: large;
}
code.keyword {
    font-family: monospace;
    font-weight: normal;
}
code.command {
    font-family: monospace;
    font-weight: bold;
}
code.arg {
    font-family: monospace;
    font-style: italic;
}
code.optarg:before {
    content: "?";
}
code.optarg:after {
    content: "?";
}
code.optarg {
    font-family: monospace;
    font-style: italic;
}
code.reqarg {
    font-family: monospace;
    font-style: italic;
}
pre.example {
    padding: 1%;
    margin: 1%;
    background-color: #6d6d6d;
    border: 1px solid #888888;
    font-family: monospace;
    display: block;
    white-space: pre;
}
div.nav {
    text-align: center;
    background-color: #6d6d6d;
    border: 1px solid #888888;
    padding: 1%;
    margin: 3%;
    margin-left: 15%;
    margin-right: 15%;
}
a {
    color: #000066;
}
a:hover {
    color: #990000;
}
div.nav a {
    color: #000066;
    text-decoration: none;
    font-weight: bold;
    margin: 1em;
}
div.nav a:hover {
    text-decoration: underline;
}
div.nav .disabled {
    color: #666666;
    font-weight: bold;
    margin: 1em;
}
table {
    background-color: #888888;
    margin: 1%;
    padding: 1%;
}
th {
    text-align: left;
}
td {
    padding-right: 1em;
}
dt {
    font-weight: bold;
}
div.warning {
    padding-left: 1%;
    padding-right: 1%;
    margin: 1%;
    background-color: #888888;
    border: 1px solid #ffcccc;
}
pre {
    background-color: #888888;
    border-top: 1px solid #6A6A6A;
    border-bottom: 1px solid #6A6A6A;
    padding: 1em;
    overflow: auto;
}
}
