:root{--color-red:rgb(213,62,79);--color-orange:rgb(252,141,89);--color-amber:rgb(254,224,139);--color-yellow:rgb(230,245,152);--color-green:rgb(153,213,148);--color-blue:rgb(50,136,189);--color-blue-drupal:rgb(6, 120, 190);--color-blue-drupal-accent:rgb(83, 176, 235);--color-gray:rgb(85, 85, 85);--color-gray-light:rgb(158, 158, 158);--color-gray-lighter:rgb(221, 221, 221);--color-gray-lightest:rgb(237, 237, 237);--color-gray-dark:rgb(51, 51, 51);--color-gray-darker:rgb(33, 33, 33);--color-gray-darkest:rgb(30, 30, 30);--bg-color-error-whitespace:var(--color-yellow);--bg-color-error-tabs:var(--color-amber);--bg-color-error-eof:#000;--fg-color-error-eof:#fff}#code .pre-selected td,#code .selected td,#code tr:hover td{--color-red:rgba(213,62,79, 0.85);--color-green:rgba(153,213,148, 0.85);--color-blue:rgba(50,136,189, 0.85)}

:root{--font-default:-apple-system, BlinkMacSystemFont, 'Ubuntu', 'Roboto', sans-serif;--font-monospace:'Operator Mono', 'Ubuntu Mono', 'Monaco', 'FiraCode-Light', 'DejaVu Sans Mono', 'Consolas', 'Courier New', monospace;--font-drupal:'Lucida Grande', 'DejaVu Sans', 'Bitstream Vera Sans', Verdana, Arial, sans-serif;--bar-bottom:40}#dreditor{border:0 !important;color:#000 !important;line-height:1.5 !important}#dreditor-content{background:var(--color-gray-darker);border-color:rgba(255,255,255,0.5);height:calc(100% - (var(--bar-bottom) * 1px)) !important}#dreditor #bar{font-size:.875rem;-webkit-overflow-scrolling:touch;background:var(--color-gray-darkest);bottom:calc(var(--bar-bottom) * 1px) !important;color:#fff;height:calc(100% - (var(--bar-bottom) * 1px)) !important;line-height:1.5;overflow-x:hidden;overflow-y:scroll;padding:0 20px 50px 20px;top:0}#dreditor #bar h3{font-size:.9375rem;background:#000;border-bottom:solid 1px var(--color-gray-dark);border-top:solid 1px var(--color-gray);color:#fff;font-family:var(--font-default);font-weight:300;line-height:1 !important;margin:0 -20px 10px -20px !important;padding:15px 10px}#dreditor #bar .resizer.resizing{background:var(--color-bg-bar-border)}#dreditor-actions{-webkit-box-align:stretch !important;-ms-flex-align:stretch !important;align-items:stretch !important;background:var(--color-gray-darker) !important;border-top:solid 1px #000;display:-webkit-box !important;display:-ms-flexbox !important;display:flex !important;height:calc(var(--bar-bottom) * 1px) !important;left:0 !important;margin:0 !important;max-width:none !important;padding:0 !important;right:0 !important;width:100vw !important}#dreditor-wrapper[style*='height: 34px;']{height:calc(var(--bar-bottom) * 1px) !important}#dreditor-wrapper[style*='height: 34px;'],#dreditor-wrapper[style*='height: 34px;'] #dreditor{background:transparent !important}#dreditor-wrapper[style*='height: 34px;'] #dreditor-actions{background:transparent !important;display:-webkit-inline-box !important;display:-ms-inline-flexbox !important;display:inline-flex !important;padding:1px !important;pointer-events:none;width:auto !important}#dreditor-wrapper[style*='height: 34px;'] #dreditor-actions input.dreditor-button{background:#000 !important;outline:solid 1px #333 !important;pointer-events:all}#dreditor-wrapper[style*='height: 34px;'] #dreditor-actions input.dreditor-button:hover,#dreditor-wrapper[style*='height: 34px;'] #dreditor-actions input.dreditor-button:focus{background:#333 !important}#dreditor ul#menu{font-size:.8125rem;font-family:var(--font-default);margin:0 -20px 0 0}#dreditor ul#menu a{color:#fff !important}#dreditor ul#menu a:focus,#dreditor ul#menu a:hover{text-decoration:underline !important}#dreditor ul#menu>li{border-bottom:solid 1px rgba(255,255,255,0.075) !important;margin-bottom:0.3em !important;padding:0.2em !important}#dreditor ul#menu>li a{background-color:transparent !important;padding:0 !important}#dreditor ul#menu>li li{font-size:.75rem;border:0 !important;font-family:var(--font-monospace);font-style:italic;font-weight:200 !important;line-height:1.7 !important;padding:0 0 0 1em !important}#dreditor ul#menu li li a{color:var(--color-gray-light) !important}#dreditor #diffstat{font-size:.75rem;color:var(--color-gray-light);margin:0 -20px !important;padding:0 20px 15px 20px}#dreditor #diffstat+a.dreditor-application-toggle{font-size:.75rem;border-color:var(--color-gray-light);color:#fff;padding:0.231em 0.615em;position:absolute;right:10px;top:10px}#dreditor #diffstat+a.dreditor-application-toggle:hover,#dreditor #diffstat+a.dreditor-application-toggle:focus{color:var(--color-gray-light)}#dreditor #code *,#dreditor #code td,#dreditor #code span.pre,#dreditor #code tr.new{font-family:var(--font-monospace) !important;font-size:0.9375rem !important;font-weight:200 !important;line-height:1.2 !important}#dreditor #code tr.comment .pre{font-style:italic !important}#dreditor #code{background:#fff !important;border:0 !important;margin-bottom:0 !important}#dreditor #code tr,#dreditor #code tr:hover,#dreditor #code tr.has-comment,#dreditor #code tr.has-comment:hover,#dreditor #code tr.pre-selected:hover,#dreditor #code tr.pre-selected,#dreditor #code tr.selected,#dreditor #code tr.selected:hover{background:#fff !important;border:0 !important}#dreditor #code td,#dreditor #code td.ln,#dreditor #code tr.has-comment td,#dreditor #code tr.has-comment td.ln{background:#fff !important;border:0 !important;color:var(--color-gray) !important;overflow:auto !important}#dreditor #code tr td,#dreditor #code tr td.ln{background:#fff !important;border:0 !important;color:var(--color-gray) !important;overflow:auto !important}#dreditor #code tr:hover td,#dreditor #code tr:hover td.ln,#dreditor #code tr.has-comment:hover td,#dreditor #code tr.selected td,#dreditor #code tr.selected td.ln,#dreditor #code tr.selected:hover td,#dreditor #code tr.pre-selected td,#dreditor #code tr.pre-selected:hover td,#dreditor #code tr.pre-selected td.ln{background:var(--color-gray-lighter) !important;border:0 !important;color:#000 !important}#dreditor #code tr:hover td{-webkit-box-shadow:none !important;box-shadow:none !important}#dreditor #code tr.file td,#dreditor #code tr.file td.ln,#dreditor #code tr.file:hover td,#dreditor #code tr.file:hover td.ln,#dreditor #code tr.file.has-comment td,#dreditor #code tr.file.pre-selected td,#dreditor #code tr.file.selected td{background:var(--color-blue) !important;border:0 !important;color:#fff !important}#dreditor #code tr.file a,#dreditor #code tr.file a:hover,#dreditor #code tr.file:hover a,#dreditor #code tr.file.has-comment:hover a,#dreditor #code tr.file.pre-selected:hover a,#dreditor #code tr.file.selected:hover a{background:none !important;border:0 !important;color:#fff !important}#dreditor #code tr.new td,#dreditor #code tr.new td.ln,#dreditor #code tr.new:hover td,#dreditor #code tr.new:hover td.ln,#dreditor #code tr.new.has-comment td,#dreditor #code tr.new.pre-selected td,#dreditor #code tr.new.selected td{background:var(--color-green) !important;border:0 !important;color:#000 !important}#dreditor #code tr.old td,#dreditor #code tr.old td.ln,#dreditor #code tr.old:hover td,#dreditor #code tr.old:hover td.ln,#dreditor #code tr.old.has-comment td,#dreditor #code tr.old.pre-selected td,#dreditor #code tr.old.selected td{background:var(--color-red) !important;border:0 !important;color:#fff !important}#dreditor #code th.line-ruler{visibility:hidden !important}#dreditor #code tr td:last-child{padding-left:1ch !important;position:relative}#dreditor #code tr td:last-child::after{background:rgba(0,0,0,0.25);bottom:0;content:' ';left:82ch;position:absolute;top:0;width:1px}#dreditor #code td.ln{background:none;padding:0 1ch !important;position:relative;text-align:center !important;vertical-align:top !important}#dreditor #code td.ln:first-child{padding:0 1ch 0 3ch !important}#dreditor #code td.ln::before,#dreditor #code td.ln::after{-webkit-box-sizing:border-box !important;box-sizing:border-box !important;content:attr(data-line-number);white-space:pre}#dreditor #code td.ln:first-child::before{content:'\020';position:absolute}#dreditor #code td.ln:first-child::after{content:attr(data-line-number)}#dreditor #code td.ln:nth-child(2)::before{content:attr(data-line-number)}#dreditor #code td.ln:nth-child(2)::after{content:'\020'}#dreditor #code tr td.ln:first-child::before{background:#212121;bottom:0;color:#fff;content:'\020';left:0;top:0;width:2ch}#dreditor #code tr:hover td.ln:first-child::before{background:rgba(0,0,0,0.5)}#dreditor #code tr.selected td.ln:first-child::before,#dreditor #code tr.pre-selected td.ln:first-child::before{background:#eee;color:#000}#dreditor #code tr.selected:hover td.ln:first-child::before,#dreditor #code tr.pre-selected:hover td.ln:first-child::before{background:#bbb}#dreditor #code tr.has-comment td.ln:first-child::before{content:'‚óè'}#dreditor #code .pre span.error{display:inline-block}#dreditor #code .pre span.error.whitespace{background:var(--bg-color-error-whitespace) !important;width:1ch}#dreditor #code .pre span.error.tab{background:var(--bg-color-error-tabs) !important}#dreditor #code .pre span.error.eof{background:var(--bg-color-error-eof);-webkit-box-shadow:none;box-shadow:none;color:var(--fg-color-error-eof);display:block;text-indent:1ch}#dreditor #code .pre span.error.eof::after{color:var(--fg-color-error-eof) !important;content:'?‚èé';margin-left:1ch}#dreditor #pastie{margin:0 -20px !important;position:relative}#dreditor #pastie h3{margin-bottom:0 !important;margin-left:0 !important;padding-left:10px}#dreditor #pastie textarea.form-textarea{background:var(--color-gray-lighter);border:0 !important;font-family:var(--font-monospace);font-size:14px !important;font-weight:300;padding:12px}#dreditor #pastie textarea.form-textarea:focus{background:#fff;border:0 !important;outline:0}#dreditor .dreditor-tooltip{-webkit-box-align:center;-ms-flex-align:center;align-items:center;background:var(--color-yellow);color:#000;font-family:var(--font-default);font-weight:600;height:calc(var(--bar-bottom) * 1px);left:0;padding:0 10px;position:absolute !important;right:0;top:3px}#dreditor .dreditor-tooltip span{background:rgba(0,0,0,0.5);color:#fff;font-family:var(--font-monospace);font-weight:300;margin-left:1ch;padding:0.25em 0.5em}#dreditor .dreditor-tooltip[style='display: block;']{display:-webkit-box !important;display:-ms-flexbox !important;display:flex !important}#dreditor-overlay{font-size:.875rem;background:#fff !important;border:0;border-left:solid 5px var(--color-blue-drupal);color:#000 !important;font-family:var(--font-default) !important;margin:0 -20px;padding:10px 10px 10px 15px}#dreditor-overlay code,#dreditor-overlay pre{font-size:.875rem;font-family:var(--font-monospace) !important;font-weight:300 !important}#dreditor-overlay ul,#dreditor-overlay ol{margin:1rem 0 !important;padding:0 0 0 20px !important}#dreditor-overlay ul{list-style:disc outside}#dreditor-overlay li{margin:0 0 0.25rem 0}#dreditor-overlay a:hover{text-decoration:underline}#dreditor-overlay p{margin:0.5rem 0}#dreditor-overlay blockquote{background:var(--color-gray-lightest)}#dreditor-overlay blockquote::after,#dreditor-overlay blockquote::before{content:'';display:table}#dreditor-overlay blockquote::after{clear:both}.dreditor-button,.dreditor-button:link,.dreditor-button:visited,#page a.dreditor-button{font-size:.75rem !important;-webkit-box-align:end;-ms-flex-align:end;align-items:flex-end;background:#333;border:0;border-radius:3px;-webkit-box-shadow:none;box-shadow:none;color:#fff;display:-webkit-inline-box !important;display:-ms-inline-flexbox !important;display:inline-flex !important;font-family:var(--font-default);font-weight:200;margin:0 0 0 10px !important;padding:0.5em 1em;text-transform:none}.dreditor-button:hover,.dreditor-button:focus,.dreditor-button:link:hover,.dreditor-button:link:focus,.dreditor-button:visited:hover,.dreditor-button:visited:focus,#page a.dreditor-button:hover,#page a.dreditor-button:focus{background:var(--color-gray);outline:0}#dreditor .dreditor-button,#dreditor input.dreditor-button{border-radius:0;margin:0 1px 0 0 !important}#dreditor .dreditor-button:first-of-type,#dreditor input.dreditor-button:first-of-type{margin-left:0}#dreditor .dreditor-button:last-of-type,#dreditor input.dreditor-button:last-of-type{margin-right:0}#dreditor .dreditor-button:hover,#dreditor .dreditor-button:focus,#dreditor input.dreditor-button:hover,#dreditor input.dreditor-button:focus{background:var(--color-blue-drupal)}#dreditor #pastie input.dreditor-button{float:left}#dreditor #dreditor-actions input.dreditor-button{font-size:1rem !important}#page .dreditor-button,#page a.dreditor-button,#page #tabs button.dreditor-button{background:var(--color-blue-drupal) !important;font-family:var(--font-drupal) !important;line-height:1.384615;padding:0.231em 0.615em}#page .dreditor-button:hover,#page .dreditor-button:focus,#page a.dreditor-button:hover,#page a.dreditor-button:focus,#page #tabs button.dreditor-button:hover,#page #tabs button.dreditor-button:focus{background:var(--color-gray) !important}

/* Fixes a bug with pift tables under the issue summary. */  
#extended-file-field-table-field-issue-files tr.even,
#extended-file-field-table-field-issue-files tr.odd {
  display: table-row !important;
}

}
