css:
@-moz-document domain("theodinproject.com") {

body, .lesson-functions {
	background-color: #282828 !important;
	color: #f8f8f8 !important;
}

p.profile-card__learning-goal, a.profile-card__link, .skills-header__content, .skill__sub-title { color: #dfdfdf }

.navbar {
	background-color: #7e7e7e;
}

.navbar .navbar-nav .nav-item a.nav-link, .skill__link, .skill__title, footer .footer-links a.footer-link { color: #e7e7e7 }

h1.banner__title, .course-description h2.course-description__heading, p.hero__sub-heading, h2, h3, h4, h5, h6, .skills-header__title {
	color: #e7e7e7;
}

.gradient { background-image: none; }

.lesson-content h3, .lesson-content h4, .lesson-content h5, .lesson-content h6 { 
	color: #f0f0f0;
}

.lesson-content p, .lesson-content ol li, .lesson-content ul li, .bottom-cta p, .course-description p, p.course-section__description, .lesson-content__panel ol li, h1.lesson-functions__title, .course-card__description p, h2.course-card-header__sub-title, span.course-card-header__sub-title, .how-it-works-title p, .description p, .success-content p { color: #bfbfbf; }

.card-main, .lesson-content__panel, footer#footer, .curriculum-details, .curriculum-details .curriculum-details-grid .curriculum-details-tile { background-color: #5e5e5e; }

.lesson-navigation__circle.active { background: #fff; } 
.lesson-navigation__link.active, a.secondary { color: #fff; }

.lesson-navigation__circle { background: #a9a9a9; }
.lesson-navigation__link { color: #a9a9a9; }

:not(pre) > code[class*="language-"], pre[class*="language-"], code { background: #3b3b3b }
pre { color: #f8f8f8 }

.viking-advert { display: none; }
}
