@-moz-document domain("patreon.com") {
/*!
 * I really hate React
 * Why do people make me write selectors like this,
 * It's just not fun.
 *
 * A whiny, sleepily written poem by Alan
 */
body,
#renderPageContentWrapper {
  background-color: #222;
}
#component-playground,
.reactWrapper {
  color: #ccc;
}

/* Top nav */
.reactWrapper header {
  background-color: #333;
}
.reactWrapper header form {
  border-color: transparent;
}
.reactWrapper header form input {
  background-color: #333;
  color: #eee;
}
.reactWrapper header div svg g {
  fill: #777;
}
.reactWrapper header div a:hover svg g {
  fill: #ccc;
}

/* User menu */
ul[aria-label="current user"] {
  background-color: #444;
  border-color: transparent;
}
ul[aria-label="current user"] a:hover {
  background-color: #555;
}

/* Profile page nav */
#renderPageContentWrapper > div > div > div[style*="background-image"] + div > div {
  background-color: #333;
  border-color: #444;
}

/* Profile page nav, scrolled down */
div[class*="HeadNavBar--navBarBacking"] {
  background-color: #333;
  border-color: #444;
}
div[class*="HeadNavBar--navBarBacking"] [color="gray1"] {
  color: #eee;
}
div[class*="HeadNavBar--navBarBacking"] [color="gray3"] {
  color: #ccc;
}

/* Profile Post Filter */
.mb-lg > div > div[overflow="visible"] {
  background-color: #444;
}
.mb-lg > div > div[overflow="visible"] > div > div {
  border-color: #555;
}
.jqJcrQ:hover [color="gray2"] {
  color: #333;
}
.ifXJAo svg g {
  fill: #aaa;
}
.kCfyHL svg g {
  fill: #666;
}

/* Homepage */
/* I hope these classes don't change frequently... No other way to safely target these though */
/* Update: they do change frequently. Seriously, WTF React. */
/* Left sidebar */
.gnZWlQ {
  background-color: #444;
}
.fqguTv, .fGMDrQ {
  background-color: #333;
  border: none;
}
.jHwxdN:not(:last-child),
[data-tag="list-item-"]:not(:last-child){
  border: none;
}
.jHwxdN:hover,
[data-tag="list-item-"]:hover {
  background-color: #555;
}

/* Top center stuff */
.bYdUEh {
  border-color: #444;
}
.bTxqkp, .cxaVhb {
  background-color: #444;
  border-color: transparent;
}
.iRHWqt, .jOlTKC {
  background-color: #444;
}
.bTxqkp svg g,
.cxaVhb svg g {
	fill: #ccc;
}
.bVJOan:hover {
  background-color: #555;
}
/* Right sidebar */
.fzmefO,
.dNYnrv {
  border-color: #333;
}
.dnHSTF + div,
.gNAIzz + div {
  background-color: #333;
}

/* General foreground color replacements */
[color="gray1"] {
  color: #eee;
}
[color="gray2"] {
  color: #ddd;
}
[color="gray3"] {
  color: #aaa;
}
[color="dark"] {
  color: #ccc;
}

/* Override hardcoded community bg */
.containerInner[style*="background-color"] {
  background-color: transparent !important;
}

/* Posts */
/* I really hope this [data-tag] attribute sticks around... */
div[data-tag="post-card"] > div {
	background-color: #333;
  color: #eee;
}
div[data-tag="post-card"] [color="gray1"] {
  color: #ccc;
}
div[data-tag="post-card"] [color="subdued"],
div[data-tag="post-card"] [color="subduedGray"]{
  color: #777;
}
div[data-tag="post-content-collapse"] > div > div[style*="relative"] > div:first-child {
  background-image: linear-gradient(transparent 0%, #333 100%);
}
div[data-tag="post-card"] .stackable {
  border-color: #444;
}
div[data-tag="post-content"] a,
div[data-tag="post-content-collapse"] a {
  color: #58a6ff;
}
.hAAZtv path,
.cSUxQS svg g {
  fill: #aaa;
}
div[data-tag="post-card"] div[aria-label="Audio Player"] {
  margin: -1px;
}
div[data-tag="post-card"] div[aria-label="Audio Player"] svg path {
  fill: #aaa;  
}
div[data-tag="post-card"] div[aria-label="Audio Player"] button:hover svg path,
div[data-tag="post-card"] div[aria-label="Audio Player"] button:focus svg path {
  fill: #ddd;  
}

/* Comments */
div[data-tag="comment-body"] {
  color: #ddd;
}
div[data-tag="post-card"] textarea {
  border-bottom-color: #444;
}
div[data-tag="post-card"] textarea:focus {
  border-bottom-color: #58a6ff;
}

/* Messages Left Nav */
.jMYyMj {
  border-color: #222;
}
.bjTZgN,
.iEAnyM,
.iCEwVs,
.eZLzYI {
  border-color: #333;
  border-left: 0;
}
.jbxBQu,
.iEAnyM {
  border-left-color: #58a6ff;
}

/* Messages Body */
.iBSGYo,
.iPqUla{
  border-color: #333;
}
.s1l2zpxd-2 > a:nth-child(1),
.bsXIiK,
._115-components-HorizontalNavListCompact-components-ListItem--isActive a {
  color: #F96854;
}
.kCfyHL svg g {
  fill: #aaa;
}
.eLjAGB {
  background-color: #333;
  border: 0;
}

/* Footer */
.reactWrapper > :last-child > div {
  background-color: #111;
}
.caSTfl svg g {
  fill: #777;
}
}
