/*隐藏帖子列表上方标签*/
#nv,
#thread_types,
.avt.y
{
    display: none;
}
/* 搜索框：热搜词 */
.scbar_hot_td {
    display: none;
}
/* 搜索框：背景色 */
#scbar {
    background: none;
    border-top: none;
}
.scbar_icon_td {
    background: none;
    display: none;
}
/* 搜索框：输入框内边距 */
.scbar_narrow #scbar_txt {
    padding: 0 5px;
}
/*隐藏公告*/
.bm.bml.pbn {
    display: none;
}
/*帖子列表的分割线*/
.tl th, .tl td {
    border-bottom: none;
}
/*用户名旁边的企鹅图标*/
.vwmy.qq {
    background: inherit;
    padding-left: inherit;
}
/*隐藏头像和头像下面的个人信息*/

/*发短信，边上的图标*/
.pls .o .pm2 {
    background-image: none;
    text-indent: inherit;
}
/*帖子下方的广告条*/
.ad {
    display: none;
}
/*回帖区域背景色*/
.pls {
/*     background: #ffedda; */
}
/*网页背景色*/
body{
    background: #F7F7F7;
}
#hd {
    background-color: #f1f1f1;
    border-bottom: 1px solid #ebebeb;
}
/*屏蔽ajax下一页按钮*/
/* #autopbn {
    display: none;
} */
/*隐藏logo*/
.hdc>h2>a>img {
    min-height: inherit;
    display: none;
}
.hdc>h2>a:before{
    content: "Hostloc";
    font-size: 50px;
    color: #03A9F4;
}
/*隐藏顶部全屏条*/
#toptb {
    display: none;
}
/* 边框 */
.bmw {
    border: none;
}
/*屏蔽发帖表情输入*/
#fastsmiliesdiv {
    display: none;
}
/*无用的帖子列表页头*/
/* #separatorline {
    display: none;
} */
#pt {
    margin: 10px 0;
}
/*美化帖子列表*/
.bm_c {
    padding: 20px;
}
