css:
@-moz-document domain(www.citeclass.com) {

body, h1, h2, h3, h4, h5, h6, p, ul, ol, dl, input, textarea, a, div {
    font-family: "/*[[font]]*/" !important;
}
img {
    border-radius: 10px;
}
.navbar {
    border-radius: 10px;
    margin: 10px 10px 0px 10px;
    border: 1px solid #f5f5f5;
}
input:focus, button:focus { 
    outline: none; 
} 
ul li {
border-radius: 10px;
}
div.region-content div.header-tab {
    background: #f5f5f5;
}
input[type=text], input[type=password], textarea {
    border-radius: 10px;
    background: #d6d6d6;
    border: 1px solid #d6d6d6;
}
#page {
border-radius: 10px;
}
input[type="submit"], button, .pretty-button, .delete-slide {
    color: #4a4a4a;
    background-color: #d6d6d6;
    border: 1px solid #d6d6d6;
    background-image: none;
    padding: 0px 5px 0px 5px !important;
    border-radius: 10px;
    height: 24px;
    width: auto;
}
#region-post div.region-content div.current, #report-region-pre div.region-content div.current, #region-pre div.region-content div.current {
    background-color: #f5f5f5;
    border-radius: 10px 10px 0 0!important;
}
div#move-region-right.move, div#move-region.move {
    display: none;
}
h3.sectionname {
text-align: center;
}
img.userpicture {
border-radius: 8px;
}
form {
text-align: center;
font-size: 16px; 
}
input[type=text] {
    padding: 0px 7px 0px 7px;
    height: 24px;
}
table tbody tr td {
    border: 1px solid #dedede!important;
    background: #efefef;
    color: #545454!important;
}
#region-main div.header-tab {
background: #f5f5f5;
border-bottom: 1px solid #f5f5f5;
border-radius: 10px 10px 0 0;
}
#region-main .block div.content {
    width: auto;
}
footer#page-footer {
display: none;
}
.header-tab:after {
    border-bottom: 2px solid #888888;
    display: block;
    content: "";
    width: 60%;
    margin: 0 auto;
}
#region-post div.region-content div.current h2, #report-region-pre div.region-content div.current h2, #region-pre div.region-content div.current h2 {
    color: #4a4a4a;
    font-size: 20px!important;
    margin-bottom: 2px;
}
div.header-tab div.title h2 {
    font-size: 25px!important;
    margin-bottom: 2px!important;
    color: #4a4a4a!important;
}
#gridiconcontainer ul.gridicons li {
    padding: 5px!important; 
    background: #efefef!important; 
    border: 1px solid #efefef!important;
}
.image_holder {
    border-color: #efefef!important;
    background-color: #efefef!important;
}
.loginbox .loginform .form-input input#username, .loginbox .loginform .form-input input#password {
    width: 175px;
    height: 30px;
    padding: 0px 5px 0px 5px!important;
    font-size: 18px;
}
.loginbox .loginform .form-input #loginbtn {
    display: block;
    text-align: center;
    margin: 5px auto 0px auto;
    width: 180px;
    height: 30px;
}
label {
font-size: 17px;
}
.titgrade2 {
border-radius: 10px 10px 0 0;
}
img.icon.icon-pre {
display: none;
}
.loginbox.clearfix.onecolumn {
border: none;
}
div#tabs-post.region-content div div h2, div#region-pre.block-region div#tabs-pre.region-content div div h2 {
    font-size: 20px!important;
}
hr {
display: none!important;
}
div {
text-align: center!important; 
}
div#logo {
    text-align: left!important;
}
.headermenu, .logininfo {
    text-align: right!important;
}
h3.page-subtitle {
    text-align: initial;
}
table {
    border: 4px solid #dedede!important;
    border-radius: 10px 10px 10px 10px!important;
}
div#dock.dock.dock_left_vertical.nothingdocked {
    display: none;
}
.elmenu {
    width: auto!important;
}
}
