css:
/* i really want this to be global */

:root {
  --light: #555;
  --dark: #999;
  --light-bg: #f4f4f4;
  --light-bg-alt: #eee;
  --light-bg-alt-hover: #fff;
  --dark-bg: #19191d;
  --dark-bg-alt: #1f1f24;
  --dark-bg-alt-hover: #29292f;
}

@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v18/CWB0XYA8bzo0kSThX0UTuA.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2212, U+2215;
}
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 500;
  src: local('Roboto Medium'), local('Roboto-Medium'), url(https://fonts.gstatic.com/s/roboto/v18/RxZJdnzeo3R5zSexge8UUVtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2212, U+2215;
}

/*LYH Page Icon*/

.LyricsHereByRobWPageActionIcon{
background: url('data:image/png;base64,')!important;
border: none!important;
width: 28px!important;
height: 28px!important;
filter: grayscale(1)!important;
transition: 0.35s!important;
}

.LyricsHereByRobWPageActionIcon:hover{
filter: grayscale(0)!important;
}

.L759-container{
background: var(--light-bg)!important;
font-family: "Roboto";
color: #555!important;
border: none!important;
border-radius: 3px!important;
box-shadow: 0 0 7px rgba(0,0,0,0.4)!important;
padding: 0!important;
min-height: 400px!important;
min-width: 325px!important;
transition: 0.3s background!important;
}

.L759-container *{
transition: 0.3s!important;
}

.L759-container.L759-skin-dark{
background: var(--dark-bg)!important;
color: var(--dark)!important;
}

.L759-container .L759-title{
background: url('https://i.imgur.com/z6mNoYD.png') no-repeat top 8px left 12px !important;
background-color: var(--light-bg)!important;
background-size: 24px 24px!important;
border: 1px solid rgba(0,0,0,0.1)!important;
border-width: 0 0 1px 0!important;
font-size: 15px;
height: 40px;
line-height: 40px;
padding: 0 35px 0 45px;
left: 0;
right: 0!important;
}

.L759-skin-dark .L759-title{
background-color: var(--dark-bg)!important;
border-color: rgba(255, 255, 255, .03)!important;
}

.L759-container .L759-close{
background: none!important;
height: 40px!important;
width: 40px!important;
fill: #ccc!important;
}

.L759-container .L759-close:hover{
fill: #aaa!important;
background: none!important;
}

.L759-skin-dark .L759-close{
fill: rgba(255, 255, 255, .15)!important;
}

.L759-skin-dark .L759-close:hover{
fill: rgba(255, 255, 255, .3)!important;
}

.L759-container .L759-top-bar{
background: none!important;
border: none!important;
height: 100%;
position: relative; top: -5px!important; bottom: 0!important; left: 0;
z-index: 50;
pointer-events: none;
}

.L759-container .L759-top-bar .L759-inline-settings{
border-top: 1px solid rgba(0, 0, 0, .1)!important;
display: block!important;
margin-top: 0px!important;
padding: 10px 0 25px 3px!important;
position: relative!important;
line-height: 18px!important;
}

.L759-container.L759-skin-dark .L759-top-bar .L759-inline-settings{
border-top: 1px solid var(--dark-bg-alt-hover)!important;
}

.L759-container .L759-top-bar .L759-inline-settings:after{
display: block!important;
padding-top: 5px!important;
content: 'Temporarily changes the theme to the dark color palette, to permanently retain the dark color palette, visit the settings link below.'!important;
}

.L759-container.L759-skin-dark .L759-top-bar .L759-inline-settings:after{
content: 'Temporarily changes the theme to the lgiht color palette, to permanently retain the light color palette, visit the settings link below.'!important;
}

.L759-container  .L759-top-bar .L759-settings-link{
font-size: 0!important;
position: absolute!important; bottom: 22px!important;
text-decoration: none!important;
z-index: 10!important;
}

.L759-container .L759-top-bar .L759-settings-link:before{
display: block!important;
font-size: 14px!important;
content:'Click here for more settings'
}

.L759-container .L759-top-bar .L759-inline-settings-toggle{
display: none!important;
}

.L759-container .L759-content-wrapper{
box-sizing: border-box;
top: 40px!important; bottom:40px; left: 0; right: 0;
}

.L759-content{
padding: 10px 12px 10px 12px!important;
}

.L759-container .L759-content .L759-result{
padding-bottom: 20px!important;   
}

.L759-content .L759-lyrics-line{
text-indent: 0!important;
padding: 1.5px 1.5px 0 5px!important;
}

.L759-content .L759-lyrics-line:hover{
cursor: pointer;
}

.L759-container .L759-content .L759-lyrics-line.L759-highlight{
background: none!important;
color: #e60f0f!important;
}

.L759-container .L759-content .L759-result:focus .L759-lyrics-line.L759-highlight{
outline: none!important;
}

.L759-content .L759-buttons{
box-sizing: content-box!important;
box-shadow: 0 2px 2px 0 rgba(0,0,0,0.16),0 0 0 1px rgba(0,0,0,0.08);
margin-bottom: 10px!important;
display: inline-block!important;
width: 100%!important;
}

.L759-content .L759-buttons button{
background: var(--light-bg-alt)!important;
border: 1px solid rgba(255,255,255,0.03)!important;
box-sizing: content-box!important;
margin-right: -5px!important;
padding: 5px 10px!important;
text-align: center!important;
width: calc(100% / 3 - 20px)!important;
}

.L759-container.L759-skin-dark .L759-buttons button{
background: var(--dark-bg-alt-hover)!important;
}

.L759-content .L759-buttons button:hover{
color: #fff!important;
background: var(--dark-bg-alt-hover)!important;
}

.L759-content .L759-buttons button:first-child{
border-right-width: 0!important;
border-radius: 3px 0 0 3px!important;
margin-right: -5px!important;
}

.L759-content .L759-buttons button:hover:first-child{
/*background: #d52a2a!important;*/
}

.L759-content .L759-buttons button:last-child{
border-radius: 0px 3px 3px 0!important;
}

.L759-content .L759-buttons button.L759-b-next{
display: inline-block!important;
}

.L759-status{
font-size: 0!important;
}

.L759-link-to-fetched-source{
font-size: 14px!important;
text-decoration: none!important;
}

.L759-link-to-fetched-source:before{
border: none!important;
color: var(--dark)!important;
display: block!important;
content: 'Searching from'!important;
font-size: 20px!important;
margin-top: -10px!important;
padding-bottom: 10px!important;
text-decoration: none!important;
}

.L759-container .L759-line-finder{
background: var(--light-bg)!important;
border: none!important;
border-bottom: 0px solid rgba(0,0,0,0.1)!important;
bottom: 0px; left: 0; right: 0;
box-shadow: 0 0 10px rgba(0,0,0,0.3)!important;
height: 41px;
z-index: 100;
}

.L759-skin-dark .L759-line-finder{
background: var(--dark-bg)!important;
}

.L759-container .L759-line-finder .L759-finder-searchterms{
color: var(--light)!important;
padding-left: 10px;
}

.L759-container .L759-line-finder button{
background: none!important;
}

.L759-toggle-info{
background: none!important;
border: none!important;
position: absolute!important; top: 13px!important; left: 12px;
font-size: 0;
height: 24px;
width: 24px;
z-index: 70;
pointer-events: all;
outline: none!important;
}

.L759-info-toggled:after{
font-size: 12px!important;

}

.L759-container .L759-top-bar .L759-toggle-info:hover, .L759-container .L759-top-bar .L759-toggle-info.L759-info-toggled{
background: none!important;
}

.L759-container .L759-info-wrapper{
display: block!important;
position: absolute; top: 3px; left: 0;
width: 100%;
height: 75px;
pointer-events: none;
opacity: 0;
transition: 0.5s!important;
}

.L759-toggle-info:hover+.L759-info-wrapper{
opacity: 1;
pointer-events: all;
transition: 0.5s!important;
}

.L759-info-toggled+.L759-info-wrapper{
opacity: 1!important;
pointer-events: all;
}

.L759-info-wrapper:hover{
pointer-events: all;
opacity: 1!important;
}

.L759-container .L759-top-bar .L759-info{
box-shadow: 0 0 8px rgba(0,0,0,0.55);
background: url('https://i.imgur.com/z6mNoYD.png') no-repeat top 8px left 12px!important;
background-color: var(--light-bg)!important;
border: none!important;
background-size: 24px 24px!important;
padding:55px 15px 15px 15px!important;
}

.L759-container.L759-skin-dark .L759-top-bar .L759-info{
background-color: var(--dark-bg-alt)!important;
}

.L759-container .L759-top-bar .L759-info:before{
content: 'Song Info & Settings';
position: absolute; top: 12px; left: 45px;
}

.L759-container .L759-top-bar .L759-info:after{
content: '';
position: absolute; top: 40px; left: 0;
background: rgba(0,0,0,0.1)!important;
height: 1px;
width: 100%;
transition: 0.5s!important;
}

.L759-container.L759-skin-dark .L759-top-bar .L759-info:after{
background: var(--dark-bg-alt-hover)!important;
}

.L759-switch-source{
height: 35px;
line-height: 40px!important;
font-size: 12px!important;
position: absolute!important; bottom: 0!important; left: unset!important; right: 10px!important;
z-index: 1000;
pointer-events: all!important;
text-decoration: none!important;
}

.L759-song-info{
font-size: 0;
}

.L759-song-title, .L759-link-to-found-source{
color:#c43131!important;
display: block;
font-size: 14px!important;
font-style: normal!important;
font-weight: normal!important;
}

.L759-song-title:before{
color: var(--light);
content: 'Song : '
}

.L759-link-to-found-source{
border: none!important;
text-decoration: none!important;
padding-top: 7px;
margin-left: 92px;
}

.L759-link-to-found-source:before{
color: var(--light);
position: absolute!important; left: 15px!important;
content: 'Lyric Source : ';
pointer-events: none!important;
}

.L759-container .L759-searchbox{
border: none!important;
height: 40px!important;
left: 0; right: 0;
}

.L759-container .L759-searchbox .L759-searchterms{
border: 1px solid rgba(0,0,0,0.1)!important;
border-width: 1px 0 0 0!important;
padding-left: 15px;
padding-right: 150px;
height: 40px!important;
    white-space: nowrap!important;
    overflow: hidden!important;
    text-overflow: ellipsis!important;
}

.L759-skin-dark .L759-searchbox .L759-searchterms{
border-color: rgba(255, 255, 255, .03)!important;
}

.L759-container .L759-dosearch{
display: none;
}

.L759-container .L759-resizer{
background: url('https://i.imgur.com/oRtGhlN.png')bottom left no-repeat!important;
height: 10px!important;
position: absolute; left: 3px; bottom: 3px;
width: 10px!important;
}

.L759-container .L759-resizer{
transform: scale(1)!important;
}
