css:
@-moz-document url-prefix("https://massengeschmack.tv/"), url-prefix("http://massengeschmack.tv/") {
body {
     background-color: #222222;
     ;
     color: white;
}
.navbar-default {
     background-color: #111111;
     border-color: #d67e00;
}
.navbar-default .navbar-nav > .open > a,
.navbar-default .navbar-nav > .open > a:focus,
.navbar-default .navbar-nav > .open > a:hover {
     color: #ffffff;
     background-color: #f19100;
}
.moduleTableLayout .entry h4 a {
     color: white;
}
.moduleTableLayout .entry .desc .btn-custom {
     background-color: grey;
}
.navbar-default .navbar-nav>li>a:hover {
     color: #d67e00;
}
.navbar-default .navbar-nav>li>a:focus {
     color: #f19100;
}
a.btn-custom,
button.btn-custom {
     background-color: grey;
}
.orangebox > div {
     border-left: solid 0.1em #d67e00;
}
.black {
     color: white;
}
pre {
     background-color: #333;
     color: #9f9;
}
code {
     background-color: #333;
     color: #ff9;
}
.breadcrumb {
     background: none;
     border-color: black
}
.modulePlayer .nav li a {
     color: white;
}
.modulePlayer .link a {
     color: white;
}
.modulePlayer h3 {
     margin-top: 5px;
     color: #d67e00;
}
.btn-default {
     color: #fff;
     background-color: #333;
     border-color: #000;
}
.progress {
     background-color: #333;
}
.table>tbody>tr>td {
     border-top: 1px solid #d67e00;
}
.table>tbody>tr:last-child>td {
     border-bottom: 1px solid #d67e00;
}
.dropdown-menu {
     background-color: #333;
}
.modulePlayer .link a {
     color: #d67e00;
}
.modulePlayer .link a:hover {
     color: #f90;
}
.dropdown-menu>li>a:focus,
.dropdown-menu>li>a:hover {
     background-color: #222222;
}
.dropdown-menu>li>a {
     color: #d67e00;
}
.dropdown-menu>li>a:hover {
     color: #f90;
}
.dropdown-header {
     color: white;
}
.form-control {
     background-color: #333;
     border-color: black;
     color: white;
}
.moduleNewContent .moduleNewContentMain {
     background-color: #333;
}
.embeddedtweet {
     background-color: #333;
     border-color: black;
}
#val-topbar {
     background-color: #333;
}
.soft-shadow {
     box-shadow: 0px 0px 1em #000;
}
.body_wrapper {
     background: #333 none;
}
.above_postlist .pagination_top .pagination,
.above_postlist .pagination_top .postpagestats {
     color: white;
}
.notices li {
     box-shadow: none;
     background-color: #333;
     color: gray;
     border-color: black;
}
.newcontent_textcontrol {
     background-color: gray;
     border-radius: 0;
     border-color: black;
     box-shadow: none;
}
.toolsmenu div {
     color: white;
     background-color: #333;
}
.toolsmenu .popupgroup a.popupctrl,
.toolsmenu .popupgroup .popupmenu a.popupctrl,
.toolsmenu .popupgroup .popupmenu:hover a.popupctrl,
.toolsmenu .popupgroup .popupmenu:hover .popupctrl a.popupctrl.active {
     color: white;
}
.postbitlegacy,
.postbitlegacy .postdetails,
.eventbit .eventdetails,
.postbitlegacy .userinfo,
.postbitlegacy .userinfo .username_container,
.postbitlegacy dl.userinfo_extra dd,
.postbitlegacy dl.user_rep dd,
.postbitlegacy .postbody,
.eventbit .eventdetails .eventbody,
.postbitlegacy .postrow,
.eventbit .eventrow,
.postbitdeleted .postrow,
.postbitignored .postrow,
.postbit .postfoot .textcontrols,
.postbitlegacy .postfoot .textcontrols,
.eventbit .eventfoot .eventcontrols {
     background-color: #333;
     border-color: black;
     color: white;
}
.postbitlegacy .postfoot .textcontrols {
     background-color: #333;
}
.bbcode_quote div.quote_container {
     background-color: black;
     border-radius: 0;
     border: 1px solid white;
}
.popover {
     background-color: #333;
     border: 0.1em solid black;
     color: white;
}
.popover.bottom>.arrow {
     border-bottom-color: black;
}
.popover.bottom>.arrow:after {
     border-bottom-color: #333;
}
.fb_iframe_widget iframe,
.fb_iframe_widget {
     display: none;
}
footer a {
     color: #777;
}
footer {
     border-top: 1px solid #f19100;
     position: absolute;
     bottom: 0;
     width: 100%;
     height: 139px;
     font-size: 12px;
     padding-top: 2em;
     background-color: #111111;
     font-family: Lato, "Helvetica Neue", Helvetica, Arial, sans-serif;
}
#chatwindow #tabs ul li.active {
     z-index: 1;
     background-color: #333333;
}
#chatwindow #tabs ul li:hover {
     background-color: #f19100;
     cursor: pointer;
}
#chatwindow #tabs ul li:first-child {
     margin-left: 0;
}
#chatwindow #tabs ul li {
     border-top: 1px solid #f19100;
     border-left: 1px solid #f19100;
     border-right: 1px solid #f19100;
     /*   float: left; */
     
     display: inline-block;
     margin: 0 0 0 2px;
     margin-left: 2px;
     list-style-type: none;
     padding: 5px 10px;
     background-color: #d8d8d8;
     position: relative;
}
#chatwindow #rooms {
     border: 1px solid #f19100;
     clear: left;
     overflow-y: hidden;
     overflow-x: hidden;
     height: 250px;
     min-height: 200px;
     background-color: #333333;
}
#chatwindow #rooms .m.highlighted div {
     background-color: #f19100 !important;
}
.moduleBlogLink {
     display: none;
}
.container-blog h1,
.container-blog h2,
.container-blog h3,
.container-blog h4,
.container-blog h5,
.container-blog h6 {
     text-transform: none;
     color: white;
     margin-left: 10%;
}
.moduleEpisodeList .desc {
     background-color: #111111;
}
.moduleEpisodeList .col:nth-child(2n) .desc,
.moduleEpisodeList .col:nth-child(2n) .tb,
.moduleEpisodeList .col:nth-child(2n+1) .back {
     background-color: #111111;
}
.moduleEpisodeList .tb {
     margin: 0;
     margin-bottom: 0px;
     padding: 0 12px 6px 12px;
     width: 100%;
     font-size: 11px;
     color: white;
     margin-bottom: 9px;
     background-color: #111111;
}
.moduleEpisodeList .progressIndicator {
     width: 0%;
     height: 3px;
     background-color: #f19100;
}
}
