css:
@-moz-document domain(coinmarketcap.com){*::-webkit-scrollbar {
    width: 10px !important;
    height: 10px !important;
    background: #14161a !important;
    border: 1px solid #282C34 !important;
}
*::-webkit-scrollbar-button {
    display: none !important;
}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
    border: 0 !important;
    box-shadow: none !important;
}
*::-webkit-scrollbar-thumb {
    min-height: 28px !important;
    background: #282C34 !important;
}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
    background: #14161a !important;
}
body {
    color: #bec2ca;
    background: #21252B;
}
.global-stats.desktop.outer-container {
    background: #282C34;
    box-shadow: 0 0 6px 0 rgba(0, 0, 0, 0.14), inset 0 -1px 0 0 #21252b;
    border: 0
}
.navbar-default .navbar-collapse, .navbar-default .navbar-form {
    border-color: transparent;
}
.navbar-default {
    background-color: transparent;
    border-color: transparent;
}
hr {
    border-top:  transparent;
}
#title a {
    color: #bec2ca;
}
h1 span {
    color: #E3E5E8;
}
.coin-summary-item h3 {
	background: #282C34;
    border-top: 0;
    border-bottom: 0;
}
.form-control {
    color: #6c7384;
    background-color: #14161a;
    background-image: none;
    border: 1px solid #14161a;
}
.language-dropdown, .global-currency-dropdown {
    background-color: #14161a;
    color: #6c7384;
    border-color: #14161a;
}
.highcharts-button rect{
    fill: #282C34 !important;
    color: #282C34 !important;
}
.highcharts-button text{
    fill: #7e848f !important;
    color: #7e848f !important;
}
.highcharts-grid.highcharts-yaxis-grid path{
    stroke: #30343c  !important;
}
.text-gray {
    color: #6c7384;
}
.navbar-default .navbar-nav>li>a {
    color: #6c7384;
}
.navbar-default .navbar-nav>li>a:hover, .navbar-default .navbar-nav>li>a:focus {
    color: #535863;
    background-color: transparent;
}
.highcharts-label.highcharts-range-input rect{
fill: #282C34 !important;
    color: #282C34 !important;
    stroke: #282C34 !important;
}
.highcharts-label.highcharts-range-input text{
    fill: #7e848f !important;
    color: #7e848f !important;
}
.nav-tabs {
    border-bottom: 1px solid #30343c;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
    color: #bec2ca;
    background-color: #282C34;
    border: 1px solid #282C34;
}
.nav>li>a:hover, .nav>li>a:focus {
    text-decoration: none;
    background-color: transparent;
    border-color: transparent;
}
.btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active, .open .dropdown-toggle.btn-default {
    color: #8d939f;
    background-color: #14161a;
    border-color: #14161a;
}
.btn-default {
    color: #6c7384;
    background-color: #14161a;
    border-color: #14161a;
}
.pagination>li>a, .pagination>li>span {
    color: #bec2ca ;
    background-color: #14161a;
    border: 1px solid #14161a;
}
.pagination>li>a:hover, .pagination>li>span:hover, .pagination>li>a:focus, .pagination>li>span:focus {
    color: #2a6496;
    background-color: #14161a;
    border-color: #14161a;
}
.highcharts-title{
    fill: #bec2ca !important;
    color: #bec2ca !important;    
}
.highcharts-label.highcharts-range-label text{
    fill: #6c7384 !important;    
}
.highcharts-range-selector-buttons text{
    fill: #6c7384 !important;  
}
a {
    color: #61AFEF;
    text-decoration: none;
}
.highcharts-label.highcharts-tooltip text{
    color: #6c7384 !important; 
    fill: #6c7384 !important; 
}
.highcharts-label-box.highcharts-tooltip-box {
    fill: #14161a !important;  
    stroke: #14161a !important;  
}
.highcharts-loading.highcharts-loading-hidden{
    background-color: #282C34 !important;    
    display: none !important;
}
.highcharts-loading-inner{
   background-color: #282C34  !important;    
    display: none !important;
}
.highcharts-point {
   fill: #323741 !important;
}
thead tr {
    color: #bec2ca;
    background-color: #282C34;
    border-color:#282C34;
}
.table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td {
    border-top: 1px solid #282C34;
}
.dataTable td.sorting_1 {
    border:1px solid #323741
}
.table>thead>tr>th {
    vertical-align: bottom;
    border-bottom: 2px solid transparent;
}
.sorting_asc {
    border-left: 1px solid transparent;
    border-right: 1px solid transparent;
}
#reportrange {
    border: 1px solid transparent;
}
.highcharts-scrollbar rect {
    fill: #14161a !important;
    stroke: #14161a !important;    
}
.highcharts-scrollbar-thumb {
    fill: #282C34 !important;
    stroke: #282C34 !important;
}
.highcharts-navigator-mask-inside{
    fill: rgba(40, 44, 52, 0.3);
}
.highcharts-navigator-outline{
    stroke: #14161a !important;
}
.highcharts-navigator-handle{
    fill: #14161a !important;
    stroke: #14161a !important;     
}
table#currencies>tbody>tr>td {
    border-bottom: 1px solid #2d313a
}
table#assets>tbody>tr>td {
    border-bottom: 1px solid #2d313a
}
}
