css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sousetsuka.com") {

/*!========================================\\
|| Dark sousetsuka                         ||
||-----------------------------------------||
|| Created by ARH                          ||
||-----------------------------------------||
|| A simple dark theme for sousetsuka      ||
|| Especially usefull when reading at night||
||-----------------------------------------||
|| contact: hakerti@wp.pl                  ||
\\----------------------------------------!*/

  ::-moz-selection {
    background: #444444;
    color: #bbbbff;
  }

body, div.content-wrapper {   
    background-color: #222222 !important;
}
  
body, .post-body {
	color: #DDDDDD !important;
}

.post-body {
    font-size: 12px!important;
    line-height: 15px!important;
    font-family: inherit!important;
}

div#postFooterGadgets {
    background: inherit!important;
}

div#comments.comments {
    display: none!important;
}

.content-wrapper {
    max-width: 1820px!important;
}

.main-wrapper {
    width: 1450px!important;
    padding-left:40px;
}

.sidebar li {
    color:#666666!important;
}

.widget-content > ul > li,
.nav li:hover ul {
    background-color: #444!important;
}


  
a.mw-echo-notifications-badge {
	background-color: #424242!important;
}

a {
	color: #aaaaff !important;
}

a:visited {
	color: #ffaaff !important;
}

a.external {
	color: #aaffaa !important;
}

a.new {
	color: red !important;
}
}
