css:
@-moz-document domain(www.justdancenow.com) {
/*-------------------------------BASE-------------------------------*/
.dr-landing__icon {
    background: url(https://dl.dropbox.com/s/np15zn93yc976vf/2017_basic_shape.png?dl=0) center/ 70% no-repeat;
}
.dr-landing__label {
    opacity: 0;
}
.dr-landing__room-number {
    text-shadow: none;
    color: white;
    left: -5%;
    top: 35%;
    font-size: 25%;
}
.dr-landing__wrapper {
    left: 17%;
}
.qr-code__img {
    width: 55%;
    height: 55%;
    margin: 23% 23%;
    float: none;
}
.dr-landing::before {
    right: 72%;
    width: 0.5%;
}
video.landing__video,
.footer,
.footer .copyright,
nav,
.footer__ubi-logo,
.footer .menu,
h1.landing-text__title,
.logo__inner,
h2.landing-text__subtitle,
.side-nav,
button.landing-arrow.scroll_down,
.landing-button::before,
.section.availability,
.section--vertical-content,
.section#how-it-works,
section#explore {
    display: none !important;
}

.enter--pairing::after {
    display: block;
    height: 1em;
    position: fixed;
    top: 2em;
    left: 6em;
    width: 13em;
    color: white;
    content: "Neolish Profile";
}
.enter__icon--img::after {
    content: "";
    z-index: -2;
    left: -9%;
    top: -10%;
    position: absolute;
    display: block;
    background: url(https://dl.dropbox.com/s/np15zn93yc976vf/2018_basic_shape.png?dl=0) center/ cover no-repeat;
    width: 4em;
    height: 4em;
}
h1.landing-text__title {
    animation: BrightnessCoverBeat 2s linear infinite;
    text-align: center;
    top: 45%;
    opacity: 0.2;
}
	
ï»¿/*----------------------------------BASE-------------------------------*/
 .init-spinner--fullscreen {
    width: 83%
}
.song-preview.transitioning:before {
    content: "";
    display: block;
    width: 83%;
    height: 65.1%;
    position: absolute;
    z-index: 99999999999999 !important;
    animation: transitionmenuflash 0.65s linear;
}
.init-spinner {
    top: 40%;
    left: 40%;
    fill: transparent;
    background: none;
    -webkit-animation: fadeInDefault 0.2s linear forwards;
    animation: fadeInDefault 0.2s linear forwards;
}
.dr-landing .init-spinner {
    top: 22%;
    width: 80% !important;
    left: 3.7%;
}
.dr-landing .init-spinner__spinner {
    fill: transparent;
}
.init-spinner__spinner {
    background: url(https://dl.dropbox.com/s/0pfu61atygf5ug4/loading-sniper-base.png?dl=0) center/ cover no-repeat;
    animation: spin 1s linear 0s normal none infinite;
    fill: transparent;
    opacity: 1;
}
.fullscreen-spinner .spinner {
    fill: transparent;
    animation: spin 1s linear 0s normal none infinite;
    background: url(https://dl.dropbox.com/s/0pfu61atygf5ug4/loading-sniper-base.png?dl=0) center/ cover no-repeat;
}
.fullscreen-spinner::before {
    animation: spin 2s linear 0s normal none infinite;
    position: absolute;
    background: url(https://dl.dropbox.com/s/n6006nkysg76skv/loading-sniper-base2.png?dl=0) center/ cover no-repeat;
    display: block;
    content: "";
    top: 44.8%;
    left: 47.1%;
    fill: #fff;
    width: 0.6em;
    height: 0.6em;
}
html.js.no-touch.jdf-loaded.room-number.show-controls.wide.state-coachselection #preview .right-pane {
    animation: ZIndexMais 7s both !important;
}
html.js.no-touch.jdf-loaded.room-number.show-controls.wide.state-coachselection {
    opacity: 1;
    animation: ZIndexMais 7s both !important;
}
html.js.no-touch.jdf-loaded.room-number.show-controls.wide.state-coachselection::before {
    content: "";
    animation: FlashTransition 4s linear !important;
    position: fixed;
    height: 6em;
    left: -13%;
    width: 13.3em;
    z-index: 9999999999;
    display: block;
    overflow: visible;
    transition: none;
    transform: none;
}
html.js.no-touch.jdf-loaded.room-number.show-controls.wide.state-coachselection .preview {
    animation: ZIndexMenos 7s both !important;
}
html.js.no-touch.jdf-loaded.room-number.show-controls.wide.state-coachselection .song-cover.song-cover--hi-res.AintMy {
    z-index: 9999999999999999999
}
@keyframes EntraceTranslationSong {
    0% {
        z-index: 9999999999999999999;
        background-size: 99%, 100% !important;
        background-position: -1880% 1520% !important;
        -webkit-mask: url(https://dl.dropbox.com/s/kgnrsvcb7w0q94d/MaskSongViewn.png?dl=0) center/ 95% no-repeat;
    }
    100% {
        z-index: 0;
        background-size: 0, cover;
        background-position: center;
        -webkit-mask: none
    }
}
.state-coachselection #players .player.ghost.visible {
    -webkit-animation: newPlayerEntraceGlow 0.3s linear both !important;
    animation: newPlayerEntraceGlow 0.3s linear both !important;
}
.state-coachselection #players .player.ghost.visible::before {
    animation: newPlayerStarEntraceGlow 1s linear both;
    content: "";
    width: 8em;
    height: 5em;
    top: -32%;
    left: -69%;
    display: block;
    background: url(https://dl.dropbox.com/s/zaabb3rt8mzywvd/Glow_Star.png?dl=0) center/ cover no-repeat;
    position: absolute;
    filter: brightness(200%);
}
.state-coachselection #players .player:nth-of-type(4) {
    left: 61.6%;
    top: 35%;
}
.state-coachselection #players .player:nth-of-type(3) {
    left: 55.6%;
    top: 5%;
}
.state-coachselection #players .player:nth-of-type(2) {
    left: 63.6%;
    top: -7%;
}
.state-coachselection #players .player {
    width: 0.8rem;
    z-index: 9999999;
}
.state-coachselection #players .player.ghost.visible::after {
    animation: NewGhostEntrace 0.4s linear both;
    content: "";
    width: 5em;
    height: 5em;
    top: -29%;
    left: -29%;
    display: block;
    background: url(https://dl.dropbox.com/s/ew4p7uybsch84os/CircleGlow.png?dl=0) center/ cover no-repeat;
    position: absolute;
    filter: brightness(200%);
}
.state-coachselection #song-selection {
    background: 0;
    z-index: 5;
    top: 60%;
    height: 55%;
    margin-left: 0;
    box-shadow: none;
    -webkit-mask: url(https://dl.dropbox.com/s/bbdvibwz2yz6tti/%40MenuMaskFInal.png?dl=0) 0% 0% /102% no-repeat;
}
#song-selection:after {
    left: -6.7%;
    top: 13%;
    width: 14%;
    visibility: visible;
    background-image: none;
    background-color: transparent;
    opacity: 1;
    transform: rotate(-191.1deg);
}

.pre-beat.odd .song-preview:not(.transitioning) {
    animation: BrightnessStarBeat 1s infinite !important;
}
.song-cover--hi-res {
    z-index: -99999999999999 !important;
    -webkit-mask: url(https://dl.dropbox.com/s/iwq3oj1iluatk40/%40MaskCover.png?dl=0) center/ cover no-repeat;
}
.category--focused[data-name="EXTREME"]::after {
    background: url(https://DL.dropbox.com/s/ap332d49yoo6ic9/EXTREME.png?dl=0) center/ 100% no-repeat;
}
.category--focused[data-name="FITNESS"]::after {
    background: url(https://dl.dropbox.com/s/rdfcq95pbxp009a/Fit.png?dl=0) center/ 100% no-repeat;
}
.category--focused[data-name="EASY PEASY PARTY"]::after {
    background: url(https://dl.dropbox.com/s/961h80to0c3waft/EasyPParty.png?dl=0) center/ 100% no-repeat;
}
.category--focused[data-name="RECOMMENDED FOR YOU"]::after {
    background: url(https://dl.dropbox.com/s/og97ddiukd2823r/Hall.png?dl=0) center/ 109% no-repeat;
}
.category--focused[data-name="EXTREME"]::after,
.category--focused[data-name="FITNESS"]::after,
.category--focused[data-name="EASY PEASY PARTY"]::after,
.category--focused[data-name="RECOMMENDED FOR YOU"]::after {
    content: "";
    display: block;
    position: absolute;
    width: 1.4%;
    height: 13%;
    left: 21.5%;
    top: 101%;
}
.item-container {
    top: -1%;
}
.category--focused::before {
    top: 98%;
    font-size: 24%;
    opacity: 1;
    text-align: left;
    left: 23.5%;
    font-family: 'Just Dance';
    -webkit-mask-image: linear-gradient(135deg, rgb(0, 0, 0) 0%, rgba(255, 255, 255, 0) 100%);
}
.list,
.list-x {
    will-change: transform;
    transition: -webkit-transform .3s;
    transition: transform .3s;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    display: -webkit-flex
}
.category.bounce-left,
.category.bounce-right {
    transform: translate(0%, 0%) !important;
    animation: none!important
}
.state-songselection #players[n-real-players="1"]::before,
.state-songselection #players[n-real-players="1"]::after {
    display: none;
}
.state-songselection #players::after {
    content: "";
    display: block;
    background: url(https://dl.dropbox.com/s/dlwml1vscaicx4e/phone_on.png?dl=0) center/ cover no-repeat;
    position: absolute;
    width: 0.7em;
    height: 0.7em;
    left: -24%;
    top: 30%;
    transition: all 300ms ease-out;
}
.state-songselection #players::before {
    content: "";
    display: block;
    position: absolute;
    border-left: 2px solid;
    height: 0.5em;
    left: -16.8%;
    transform: rotate(-11.2deg);
    transition: all 300ms ease-out;
}
#admin-menu .player--controller .avatar-wrapper {
    width: 1.4rem;
}
#admin-menu .player__name {
    font-size: 2em;
    color: #f5f2f2
}
#admin-menu .player--controller {
    color: white;
    width: 37.2em;
    background-color: rgba(255, 255, 255, 0.64);
    border: none;
    left: -188%;
    z-index: 99999999;
}
#admin-menu .players {
    overflow: visible !important;
}
#admin-menu .menu__head {
    overflow: visible;
    position: absolute;
}
#admin-menu .menu-section {
    width: 30%;
    margin-left: 71%;
}
#admin-menu .menu__head::after {
    height: 3.95rem;
    width: 20em;
    left: -0%;
    top: 0%;
    -webkit-mask: url(https://dl.dropbox.com/s/p7uz8m9639xsz4c/%40maskadmprofile.png?dl=0) center/ cover no-repeat;
    z-index: 1000;
    transform: rotate(0deg) !important;
    color: white;
}
.state-dance.buffering .fullscreen-spinner {
    pointer-events: none;
}
#room-info {
    opacity: .4!important;
}
.dialog #admin-menu {
    left: 1em;
    width: 50em;
}
#admin-menu .menu__body {
    background: url(https://dl.dropbox.com/s/9e3w3w167ci4y56/Fundo.jpg?dl=0) center/ cover no-repeat;
}
#admin-menu {
    z-index: 9999
}
#room-info,
#room-info::before,
.toggle-fullscreen,
.toggle-settings{
    transition: none !important
}
.toggle-settings:hover svg,
.toggle-settings:focus svg {
    transform: none;
}

/*----------------------------------ROOM NUMBER -------------------------------*/
#room-info {
    display: block;
    top: -3%;
    left: -3%;
    opacity: .2;
    width: 8.5%;
    height: 29.5%;
    text-align: center;
    justify-content: center;
    background-color: inherit;
}
.state-dance #room-info::before {
    left: 27%;
}
#room-info::before {
    left: 0.5em;
    top: -211%;
    width: 320%;
}
.danceroom {
    padding-left: 0;
    width: 110%;
    margin-left: -6.5%;
    text-align: center;
}
.danceroom__number {
    line-height: 0.30rem;
    font-size: 0.27rem;
}
/*----------------------------------QRCODE E DANCEROOM TEXT-------------------------------*/
 
.danceroom__qr-code-wrapper,
#just-dance-now .danceroom__label {
    display: none;
}
/*----------------------------------------------------------------- TITLE ------------------------------------------------------------------*/
 
@keyframes StarGlowBeat {
    100% {
        -webkit-filter: brightness(120%);
        opacity: 0;
    }
    0% {
        -webkit-filter: brightness(100%);
        opacity: 1
    }
}
@keyframes LogoBeat {
    100% {
        transform: scale(1);
        opacity: 0
    }
    0% {
        transform: scale(1.2, 1.2);
        opacity: 0.5
    }
}
@keyframes LogoBeatList {
    100% {
        background-position: bottom right;
    }
    0% {
        background-position: bottom left;
    }
}
@keyframes BrightnessBeat {
    50% {
        -webkit-filter: brightness(100%)
    }
    0% {
        -webkit-filter: brightness(110%)
    }
}
@keyframes LogoBeatEffectList {
    50% {
        transform: scale(1, 0);
        opacity: 0;
    }
    0% {
        transform: scale(1, -20);
        opacity: 0.7;
    }
}
/*----------------------------------------------------------------- MENU -------------------------------------------------------------------*/
 
@keyframes transitionmenuflash {
    0% {
        background: url(https://dl.dropbox.com/s/fmt9fc3wyqhs095/TransitionMenu.png?dl=0) -6em/ 100% 170% no-repeat;
        opacity: 0;
    }
    50% {
        opacity: 0.7;
    }
    100% {
        background: url(https://dl.dropbox.com/s/fmt9fc3wyqhs095/TransitionMenu.png?dl=0) 3em/ 100% 170% no-repeat;
        opacity: 0;
    }
}
@keyframes OpacityTag {
    100% {
        opacity: 0;
    }
    50% {
        opacity: 1;
    }
    0% {
        opacity: 0;
    }
}
@keyframes OpacityBeat {
    100% {
        opacity: 0
    }
    0% {
        opacity: 0.7
    }
}
@keyframes focusZoom {
    0% {
        transform: scale(1)
    }
    50% {
        transform: scale(1.2)
    }
    100% {
        transform: scale(1)
    }
}
@keyframes CoachBeatMenu {
    100% {
        transform: scale(1);
    }
    0% {
        transform: scale(1.2);
    }
}
@keyframes coachBeat {
    50% {
        filter: brightness(100%)
    }
    0% {
        transform: scale(1.1);
        filter: brightness(115%), drop-shadow(-15px -15px 20px white);
    }
}
/*----------------------------------------------------------------- DANCE ------------------------------------------------------------------*/
/*---------------------------------- FEEDBACKS & RACETRACK -------------------------------*/
 
@keyframes ZIndexMenos {
    0% {
        z-index: 9999999999999999999;
    }
    95%,
    100% {
        z-index: -9999999999999999999;
    }
}
@keyframes ZIndexMais {
    0% {
        z-index: -9999999999999999999;
    }
    95%,
    100% {
        z-index: 9999999999999999999;
    }
}
@keyframes FlashTransition {
    0% {
        opacity: 1;
        -webkit-mask: url(https://dl.dropbox.com/s/2c7t5fuev6astn1/JDTransitionBlack.png?dl=0) center/ cover no-repeat, url(https://dl.dropbox.com/s/sjus8zsz1gsxl28/MaskTransitionJD.png?dl=0) -9em/ cover no-repeat;
        background-color: white;
    }
    50% {
        opacity: 1;
        -webkit-mask: url(https://dl.dropbox.com/s/2c7t5fuev6astn1/JDTransitionBlack.png?dl=0) center/ cover no-repeat, url(https://dl.dropbox.com/s/sjus8zsz1gsxl28/MaskTransitionJD.png?dl=0) -9em/ cover no-repeat;
    }
    90% {
        -webkit-mask: url(https://dl.dropbox.com/s/2c7t5fuev6astn1/JDTransitionBlack.png?dl=0) center/ cover no-repeat, url(https://dl.dropbox.com/s/sjus8zsz1gsxl28/MaskTransitionJD.png?dl=0) 12em/ cover no-repeat;
    }
    95% {
        -webkit-mask: url(https://dl.dropbox.com/s/2c7t5fuev6astn1/JDTransitionBlack.png?dl=0) center/ cover no-repeat, url(https://dl.dropbox.com/s/sjus8zsz1gsxl28/MaskTransitionJD.png?dl=0) -9em/ cover no-repeat;
    }
    99%,
    100% {
        background-color: transparent;
        opacity: 0;
        -webkit-mask: none;
    }
}
@keyframes newPlayerEntraceGlow {
    0% {
        opacity: 0;
        transform: scale(1.7);
    }
    100% {
        opacity: 1;
        transform: scale(1);
    }
}
@keyframes newPlayerStarEntraceGlow {
    0% {
        opacity: 0;
        transform: scale(1);
    }
    2% {
        opacity: 1;
        transform: scale(1);
    }
    100% {
        opacity: 0;
        transform: scale(1.5);
    }
}
@keyframes newStarGlowRemake {
    0% {
        opacity: 0;
        transform: scale(1) rotate(25deg)rotate(25deg);
    }
    1% {
        opacity: 1;
        transform: scale(1) rotate(25deg);
    }
    100% {
        opacity: 0;
        transform: scale(3) rotate(25deg);
    }
}
@keyframes ZoomMegaStar {
    0% {
        background-position: 500% 3%, 600% 16%, 700% 30%, 0em 43%, 0em 58%, 0em 73%, 0em 87%, 0em 101%;
    }
    15% {
        background-position: 500% 3%, 600% 16%, 700% 30%, 0em 43%, 0em 58%, 0em 73%, 0em 87%, 0em 101%;
    }
    25% {
        background-position: -500% 3%, 600% 16%, 700% 30%, 800% 43%, 0em 58%, 0em 73%, 0em 87%, 0em 101%;
    }
    35% {
        background-position: 500% 3%, -600% 16%, 700% 30%, 800% 43%, 900% 58%, 0em 73%, 0em 87%, 0em 101%;
    }
    45% {
        background-position: 500% 3%, 600% 16%, -700% 30%, 800% 43%, 900% 58%, 900% 73%, 0em 87%, 0em 101%;
    }
    55% {
        background-position: 500% 3%, 600% 16%, 700% 30%, -800% 43%, 900% 58%, 900% 73%, 0em 87%, 0em 101%;
    }
    65% {
        background-position: 500% 3%, 600% 16%, 700% 30%, 800% 43%, -900% 58%, 900% 73%, 900% 87%, 0em 101%;
    }
    75% {
        background-position: 500% 3%, 600% 16%, 700% 30%, 800% 43%, 900% 58%, -900% 73%, 900% 87%, 900% 101%;
    }
    85% {
        background-position: 500% 3%, 600% 16%, 700% 30%, 800% 43%, 900% 58%, 900% 73%, -900% 87%, -900% 101%;
    }
    95% {
        background-position: -500% 3%, 600% 16%, -700% 30%, 800% 43%, 900% 58%, 900% 73%, 900% 87%, -900% 101%;
    }
    100% {
        background-position: 500% 3%, 600% 16%, 700% 30%, 800% 43%, 900% 58%, 900% 73%, -900% 87%, -900% 101%;
    }
}
@keyframes StarBeatScale {
    100% {
        transform: scale(1) rotate(-25deg);
    }
    0% {
        transform: scale(1.2) rotate(-25deg);
    }
}
@keyframes FeedlabelNew {
    0% {
        transform: scale(1.2);
        opacity: 0;
        margin: 0% 0;
    }
    30% {
        transform: scale(0.9);
        opacity: 1;
        margin: 0% 0;
    }
    50% {
        transform: scale(1.1);
        opacity: 1
    }
    100% {
        transform: scale(1);
        opacity: 0;
        margin: 6% 0;
    }
}
@keyframes progressbaranimation {
    0% {
        background-position: 0 0;
        -webkit-filter: brightness(100%);
        background-size: cover
    }
    20% {
        -webkit-filter: brightness(120%);
        background-position: -800px 0;
        background-size: cover
    }
    21% {
        background-size: 0
    }
    100% {
        background-position: 0 0;
        background-size: 0
    }
}
@keyframes NewGhostEntrace {
    100% {
        transform: scale(0.5);
        opacity: 0;
    }
    50% {
        opacity: 1;
    }
    0% {
        opacity: 0;
        transform: scale(1.1);
    }
}
@keyframes GircleGlowParticle {
    0% {
        transform: scale(0.5);
        opacity: 0;
    }
    50% {
        opacity: 1;
    }
    100% {
        opacity: 0;
        transform: scale(1.1);
    }
}
@keyframes BrightnessMovements {
    0% {
        -webkit-filter: brightness(100%);
        filter: brightness(100%);
    }
    50% {
        -webkit-filter: brightness(200%);
        filter: brightness(200%);
    }
    100% {
        -webkit-filter: brightness(100%);
        filter: brightness(100%);
    }
}
@keyframes ParticlePerfect {
    0% {
        opacity: 0;
        transform: scale(0.4, 0.4)
    }
    80% {
        opacity: 1;
        transform: scale(1.2, 1.2)
    }
    85%,
    95% {
        opacity: 0;
        transform: scale(1.2, 1.2);
    }
}
@keyframes ColorSpinner {
    0% {
        fill: red;
    }
    25% {
        fill: #00fdff;
    }
    50% {
        fill: blue;
    }
    75% {
        fill: green;
    }
    100% {
        fill: red;
    }
}
/*---------------------------------- LYRICS, PICTOS & GOLDMOVE -------------------------------*/
 
@keyframes pictoOut {
    0% {
        background-size: 100% 100%;
        filter: brightness(250%);
        opacity: 1;
    }
    5% {
        filter: brightness(100%);
    }
    100% {
        background-size: 100% 0%;
        opacity: 0;
        filter: brightness(100%);
    }
}
/*-------------------------------------------------------------- AFTERDANCE ----------------------------------------------------------------*/
 
@keyframes newStarGlow {
    0% {
        opacity: 0;
        transform: scale(1) rotate(13deg);
    }
    1% {
        opacity: 1;
        transform: scale(1) rotate(13deg);
    }
    100% {
        opacity: 0;
        transform: scale(2.6) rotate(13deg);
    }
}
@keyframes MEGASTARZOOMEFFECT {
    0% {
        transform: scale(1);
        -webkit-filter: brightness(100%)
    }
    90% {
        transform: scale(1.8);
        -webkit-filter: brightness(190%)
    }
    91%,
    100% {
        transform: scale(1);
        -webkit-filter: brightness(100%)
    }
}
@keyframes PlayersEffect {
    100% {
        opacity: 1;
    }
    85%,
    0% {
        opacity: 0.25;
    }
}
@keyframes AfterDanceOpacity {
    25% {
        opacity: 0;
    }
    50% {
        opacity: 1;
    }
    60% {
        opacity: 0;
    }
    75% {
        opacity: 1;
    }
    100% {
        opacity: 0;
    }
}
@keyframes SparklesMoveOpacity {
    100% {
        opacity: 1;
    }
    0% {
        opacity: 0;
    }
}
@keyframes SparklesMove {
    0% {
        left: -1%;
        top: 150%;
    }
    10% {
        left: 16%;
        top: 113%;
    }
    30% {
        left: 3%;
        top: 147%;
    }
    50% {
