/* vim: fdm=marker foldlevel=0
 */

@-moz-document domain(waffle.io) {

/* Page setup ------------------------------------------------------------- */

/* Variables {{{ */

:root {
  --link-color: #368DE2;
  --background-color: #F5F5F7;
  --background-color-dark: #3D4958;
  --line-dark-color: #EAEAEC;
  --line-light-color: #EEEEEF;
  --primary-button-color: #51A2EE;
  --border-color-light: #d1d9e1;
  --text-color-dark: #283342;
  --text-color-light: #999fa5;
  --text-color-light-dark-bg: #9DA2A9;
  --text-color-sidebar: #FAFAFB;
  --sidebar-active-color: #3e8aff;
  --font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
  --font-size-base: 13px;
  --font-size-small: 11px;
  --font-size-large: 14px;
  --font-size-title: 16px;
}

/* }}} */

/* Font reset {{{ */

html,
body,
a,
input,
select,
textarea,
button,
.button,
.btn,
h1,
h2,
h3,
h4,
h5,
h6 {
  font-family: var(--font-family) !important;
}

.card .card-header,
.card .card-body {
  font-family: var(--font-family) !important;
}

/* }}} */

/* Headings {{{ */

h1,
h2,
h3,
h4,
h5,
h6 {
  color: var(--text-color-dark);
}

/* }}} */

/* Links {{{ */

a:link {
  color: var(--link-color);
}

a:visited {
  color: var(--link-color);
}

a:hover {
  color: var(--link-color);
}

a:active {
  color: var(--link-color);
}

/* }}} */

/* Lists {{{ */

ol, ul {
  margin: 0;
  padding: 0 0 0 12px;
}

/* }}} */

/* Buttons {{{ */

.button,
.header-nav .button,
.header-nav button,
.btn {
  padding: 6px 12px;
  text-transform: uppercase;
  font-family: var(--font-family);
  font-size: var(--font-size-small);
  border-radius: 3px;
  letter-spacing: 0.2px;
}

.button.primary,
.btn.primary,
.btn-primary {
  background-color: var(--primary-button-color);
}

/* }}} */

/* Form inputs {{{ */

.form-control,
.header-nav .form-control {
  font-size: var(--font-size-base) !important;
  border: 1px solid var(--border-color-light);
}

::-webkit-input-placeholder {
  font-size: var(--font-size-base) !important;
  color: var(--text-color-light);
}

::placeholder {
  font-size: var(--font-size-base) !important;
  color: var(--text-color-light);
}

/* }}} */

/* Components ------------------------------------------------------------- */

/* Alerts {{{ */

.alert-drawer .info {
  padding: 9px 0;
  line-height: unset;
}

.alert-drawer .message {
  font-size: var(--font-size-base);
}

.alert-drawer .message a {
  text-decoration: none;
  color: #FFF;
  display: inline-block;
  border-bottom: 1px solid #FFF6;
}

.alert-drawer .dismiss {
  padding: 0;
  font-size: var(--font-size-small);
}

/* }}} */

/* Header {{{ */

.sidebar.navbar .header-nav {
  border-bottom: 1px solid var(--line-dark-color);
}

.sidebar.navbar .header-nav .project-account-link i {
  font-size: 12px;
}

.sidebar.navbar .header-nav .account-type-icon,
.dropdown-ct .project-dropdown {
  border-right: 1px solid var(--line-light-color);
}

.dropdown-ct .project-dropdown .dropdown-title {
  font-size: var(--font-size-base);
  padding: 0 6px;
  width: calc(100% - 22px);
}

.dropdown-ct .project-dropdown .dropdown-menu-toggle i {
  color: var(--line-light-color);
}

.sidebar.navbar .header-nav .board-actions {
  justify-content: flex-start;
  flex-direction: row-reverse;
}

.sidebar.navbar .header-nav .board-actions .add-new-issue .button {
  padding: 6px 12px !important;
  font-size: var(--font-size-small) !important;
}

.sidebar.navbar .header-nav .board-actions .add-new-issue .button i {
  display: none;
}

.sidebar.navbar .header-nav .board-actions .search-query.form-control {
  background-clip: content-box;
  border-top-left-radius: 14px;
  border-bottom-left-radius: 14px;
  padding-left: 12px;
  color: var(--text-color-light);
}

.sidebar.navbar .header-nav .board-actions .search .header-action-toggle {
  border-color: var(--border-color-light);
  border-top-right-radius: 14px;
  border-bottom-right-radius: 14px;
  background-color: #f7f7f7;
  text-align: center;
}

.sidebar.navbar .header-nav .board-actions .filter-icon {
  display: none;
}

.sidebar.navbar .header-nav .board-actions .filter-more {
  margin: 0;
  padding: 0;
  padding-right: 3px;
}

.sidebar.navbar .header-nav .board-actions .add-issue .popover {
  left: unset;
  margin-top: -1px;
  border-color: var(--line-dark-color);
  border-top: 1px solid #FFF;
  border-radius: 0 0 3px 3px;
  box-shadow: none;
  box-shadow: 0 3px 3px rgba(61,73,88,.1);
}

/* }}} */

/* Sidebar {{{ */

.sidebar.navbar {
  background-color: var(--background-color-dark);
}

.sidebar.navbar .nav-list-icon {
  font-size: 14px;
}

.sidebar.navbar .nav-list-item .nav-link.share-btn i {
  font-size: 13px;
}

.sidebar.navbar .nav-list-item .nav-link.js-github-url i,
.sidebar.navbar .nav-list-item .nav-link .fa-home,
.sidebar.navbar .nav-list-item .nav-link .fa-gear {
  font-size: 16px;
}

.sidebar.navbar .nav-list-item .nav-link {
  color: var(--text-color-sidebar);
}

.sidebar.navbar .nav-list-item .nav-link.active {
  background: var(--sidebar-active-color);
}

.sidebar.navbar .brand-list-item,
.sidebar.navbar .brand-list-item .navbar-brand {
  background: #FFF;
}

.sidebar.navbar .brand-list-item .navbar-brand {
  color: var(--text-color-light);
  font-size: 24px;
  font-weight: 200;
  text-align: center;
  padding: 8px 10px;
  border-right: 1px solid var(--line-light-color);
}

.sidebar.navbar .brand-list-item .navbar-brand::before {
  content: "W";
}

.sidebar.navbar .brand-list-item .navbar-brand i {
  display: none;
}

.sidebar.navbar .sidebar-bottom-nav img {
  border: none;
  border-radius: 2px;
  margin: 3px 3px 0;
}

.sidebar-toggle {
  background-color: var(--background-color);
}

/* }}} */

/* Board columns {{{ */

/* Board column header {{{ */
.board .board-body .column-ct .column .column-header {
  font-size: var(--font-size-title);
  font-family: var(--font-family);
  font-weight: 600;
  text-align: left !important;
  height: auto;
  line-height: inherit;
}

.board .board-body .column-ct .column-toggle {
  float: none;
  padding-top: 0;
}

.board .board-body .column-ct .column .column-header .display-name {
  margin-left: 0;
  padding-left: 6px;
  color: var(--text-color-dark);
}

.board .board-body .column-ct .column .column-header .column-card-count {
  margin: 0;
  margin-left: 6px;
  color: var(--text-color-light-dark-bg);
  font-weight: 200;
}

.board .board-body .column-ct .column .column-header .column-card-count .column-card-count-filtered, .board .board-body .column-ct .column .column-header .column-card-count .column-card-count-total {
  vertical-align: bottom;
}

.board .board-body .column-ct .column-text {
  padding-left: 12px;
}

.board .board-body .column-ct .column .column-card-size-sum {
 display: none;
}

/* }}} */

/* Board body {{{ */

.board .board-body {
  background-color: var(--background-color);
}

.board .board-body .columns-ct {
  padding-top: 18px;
}

.board .board-body .column-ct .column {
  padding-bottom: 0;
  border-left: 0;
}

.board .board-body .column-ct .column .column-body {
  width: 100%;
}

/* }}} */

/* Column collapsed {{{ */

.board .board-body .column-ct.collapsed .column-header {
  padding-top: 0;
  padding-bottom: 1px;
}

.board .board-body .column-ct.collapsed .column-text {
  padding-top: 20px;
}

.board .board-body .column-ct.collapsed .column-text .display-name {
  padding-left: 0;
  padding-top: 2px;
}

.board .board-body .column-ct.collapsed .column-text .column-card-count {
  padding-top: 6px;
}

/* }}} */

/* Column empty state {{{ */

.board .board-body .column-ct .column .column-body .column-description {
  text-align: left !important;
  font-family: var(--font-family);
  color: var(--text-color-light-dark-bg)
}

.board .board-body .column-ct .column .column-body .column-description .column-description-header {
  display: none;
}

.board .board-body .column-ct .column .column-body .column-description .column-description-body {
  padding: 12px 0;
  font-size: var(--font-size-base)
}

/* }}} */

/* Card {{{ */

.board .card {
  border: 1px solid #cbcbcc;
  box-shadow: 0 1px 3px rgba(61,73,88,.1);
}

.board .card:hover {
  box-shadow: 0 1px 4px rgba(61,73,88,.3);
}

.board .card .card-header,
.board .card .card-body {
  padding: 3px 6px;
}

.board .card .card-header {
  height: auto;
}

.board .card .card-header .card-number {
  font-family: var(--font-family);
  font-weight: normal;
  color: var(--link-color);
}

.board .card .card-header .card-pull-request {
  padding: 0 0 0 3px;
  margin: 0;
}

.board .card .card-body .title {
  font-size: var(--font-size-base);
}

.board .card .footer .card-footer-action {
  font-size: var(--font-size-small);
}

.board .card .footer .card-footer-action .size-count,
.board .card .footer .card-footer-action .count {
  color: var(--text-color-light);
  font-size: var(--font-size-small);
  border-color: var(--border-color-light);
}

.card .card-pr-list {
  margin-bottom: 0;
  padding: 0;
  overflow: hidden;
  box-shadow: none;
  border-top: 1px solid var(--line-light-color);
  border-bottom-left-radius: 2px;
  border-bottom-right-radius: 2px;
}

.card .card-pr-list .pr-icon-ct {
  font-size: 12px;
  margin-top: 3px;
  margin-left: 2px;
}

.card .card-pr-list .number {
  color: var(--text-color-light-dark-bg);
}

.card .card-pr-list .card-pr-list-item {
  box-shadow: none;
  padding: 3px 6px;
  }

.card .card-pr-list .pr-title {
  font-style: normal;
  font-size: var(--font-size-small);
  font-family: var(--font-family);
  line-height: 1.4;
  color: var(--text-color-light-dark-bg);
}

.card-pr-list .pr-labels {
  margin: 3px 0 0;
  padding: 0;
}

.card .card-pr-list .label-pill {
  margin: 0 3px 3px 0;
  padding: 0 3px;
  font-family: var(--font-family);
}

/* }}} */

/* Status bar {{{ */

.status-bar {
  margin-left: -6px;
  width: calc(100% + 12px);
  margin-bottom: -3px;
}

.status-bar .status:last-child,
.status-bar .status:first-child {
  border-radius: 0;
}

/* }}} */

/* Pills {{{ */

.milestone-pill {
  color: var(--text-color-light);
  border-color: var(--border-color-light);
  font-size: var(--font-size-small);
}

/* }}} */

/* }}} */

/* Modal {{{ */

.modal {
  background-color: transparent;
}

.modal-backdrop {
  background-color: rgba(255,255,255,.8);
}

.modal.detail-modal .modal-dialog {
  margin-right: 0;
  margin-top: 0;
  margin-bottom: 0;
  width: 600px;
  height: 100%;
  max-height: 100%;
  box-sizing: border-box;
  font-family: var(--font-family);
  font-size: var(--font-size-base);
  color: var(--text-color-dark);
}

.modal.detail-modal .modal-content {
  border-radius: 0;
  border: none;
  box-shadow: 0 5px 25px rgba(61,73,88,.3);
}

.modal.detail-modal .modal-content .modal-header {
  background-color: #FFF;
  padding: 12px 0;
  margin: 0 24px 12px;
  border-bottom: 1px solid var(--line-light-color);
}

.modal.detail-modal .modal-content .modal-header  .drag-modal {
  display: none;
}

.modal.detail-modal .modal-content .modal-header .issue-number {
  color: var(--link-color);
  margin: 0;
  font-size: var(--font-size-title);
  font-family: var(--font-family);
  font-weight: 600;
  text-align: left !important;
  height: auto;
  line-height: unset;
}

.modal.detail-modal .modal-content .modal-header .issue-title {
  width: auto;
  margin-left: 6px;
  color: var(--text-color-dark);
  font-size: var(--font-size-title);
  font-family: var(--font-family);
  font-weight: 600;
  text-align: left !important;
  height: auto;
  line-height: unset;
}

.modal.detail-modal .modal-content .modal-body {
  padding: 0;
  background: #FFF;
}

.modal.detail-modal .modal-content .modal-body .row {
  display: flex;
  flex-direction: column;
  margin: 0;
}

.modal.detail-modal .modal-content .modal-body .card-sidebar {
  float: none;
  display: flex;
  flex-wrap: wrap;
  margin: 0 24px;
  margin-top: 0;
  padding: 12px 0;
  padding-top: 0;
  width: 100%;
  max-width: calc(100% - 48px);
}

.modal.detail-modal .modal-content .modal-body .card-sidebar .discussion-status {
  flex: 0 1 100%;
  width: 100%;
  padding: 0 0 12px 0;
  margin: 0 0 12px 0;
  border-bottom: 1px solid var(--line-light-color);
  text-align: left;
}

.modal.detail-modal .modal-content .modal-body .card-sidebar .discussion-status .state {
  display: inline;
  margin: 0;
  font-size: var(--font-size-base);
  line-height: 1.2;
}

.modal.detail-modal .modal-content .modal-body .card-sidebar .discussion-status .comments-number {
  display: inline;
  margin: 0;
  padding: 0;
  padding-left: 6px;
  font-size: var(--font-size-base);
  line-height: 1.2;
  font-weight: normal;
}

.modal.detail-modal .modal-content .modal-body .card-sidebar .horizontal-divider {
  display: none;
}

.modal.detail-modal .modal-content .modal-body .card-sidebar .sidebar-options {
  flex: 1 0 25%;
  padding: 0 0 6px 0;
  margin: 0 0 6px 0;
  border-bottom: 1px solid var(--line-light-color);
}

.modal.detail-modal .modal-content .modal-body .card-sidebar .sidebar-options .option-button {
  margin: 0 0 12px;
  line-height: 1.2;
  font-size: 12px;
  font-weight: 600;
}

.modal.detail-modal .modal-content .modal-body .card-sidebar .sidebar-options .option-button i {
  line-height: unset;
  float: none;
  margin-left: 3px;
}

.modal.detail-modal .modal-content .labels .option-placeholder {
  color: var(--text-color-light);
}

.modal.detail-modal .modal-content .modal-body .card-discussion {
  max-width: calc(100% - 48px);
  width: 100%;
  padding: 0 0 12px;
  margin: 0 24px;
}

.modal.detail-modal .modal-content .modal-body .discusser-avatar {
  width: 24px !important;
  height: 24px !important;
  border-radius: 3px !important;
}

.modal.detail-modal .modal-content .modal-body .card-comment .card-item {
  margin: 0 0 0 calc(24px + 6px);
}

.modal.detail-modal .modal-content .modal-body .card-comment .card-item .card-item-header {
  background: transparent;
  padding: 0;
  margin: 0;
  height: 0;
  line-height: unset;
  font-size: var(--font-size-base);
  font-weight: normal;
  border-radius: 0;
}

.modal.detail-modal .modal-content .modal-body .card-comment .card-item a:link,
.modal.detail-modal .modal-content .modal-body .card-comment .card-item a:visited {
  color: var(--link-color);
}

.modal.detail-modal .modal-content .modal-body .card-comment .card-item .card-item-header .comment-label {
  margin: 0 6px;
  color: var(--text-color-light);
  border-color: var(--border-color-light);
  font-size: var(--font-size-small);
}

.modal.detail-modal .modal-content .modal-body .card-comment .card-item .card-item-header .github-icon {
  padding: 4px 3px 0;
  font-size: 13px;
  color: var(--text-color-light);
}

.modal.detail-modal .modal-content .modal-body .card-comment .card-item .card-item-body {
  margin: 0;
  padding: 6px 0;
}

.modal.detail-modal .modal-content .modal-body .card-discussion .divider {
  display: none;
}

/* }}} */

/* Comment form {{{ */

.comment-form,
.comment-form.logged-in {
  border-radius: 0;
  border-color: var(--border-color-light);
  background-color: transparent;
  border: none;
  padding: 0;
  margin: 0;
  border-radius: 0;
}

.comment-form .nav-tabs {
  display: inline-block;
  padding: 1px;
  border: 1px solid var(--border-color-light);
  border-radius: 3px;
  background: var(--background-color);
}

.comment-form .nav-tabs > li {
  margin-bottom: auto;
}

.comment-form .nav-tabs > li a,
.comment-form .nav-tabs > li a:hover {
  border: none;
  margin-right: auto;
}

.comment-form .nav-tabs li.active a {
  padding: 6px 12px;
  border: none;
  border-radius: 3px;
  background-color: var(--sidebar-active-color);
  color: #FFF;
}

.comment-form .tab-content {
  padding: 3px 0;
}

/* }}} */

}
