css:
@-moz-document url("https://growtopiagame.com/forums/"), url("https://www.growtopiagame.com/forums/"), url-prefix("https://growtopiagame.com/forums"), url-prefix("https://www.growtopiagame.com/forums") {
/* Background + Color Change */
html {
    background-image: url(https://nenkai.s-ul.eu/7QBhta7w.png);
    background-color: #000000;
}

/* Footer image */
.below_body {
    background: url(https://nenkai.s-ul.eu/wfhZh2WQ.png) bottom no-repeat;
}

/* Logo Change */
.logo-image img {
    height: 0 !important;
    width: 0 !important;
    padding-left: 526px !important;
    padding-top: 135px !important;
    background: url(https://nenkai.s-ul.eu/2K1DbtLg.png) no-repeat !important;
}

/* Yellow to Black */
.postbitlegacy .postfoot .textcontrols a.newreply, 
.postfoot .textcontrols a.newreply, 
.postbitlegacy .postfoot .textcontrols a.newreply:hover
{
    background: #000000 !important;
    color: white !important;
}

/* Post Bar + Latest posts (user) */
.postbit .posthead,
.postbit .postdate .time,
.postbitlegacy .posthead
{
    background-color: #16b3d6 !important;
    color: white !important;
}

/* On profile, username and rank */
#sidebar_container.member_summary .mainblock .blocksubhead
{
    color: black;
}

/* Links in about me */
.userprof_content a
{
    color: #4173A0;
}

/* And link hover */
.userprof_content a:hover
{
    color: black;
}

.navtabs li a.navtab:hover
{
  background: #000000 !important;
}

.postbitlegacy .title {
  border-bottom: 2px solid grey;
}

.pagination span a.popupctrl, .pagination span a {
  border: solid 1px #000000;
}

.pagination span.selected a {
  background: #FFFFFF;
}

.pagination span a:hover {
  border: solid 1px #000000;
}

/* Change green to blue */
.toplinks form input.loginbutton {
    background-color: #16b3d6;
}
.toplinks ul.nouser li a,
ul.platform-list li a {
    background-color: #16b3d6;
}

.birthdays, .postbitlegacy, .eventbit .eventhead {
    background-color: #16b3d6 !important;
}

.activitystream_block dd.selected,
dd.userprof_module {
    background-color: #16b3d6;
}
.memberprofiletabunder,
#activity_tab_container > div {
    background-color: #16b3d6 !important;
}
.navtabs > ul.floatcontainer li:hover a.popupctrl {
    background: #16b3d6;
}
.navtabs > ul.floatcontainer li:hover a.popupctrl.active,
.navtabs > ul.floatcontainer li a.popupctrl.active {
    background: #16b3d6;
    border-color: #16b3d6;
}
.navtabs .popupbody li {
    border-color: #16b3d6;
}
.navtabs .popupbody {
    border: 1px solid #16b3d6;
    background: #16b3d6;
}
.navtabs li.selected a.navtab {
    background: #16b3d6;
}
.navtabs ul {
    border-top: 5px solid #16b3d6;
}
.navpopupmenu.popupmenu.nohovermenu .popupctrl,
.groupcontrols .textcontrol,
a.textcontrol {
    background: white !important;
    padding: 5px !important;
    font-family: CenturyGothic !important;
}
.globalsearch .buttoncontainer span {
    background: #16b3d6;
}
#searchtypeswitcher {
    border-bottom: 4px solid #16b3d6;
}
.forumhead {
    background: #16b3d6 !important;
}
.newcontent_textcontrol {
    background: #16b3d6;
}
.threadlisthead,
h2.searchlisthead,
#forum_icon_legend h5 {
    background: #16b3d6 !important;
}
.blockhead {
    background: #16b3d6 !important;
}
.wgo_block .blockhead {
    background: #16b3d6 !important;
}
.actionbuttons input[type="submit"],
.actionbuttons input[type="reset"],
.formcontrols input[type="submit"],
.formcontrols input[type="reset"] {
    background: #16b3d6 !important;
    color: white !important;
}

body {
    color: #000;
    margin: 0 auto;
    font-family: CenturyGothicBold;
    line-height: normal;
}
a.threadtitle {
    color: #2f8cef;
}
div.message,
/* Post Quotes */
blockquote
/* Posts */
{
    font-family: Verdana;
}
.bbcode_postedby,
.navbar_advanced_search li a,
#remember.remember {
    font-family: CenturyGothicBold;
    color: #16b3d6 !important;
}

/* Poll Bar */
div.blockhead 
{
    background: #F3F3F3 !important;
}

/* New notification */
.notifications a.popupctrl
{
    background: #16b3d6 url(https://growtopiagame.com/forums/images/misc/arrow.png) right center no-repeat !important;
    animation: blinker 2s linear infinite;
}
/* Blink */
@keyframes blinker {
  50% {
    opacity: 0;
  }
}

}
