css:
@-moz-document domain("tumangaonline.com") {
/*
	Style: TMO Dark-Grey
	Author: Astral Sword
	Version 0.0.3 Beta
  	Poco a poco iré organizando el codigo que esta feo...
*/
body {
    background-color: #373737;
    color: #dbdbdb !important;
}
.fa,
.btn,
a.ng-binding,
li > a,
h1,
h2,
h3,
h4,
.link-text-color {
    color: #dbdbdb !important;
}
.text-primary,
a {
    color: #2480DF;
}

/*------Header Menu------*/
.navbar-default,
.navbar-default .navbar-brand,
.navbar-default .navbar-nav.navbar-nav-bordered > li {
    border: none;
}
.navbar.navbar-size-xlarge .navbar-nav > li > a,
.navbar-size-xlarge .navbar-brand,
.navbar.navbar-size-large,
.dropdown-menu,
.dropdown-menu > li,
.navbar-brand-logo,
#main-nav > div.navbar-right > a.navbar-btn.btn.btn-primary {
    background-color: #2C2C2C !important;
    height: 40px !important;
    min-height: 40px !important;
    line-height: 40px !important;
    color: #fff !important;
    margin-top: 0px;
}
.svg {
    height: 40px !important;
    min-height: 40px;
}
.navbar-default,
.navbar.navbar-size-xlarge .navbar-nav > li > a,
.navbar-size-xlarge .navbar-brand,
.navbar.navbar-size-large,
#main-nav > div > a.navbar-btn.btn-sm.btn.btn-link {
    font-size: 1.3rem;
    font-weight: 400;
    padding: 0 10px;
}

/*--notificaciones--*/
#main-nav > div > a.navbar-btn.btn-sm.btn.btn-link > i {
    font-size: 1.2em;
    color: #fff !important;
    border: none;
}
.badge,
.badge-notify {
    top: -9px !important;
    font-size: 11px;
}
.navbar.navbar-size-xlarge,
.navbar-btn.btn-sm,
.navbar.navbar-size-large {
    margin: 0px !important;
}
#main-nav .dropdown-menu > li > a:hover,
.navbar-default .navbar-nav > li > a:hover,
.navbar-default .navbar-nav > .open > a,
.navbar-default .navbar-nav > .active > a,
.navbar-nav .dropdown-menu > li a:hover {
    background-color: #414141 !important;
}
.navbar-nav .dropdown-menu > li > a,
.navbar-nav .dropdown-menu > li > a:focus {
    font-weight: 500;
    padding: 8px 14px;
    border-top: none;
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.175);
}

/*--Borde botones--*/
.navbar-default .navbar-nav > .active > a {
    border-bottom: 5px solid #AFAFAF;
}
.navbar-default .navbar-nav > li > a:hover {
    border-bottom: 5px solid #dbdbdb;
}
.navbar-default .navbar-nav > li > a {
    color: #212121;
    /*border: 0 solid;*/
    transition: all .15s ease-in-out;
}

/*--Contenedor--*/
.page-content .container {
    margin-top: 10px;
    background-color: #212121;
}
.panel,
.profile-avatar {
    background-color: #414141;
    border: none;
}
.panel-default > .panel-heading,
.mangaListItem,
.list-group-item,
.navbar-default,
#page-scan .custom-background {
    background-color: #2C2C2C;
    border: none;
}

/*--paginacion--*/
.pagination > .disabled > a,
.pagination > li > a,
.pagination > li > span,
.key-button {
    background-color: #4B4B4B;
    border: none;
    box-shadow: none;
}
.pagination > .disabled > a:hover,
.pagination > li > a:hover,
.pagination > li > span:hover {
    background-color: #6F6F6F;
    border: none;
}
.pagination > .active > a,
.pagination > .active > a:hover,
.btn-primary,
.bg-blue-400 {
    background-color: #2480DF;
}

/*--pestaña info manga--*/
.popover,
.alert-info {
    background-color: #2C2C2C;
    border: 1.5px solid #dbdbdb;
    box-shadow: 0px 0px 10px 5px #767676;
}

/*--Boton--*/
.btn {
    border: none;
}
.btn-default {
    background-color: #4B4B4B;
}
.btn-default:hover {
    background-color: #6F6F6F !important;
}

/*--footer--*/
.footer-section {
    background-color: #212121 !important;
    margin-top: 100px;
    border-top: 2px solid #6F6F6F;
}
}

@-moz-document url("https://www.tumangaonline.com/home"), url("https://www.tumangaonline.com/perfil"), url("https://www.tumangaonline.com/biblioteca") {
.media.media-clearfix-xs {
    border-top: 5px solid #6F6F6F !important;
}

/*--18text--*/
.text-danger {
    color: #bd362f;
    background-color: #212121;
    padding: 2px;
    border-radius: 5px !important;
}
}

@-moz-document url-prefix("https://www.tumangaonline.com/biblioteca"), url-prefix("https://www.tumangaonline.com/perfil") {
/*--Sub-Menu--*/
.hidden-xs.navbar.navbar-default.bg-blue-400.navbar-size-small.navbar-size-small.ng-scope,
.tagElement:hover {
    background-color: #2480DF !important;
}
.text-primary {
    color: #2480DF !important;
}
#submain-nav li > a:hover {
    background-color: #358DE2 !important;
}
#page-content > div[ng-controller="MangaCtrl"] > div:nth-child(3) > div:nth-child(1) {
    margin: 0 -20px;
}

/*---cuadro titulo manga---*/
#page-content > div > div:nth-child(3) > div:nth-child(1) > div > div.media.v-middle {
    background-color: #212121;
    border: none;
    border-bottom: 3px solid #414141;
}
.mangaListItem .card-info,
#page-content > div > div:nth-child(3) > div:nth-child(2) > div.col-md-9 > div:nth-child(3) > div.col-md-9 > div.tab-content.padding-none > div.text-center {
    background-color: #2C2C2C;
    border: none;
}
.panel h1,
.info-title.one-line > a,
.hidden-info li {
    font-weight: 400;
}
.tagElement {
    background-color: #4B4B4B;
    border: none;
}
.page-header {
    border: none;
}
.collapsed {
    border-bottom: 3px solid #212121 !important;
}
.table > tbody > tr > td {
    border-top: 1px solid #2C2C2C;
}
.label-grey-200 {
    background-color: rgba(255, 255, 255, 0) !important;
}
.panel-collapse-trigger.panel-heading:after {
    color: #dbdbdb !important;
}
}

@-moz-document url-prefix("https://www.tumangaonline.com/biblioteca/mangas") {
/*---cuadro titulo manga---*/
#page-content > div > div:nth-child(3) > div:nth-child(1) > div > div.media.v-middle {
    border-bottom: 3px solid #2480DF;
}
}

@-moz-document url-prefix("https://www.tumangaonline.com/biblioteca/manhwas") {
/*---cuadro titulo manhwa---*/
#page-content > div > div:nth-child(3) > div:nth-child(1) > div > div.media.v-middle {
    border-bottom: 3px solid #66bb6a;
}
}

@-moz-document url-prefix("https://www.tumangaonline.com/biblioteca/manhuas") {
/*---cuadro titulo manhua---*/
#page-content > div > div:nth-child(3) > div:nth-child(1) > div > div.media.v-middle {
    border-bottom: 3px solid #8d6e63;
}
}

@-moz-document url-prefix("https://www.tumangaonline.com/biblioteca/novelas") {
/*---cuadro titulo novela---*/
#page-content > div > div:nth-child(3) > div:nth-child(1) > div > div.media.v-middle {
    border-bottom: 3px solid #ef5350;
}
}

@-moz-document url-prefix("https://www.tumangaonline.com/lector") {
.imgvisor {
    background-image: url(/*[[moe]]*/);
    background-repeat: no-repeat;
    background-position: center;
    min-height: 300px;
    min-width: 500px;
}
#page-content > div > div.container-fluid.padding-none{
background-color: /*[[fcolor]]*/ !important;
}

}
