@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(producthunt.com) {
/*mainbar text*/
.light_4a7e0 {
    color: #f14e32 !important;
}

/*outlines*/
.box_65c48, .preview_92d6a {
    border: 1px dotted #f14e32;
}
 .recommendedProducts_3cf00, .link_523b9 {
    border-bottom: 1px dotted #f14e32;
}

/*inner borders*/
div, svg, a, section, .xLarge_18016, .contributorAccess_370ef {
    background-color: #333 !important;
    color: #757575 !important;
}

/*links*/
a, a:visited, h1, h2, h3, h4, h5 {
    color: #f14e32 !important;
}

/*sidebar*/
a:hover, .activeItem_c89bf .activeItem_c89bf > span:nth-child(2), .activeItem_c89bf, .activeItem_c89bf > span:nth-child(2) {
    background-color: #f14e32 !important;
    color: #333 !important;
}

/*sidebar text*/
.itemText_063f9 {
	color: #fff;
}

/*fixed highlighed main*/
.link_523b9:hover {
    background-color: #333 !important;
}

/*clicked link box shadow*/
.contentBackground_bc644 {
	box-shadow: 1px 0 6px 0 rgba(255, 255, 255, 0.3);
}
  
 /*top search*/
.input_7ba38 {
    color: #fff !important;
    background-color: #333;
}}
