css:
@-moz-document regexp(".*thingiverse\\.com\\/thing:\\d*\\/#files") {
.main_content {
    width: 1300px;
}

#thing-files .thing-page-left {
    width: 955px;
}

#thing-files .filename {
    overflow-x: auto;
    text-overflow: inherit;
    font-size: 12px;
    font-weight: bold;
}

#thing-files .details-header {
    width: 640px;
}

#thing-files .detail {
    max-width: 565px;
    width: 565px;
}
}
