css:
@-moz-document url-prefix(https://steemit.com/) {
.Markdown img, .ReplyEditor__body.rte img {
width:200px!important;
height:auto;
}

.column {
    background: #1C252B;
    color: hsl(0, 0%, 27.1%);
}
.entry-content {
    color: white;
    font-size: 120%;
}

.p {
font-family: 'Montserrat', sans-serif;
}


.articles__resteem {
display: none!important;
}
.articles__tag-link {
display: none!important;
}

.Header__userpic  {
display: none!important;
}

.TagList__horizontal {
display: none!important;
}
.UserProfile__bio {
display: none!important;
}
.UserProfile__stats {
display: none!important;
}
.UserProfile__info {
display: none!important;
}

.articles__header {
display: none!important;
}
.articles__hr {
display: none!important;
}



          
.PostFull__responses {
              display: none!important;
          }
.PostFull__views {
              display: none!important;
          }
.Promote__button {
              display: none!important;
          }
.shareMenu {
              display: none!important;
          }
.explore-post {
              display: none!important;
          }
.Reblog__button {
              display: none!important;
          }

.theme-light {
background: #333333!important;
}

.PostFull__reply {
    position:fixed;
    right:1px;
    top: 120px;
    font-size: 250%;
    padding: 16px 32px;
    text-align: center;
    text-decoration: none;
color: #841B2D!important;

          }

.ReplyEditor .vframe {
    position: absolute;
    left: 5%;
    width: 40%;
}

.ReplyEditor .vframe textarea {
    height: 500px;
font-size: 24px;
}




.Preview {
    position: absolute;
    top: 0;
    left: 100%;
    width: 100%;
    margin-left: 20px;
}


.Userpic {
    width: 85px;
    height: 85px;
    border-radius: 20px;
}

.Post_comments__content {
    background: #101619;
    color: #fff;
    font-size: 100%;
    padding: 10px;
    border-radius: 20px;
}


body, .App__content, .theme-original {
    background: #1C252B;
    color: hsl(0, 0%, 27.1%);
  }
  
  blockquote > p
  {
     color: blue!important;
  }

  a, a:focus, a:visited, a:active, .steemit a, table thead, .Comments__votes, .Comment__header-user, .Post__comments_sort_order, .PostFull__time_author_category_large a, .PostFull__time_author_category_large strong, .PostFull__footer .FoundationDropdownMenu__label
  {
        color: #59866d!important;
  }

.PostFull__footer a, .PostFull__views, .Comment__footer, .VotesAndComments__votes 
{
        color: red!important;
  }


  a:hover {
    color: #06D6A9 !important;
  }
  
  hr {
    border-bottom: 1px solid hsla(305.1, 63.8%, 70.8%, 0.6);
    margin: 1rem auto 1.2rem;
  }
  
  label {
    color: hsl(35.8, 100%, 31.6%);
  }
  
  table thead, table tbody, table tfoot {
    background-color: transparent;
  }
  
  .Markdown blockquote > p, .ReplyEditor__body.rte blockquote > p, .theme-original .Markdown blockquote > p, .theme-original .ReplyEditor__body.rte blockquote > p, blockquote * {
    color: blue!important;
  }
  
    .theme-original .Markdown blockquote, .theme-original .ReplyEditor__body.rte blockquote
  {
    border-color:  hsl(0, 100%, 50%) !important;

  }
  
  /* logo */
  
  #steem path:nth-of-type(2){
    fill:hsl(309, 100%, 85.7%) !important;
  }
  
  #steem path {
    fill: hsl(55.5, 80.5%, 83.9%);
  }
  .beta{
    color: hsl(267, 100%, 66.9%) !important;
  }
  
  /* header */
  
  .UserProfile__banner > div.column {
       text-shadow:none !important;
color: white!important;
   }

  .Header {
    box-shadow: 0px 0 8px 2px hsla(300, 40%, 90.2%, 0.4);
  }
  
  .Header__top, .header {
    border-bottom: 1px solid hsla(276.7, 23%, 58.2%, 0.2);
  }

  .Header__top, .Header__top .menu{
     background-color: #1C252B;
  }
  .Header__sub-nav, .Header__sub-nav .menu, .top-bar, .top-bar ul {
    background:  linear-gradient(to bottom, rgb(196, 166, 196) 0%,rgb(221, 188, 221) 86%,rgb(201, 167, 201) 100%) !important;
  }   
  
  .Header__sub-nav {
    border-bottom: 1px solid hsl(300, 34.7%, 81.4%);
  }
  
  li.Header__userpic .Userpic {
    border: 1px solid hsla(302.9, 99.1%, 54.7%, 0.6);
                        
  }

  .Header .menu .hamburger::after {
     background:  hsl(300, 42.3%, 46.9%);
     box-shadow: 0 7px 0  hsl(300, 42.3%, 46.9%),0 14px 0  hsl(300, 42.3%, 46.9%);
  }  
  
  .Header .menu .hamburger:hover::after{
     background: hsl(300, 100%, 95.1%) ;
     box-shadow: 0 7px 0 hsl(300, 100%, 95.1%) ,0 14px 0 hsl(300, 100%, 95.1%);
  }
  
  .UserProfile__banner {
    color: hsl(313.4, 91.5%, 46.1%);
  }
  
  .Header__top ul > li.delim {
    color: hsl(300, 42.3%, 46.9%) !important;
  }
  
  .submit-story a{
    background: hsl(312.4, 58.1%, 45.9%) !important;
    color: hsl(20.8, 72.2%, 92.9%) !important;
    border-radius: 6px;
  }


  
  /* search icon */
  
  .Header svg > path {
    fill: hsl(300, 42.3%, 46.9%);
   }

   .Header svg:hover > path {
    fill: hsl(300, 100%, 95.1%);
   }
  
  /* navigation menu */
  
  .Header__sub-nav li > a {
    color: hsl(213, 29%, 72.9%);
  }   
  
  .HorizontalMenu li > a:hover {
    color: hsl(242.2, 41.5%, 87.3%);
  }
  
  .Header__sub-nav li > a {
    background: transparent;
  }
  
  .UserProfile__top-nav .menu > li > a.active {
    background: linear-gradient(to bottom,hsl(29.3, 77.6%, 77.3%) 0%,hsl(28.4, 100%, 92.5%) 14%,hsl(29.1, 82.5%, 84.3%) 100%) !important;
    color: hsl(0, 0%, 99.6%);
  }
  
  .HorizontalMenu .active > a {
    color: hsl(213.3, 39.1%, 82%);
  }
  
  .UserProfile__top-nav {
    background: #26323A !important;
Display: none!important;
  }
  
  .UserProfile__top-nav .menu > li > a:hover, .UserProfile__top-menu > .shrink > .menu > li:hover {
    background: linear-gradient(to bottom,hsl(29.3, 77.6%, 77.3%) 0%,hsl(28.4, 100%, 92.5%) 14%,hsl(29.1, 82.5%, 84.3%) 100%);
    color: hsl(301.5, 76.6%, 58%) !important;
  }
    
  .VerticalMenu svg path, .VerticalMenu svg polygon {
    fill: hsl(240, 20%, 89.2%);
  }
  
  .VerticalMenu > li.title {
    border-bottom: 1px solid hsla(0, 0.4%, 55.9%, 0.5);
  }
  
    .theme-original .articles__layout-selector:hover .icon-svg--accent {
    fill: white;
  }
  
  /* drop down menu */
  
  .Icon.dropdown-arrow polygon {
     fill: hsl(300, 72.1%, 56.5%);
  }
  
  .dropdown-pane, .gsc-control-cse .gsc-option-menu, .DropdownMenu > .VerticalMenu {
    border: 1px solid hsl(314.3, 20%, 58.8%);
    background-color: hsl(316.2, 49.3%, 85.3%);
  }
  
  .gsc-option-menu-item {
    color: hsl(240, 68.2%, 91.4%) !important;    
  }

  .VerticalMenu > li > a:hover, .gsc-option-menu-item-highlighted {
    background-color: hsla(313.8, 65%, 76.5%, 0.3);
  }
  
  .VerticalMenu svg path, .VerticalMenu svg polygon
  {
   fill: hsl(314.3, 80.7%, 63.3%);
  }
  
  /* Sidebar menu */
  
  .SidePanel > div {
    background-color: #26323a;
  }
  
  .SidePanel > div .menu > li > a {
    color: yellow!important;
    border-top: 1px solid #1c252b;
   }

  .SidePanel > div .menu > li > a:hover {
    background-color: hsl(319.6, 88.5%, 89.8%) !important;
  }
  
  .SidePanel .menu > li.last {
    border-bottom: 1px solid#1c252b;
  }
  
  .SidePanel > div .menu > li path {
   fill: hsl(320.6, 88.8%, 68.4%) !important;
  }
  
  .theme-original .c-sidebar__module, .c-sidebar__module {
    background-color:#26323a!important;
    border-color: #1c252b!important;
  }

  
  /* notifications */
  
  .NotifiCounter {
    background-color: hsl(332.6, 89.5%, 51.6%);
  }
  
  /* user profile */
  
  .UserProfile__banner > div.column, .UserProfile__banner > div.columns {
      background-image: none!important;                  
     background-size: cover;
    background-position: right;
    box-shadow: 0px 0px 4px 0px hsl(347.5, 100%, 95.3%) inset;
  }
  
  .UserProfile__banner .UserProfile__stats,
  .UserProfile__banner .UserProfile__info 
  {
    font-weight:bold;
     font-size:150%;                   
  }
  
  .UserProfile__banner .Userpic {
    border: 2px solid hsla(245.9, 97.6%, 83.5%, 0.6);
  }
  
  .UserProfile__banner .Icon svg {
   fill: hsl(313.5, 97.9%, 37.3%)!important;
  }
  
  /* user data */
  
  .Reputation {
    background-color: yellow!important;
    border: 1px solid hsla(0, 59.1%, 25.9%, 0.4)!important;
    border-radius: 50%;
    color: blue!important;
  }
  
  /* Posts */
  
  .articles__tag-link {
    color: hsl(312.6, 53.7%, 65.3%) !important;
  }
  
  .layout-list .articles, .theme-original .articles {
    background-color: transparent!important;
    border: 0px solid !important;
  }  
  
  .PostFull {
    border-bottom: 1px solid hsla(285.5, 66%, 81.6%, 0.3);
  }
  
  .layout-list .articles__summary, .theme-original .articles__summary {
    padding: 0.7rem;
    background: #26323a;
    border: 1px solid black;
    border-radius: 11px;
    box-shadow: 0px 0px 4px 0 hsla(0, 100%, 73.1%, 0.3);
    border-bottom:0px solid !important;
  }
  
  .entry-title > a, .entry-title, .PostSummary__header > h3 > a
  {
   color: #3ab770 !important;    
  }
  
  .PostSummary__body {
    color: hsl(240, 3%, 74.1%);
  }
  .PostSummary__image {
    background-color: transparent;
    border: 1px solid hsl(238.8, 25.7%, 37.5%);
    border-radius: 5px;
  }
  
  .articles__resteem-text {
    color: hsl(146.2, 75.7%, 46.9%);
  }
  
  .PostSummary__reblogged_by .Icon path, .reblog path {
    fill: hsl(146.2, 75.7%, 46.9%);
  }

  .PostSummary__reblogged_by, .articles__resteem-text{
    color:  hsl(146.2, 75.7%, 46.9%)!important;
  }
  
  .PostsList__post_overlay {
    background-color: hsla(240, 21.3%, 18.4%, 0.9);
  }

  .PostsList__post_container, .PostsList__post_container .PostFull, .PostsList__post_container  {
    background: hsl(22, 100%, 94.1%)!important;
    color: hsl(0, 0%, 27.1%);
  }
  
  .theme-original .link--secondary, .theme-original .PlainLink, .theme-original .PostFull__header a, .PostFull__header .theme-original a, .theme-original .articles__resteem-username, .theme-original .articles__tag-link a, .articles__tag-link .theme-original a, .theme-original .user__username, .theme-original .user__reputation, .theme-original .timestamp__time, .theme-original .articles__resteem .username, .articles__resteem .theme-original .username {
    color: hsl(316.7, 53%, 64.1%);
  }
  
  .theme-original .link--secondary:hover, .theme-original .PlainLink:hover, .theme-original .PostFull__header a:hover, .PostFull__header .theme-original a:hover, .theme-original .articles__resteem-username:hover, .theme-original .articles__tag-link a:hover, .articles__tag-link .theme-original a:hover, .theme-original .user__username:hover, .theme-original .user__reputation:hover, .theme-original .timestamp__time:hover, .theme-original .articles__resteem .username:hover, .articles__resteem .theme-original .username:hover, .theme-original .link--secondary:focus, .theme-original .PlainLink:focus, .theme-original .PostFull__header a:focus, .PostFull__header .theme-original a:focus, .theme-original .articles__resteem-username:focus, .theme-original .articles__tag-link a:focus, .articles__tag-link .theme-original a:focus, .theme-original .user__username:focus, .theme-original .user__reputation:focus, .theme-original .timestamp__time:focus, .theme-original .articles__resteem .username:focus, .articles__resteem .theme-original .username:focus {
    color: hsl(327.3, 69.9%, 77.8%);
  }
  
  .theme-original .articles__summary-footer {
    border-top: 1px solid hsla(326.9, 52.7%, 81.8%, 0.5);
  }
  
  .theme-original .articles__summary-header {
     border-bottom: 1px solid hsla(326.9, 52.7%, 81.8%, 0.5);
  }
  
  #svg-icon-symbol-layout * {
    fill: hsl(120, 36.2%, 72.9%);
  }
  
  /* voting interface */

  .Voting__adjust_weight .rangeslider-horizontal .rangeslider__fill, 
  .Voting__adjust_weight_down .rangeslider-horizontal .rangeslider__fill
  {
      background: hsl(292.2, 100%, 50%);
  }

    .PostFull__footer .Reblog__button, .PostFull__reply, .PostFull__views {
    border-right: 1px solid hsl(40, 5%, 52.9%);
  }
  
  .PostSummary__time_author_category, .PostFull__time_author_category, .Comment__header .updated{
    border-right: 1px solid hsla(106.4, 8.8%, 51%, 0.6) !important
  }
  
   .PostFull__reply {
      background: #3ab770 !important; 
   }

   .PostFull__footer {
      background: #3ab770!important; 
      border-bottom:1px solid hsla(276.7, 23%, 58.2%, 0.2)!important;
      z-index: 999;
   }
  
  .PostFull__footer .FoundationDropdownMenu__label, .PostFull__footer a, .PostFull__views, .Comment__footer a{
    color: hsl(30, 20.8%, 71.8%);    
  }

  .PostSummary__time_author_category,.PostFull__time_author_category_large, .PostFull__time_author_category, .Comment__header .updated, .PostFull__time_author_category_large a, .PostFull__time_author_category_large strong{
    color:  hsl(300, 42.3%, 46.9%)!important;
   }

  .FormattedAsset .decimal {
    opacity: .8;
    color: #6CA000;
   }

  .Voting__inner, .VotesAndComments__votes, .PostSummary__time_author_category .Reblog__button, .PostFull__time_author_category,.PostFull__footer .Reblog__button, .PostFull__reply, .PostFull__views {
    border-right: 1px solid hsl(20.4, 83.7%, 75.9%);
  }
  
  .PostSummary__time_author_category
  {
    border-left: 1px solid hsl(20.4, 83.7%, 75.9%);
  }
  
  #Chevron_Up_Circle path {
    stroke: hsl(301.9, 100%, 37.5%) !important; 
  }
  
  #Chevron_Up_Circle circle  {
    fill: transparent;
    stroke: hsl(323.2, 67%, 41.6%) !important;
  } 

  .Voting__button--upvoted   #Chevron_Up_Circle circle  {
    fill: hsla(323.3, 66.2%, 71%, 0.5) !important;
    stroke: hsl(323.2, 67%, 41.6%) !important;
  }
  
  .Icon_1x #Chevron_Up_Circle circle {
    fill: hsla(323.3, 66.2%, 71%, 0.5) !important;
    stroke: hsl(323.2, 67%, 41.6%) !important;
  }
 
    .Voting__button--upvoted  > a:hover path
  {
    fill: hsl(0, 0%, 98.4%)
  } 
  
  .Voting__button--upvoted > a:hover circle, a.confirm_weight:hover circle {
    stroke: hsl(120, 89.6%, 37.6%)!important;
    fill: hsl(327.2, 66%, 71.2%) !important;
  }
  
  .Reblog__button-active path {
    fill: hsl(151.9, 97.1%, 41%) !important;
  }

  .VotesAndComments path, .Reblog__button path {
    fill: hsl(313, 54.2%, 64.9%)!important;
  }
  
  /* labels and markers */
  
  .PostFull__footer svg {
    fill: hsl(300, 45.2%, 67.1%);
  }
  
  .nsfw-flag {
    color: hsl(0, 95.1%, 75.9%);
    border: 1px solid hsl(0, 95.1%, 75.9%);
  }
  
  .Voting__button path {
    fill: hsl(118.7, 100%, 27.6%);
  }
  
    .theme-original .layout-list .articles__hr{
    border-bottom: 1px solid hsla(77.3, 53.2%, 75.7%, 0.9);
  }

  /* Post Index */
  
  .PostsIndex__topics {
    border-left: 1px solid red;
   }

  .Topics__title {
    color: hsl(283.8, 54.7%, 52.4%)!important;
  }

  /* comments */
  
    .Comment__header-user, .Post__comments_sort_order {
    color: hsl(30, 20.8%, 71.8%);    
  }
  
  .callout {
    border: 1px solid hsla(0, 0%, 20%, 0.3);
    background-color: hsla(0, 0%, 100%, 0.1);
    color: hsl(0, 21.7%, 45.1%); 
  } 

  .Comment .highlighted {
    background: hsl(29.2, 64.4%, 76.9%);
    border-radius: 7px;
    padding: 0.5rem;
  }
  
  /* buttons */
  
  .close-button {
    color: hsl(0, 100%, 79.4%)!important;
  }
  
  .close-button:focus, .close-button:hover {
    color: hsl(0, 100%, 69%)!important;
  }
  
  .button.disabled, .button[disabled] {
    opacity: .4;
  }
  
  .button {
     background-color: hsl(38.3, 96.7%, 35.3%);
  }

  .button:focus, .button:hover {
    background-color: hsl(38.3, 71.3%, 46.5%);
  }
  
  .button.hollow {
    border: 1px solid hsl(300, 42.3%, 46.9%);
    color: hsl(300, 42.3%, 46.9%);
  }
  
  .button.hollow:focus, .button.hollow:hover {
    border-color: hsl(300, 100%, 68.8%) ;
    color: hsl(300, 100%, 68.8%) ;
   }
  
  .button.disabled, .button.disabled:focus, .button.disabled:hover, .button[disabled], .button[disabled]:focus, .button[disabled]:hover {
    background-color: hsl(37.6, 67.1%, 42.9%);
  }

  /* tags */
  
  .TagList__horizontal a {
    background: #1c254b;
    color: hsl(39.7, 41.3%, 72%);
   }

  .TagList__horizontal a:hover {
      background: #1c256b;
  }
  
  /* inputs */
  
  input:disabled, input[readonly], textarea:disabled, textarea[readonly] {
    background-color: white;
    color: black;
font-size: 24px;
   }
  
  [type="color"]:focus, [type="date"]:focus, [type="datetime-local"]:focus, [type="datetime"]:focus, [type="email"]:focus, [type="month"]:focus, [type="number"]:focus,    [type="password"]:focus, [type="search"]:focus, [type="tel"]:focus, [type="text"]:focus, [type="time"]:focus, [type="url"]:focus, [type="week"]:focus, textarea:focus {
    outline: none;

    background-color: #ddebcc;
    border: 1px solid hsl(21.1, 39.5%, 53.3%);
    box-shadow: 0 0 5px hsla(18.9, 42.2%, 51.2%, 0.6);
  }
  [type="color"], [type="date"], [type="datetime-local"], [type="datetime"], [type="email"], [type="month"], [type="number"], [type="password"], [type="search"], [type="tel"], [type="text"], [type="time"], [type="url"], [type="week"], textarea {
    background-color: hsla(38.2, 38.4%, 53.5%, 0.5);
    border: 1px solid hsl(34.1, 21.2%, 59.2%);
    box-shadow: 0 0 5px hsla(23.3, 35%, 47.6%, 0.6);
    color: hsl(0, 0%, 27.8%);
  }
  
  .drag-and-drop {
    background-color: hsl(33.3, 46.6%, 77.3%);
    color: hsl(0, 14.3%, 39.8%);
    border-color: hsl(33.3, 21.5%, 59%);
  }
  
  select, select:focus {
    background-color: hsl(27.4, 67.6%, 73.3%);
    border: 1px solid hsl(21.3, 59.5%, 55.5%);
    color: hsl(0, 100%, 95.7%);
    box-shadow: 0 0 5px hsla(23.5, 18.7%, 24.1%, 0.4);
  }
  
  /* editor */

.drag-and-drop {
display:none!important;
}
  
  .ReplyEditor .Preview .Markdown {
    border: 1px solid hsl(29.3, 43.3%, 59.2%);
    border-radius: 8px;
background: white;
color: black;
font-size: 24px;
  }
  
  .ReplyEditor__draft {
    background: hsla(125.6, 34%, 18.4%, 0.9);
    color: hsl(0, 0%, 73.3%);
font-size: 24px;
  }

  .RichTextEditor__root___33zoV{
     background: hsl(34, 43.5%, 62.5%);
     border-color: hsl(33.9, 11.6%, 61%);
  }
  
  /* google search */
  
  .cse .gsc-control-cse, .gsc-control-cse {
    background: transparent;
    border: 0px solid;
  }
  .gsc-input, .gsc-completion-container table {
    color: black!important;
  }
  
  .gsc-result-info, .gsc-orderby-label {
    color: hsl(0, 0%, 34.9%);
  }
  
  .gsc-above-wrapper-area, .gsc-webResult.gsc-result {
    border-color: hsla(19, 70.3%, 71%, 0.7);
  }
  
  .cse .gsc-webResult.gsc-result:hover, .gsc-webResult.gsc-result:hover, .gsc-webResult.gsc-result.gsc-promotion:hover, .gsc-results .gsc-imageResult-classic:hover, .gsc-results .gsc-imageResult-column:hover{
   border-color: hsla(0, 90.2%, 64.1%, 0.7);  
  }
    
  .gsc-preview-reviews, .gsc-control-cse .gs-snippet, .gsc-control-cse .gs-promotion em, .gsc-control-cse .gs-snippet, .gsc-control-cse .gs-promotion em {
    color: hsl(0, 0%, 37.3%);
  }

  .gs-result .gs-title, .gs-result .gs-title * {
    color: hsl(292.3, 82.7%, 38.6%);
  }

  .gsc-results .gsc-cursor-box .gsc-cursor-page {
    color: hsl(0, 100%, 41.2%);
  }
  
  .gsc-option-menu-item {
    color:hsl(300.5, 89.5%, 26.1%) !important;
  }
  
  /* wallet */
  
  .UserWallet__claimbox {
    background: hsla(102.3, 76.2%, 44.5%, 0.4);
    border-color: hsla(102.2, 48.1%, 54.7%, 0.7);
  }
  
