css:
@-moz-document url-prefix("https://catwar.su") {
#t,
#t_hide/* реклама*/
{
    display: none;
}
}

@-moz-document url("https://catwar.su/cw3/") {
/*
________________________________________________

   Cream  |   Aqua  |  Green  |   Red    
  #282828 | #2C2C36 | #2A2A2C | #2F353A  ← цвет границ 
  #222222 | #23232D | #212122 | #2b3034  ← цвет фона у главной таблицы (например, чат) 
  #191919 | #1D1D24 | #19191A | #262a2e  ← цвет заднего фона

  #B9B384 | #77BABC | #63b97f | #ff7b5d  ← цвет ссылок
  #908B67 | #5B8E90 | #4B8B60 | #B35843  ← цвет выделения 

________________________________________________
*/

:root { 
	--color1: #282828;
	--color2: #222222;
	--color3: #191919;
	--color4: #B9B384;
	--color5: #111;
	--color6: #838383;
	--color7: #383838;
	--color8: #000;
	--color9: #fff;
	--color10: #333;
	--color11: #908B67;
	--color12: #ccc;
	--color13: rgba(34,34,34,0.9);
}

#newchat, #newls {
    color: var(--color3);
}

a, a:hover, .nick {
    color: var(--color4);
}

select, body, input, .hotkey, #mit, #mitok {
	color: var(--color6);
}

.move_name, .myname, .log_teeth, .log_claws {
    color: var(--color8);
}

.adopt-id, #text {
	color: var(--color9);
}

body, .move_name {
    font-size: 13px;
}

#block_deys, #chat_form, #tr_mouth, #history_clean, .log_teeth, .log_claws, .log_paws, #history_block div {
	font-size: 14px;
}

#block_deys {
	font-size: 15px;
}

.title {
    font-weight: normal;
}

#error, #fightPanel {
    background: var(--color1);
}

a.dey:hover, #dein a.specialAct:hover, #dein a.exchange:hover, #dialog:hover, .itemInMouth:hover, #reveal_info img:hover  {
	background-color: var(--color1);
    border-radius: 10px;
}

a.dey:hover, #dein a.specialAct:hover, #dein a.exchange:hover, #dialog:hover {
    padding-top: 60px;
}

#reveal, #tr_chat, #chat_msg, #tr_actions, #tr_mouth, #family, #history, #parameter {
    background-color: var(--color2);
}

body {
    background-color: var(--color3);
}

.ui-scroll, #newchat, #newls, .myname {
	background-color: var(--color4);
}

select, input, .ui-slider, .hotkey, #mit, #mitok {
    background: var(--color5);
}

.ui-slider .ui-slider-handle, input:focus {
	background: var(--color7);
}

#history div {
    background: none !important;
}

#reveal {
    background-image: none !important;
}

select, #reveal, input, hr, .ui-slider, .ui-slider .ui-slider-handle, .hotkey, #fightPanel, #mit, #mitok, #error {
    border: 0.5px solid var(--color1);
}

#parameters_block hr {
    border: 0;
}

.active_thing, #reveal_info img.active_thing {
	border: 1px solid var(--color4);
    border-radius: 10px;
}

input, .hotkey, #mit, #mitok {
    border-radius: 5px;
}

 table
  {
    border-color: transparent;
  }

.title, .toggle, #chat_msg {
	margin-left: 5px;
}

#akten, #block_mess, #layer, #relatives_block, #history_block, #parameters_block, #dein, #tir, #tr_mouth td > a, #thdey, #ctdey {
    margin-left: 17px;
}

#akten, .title, .infos, #mit, #mitok, #block_mess, #dein, #tir, #history div, #pairs p, #parameters_block hr  {
	margin-top: 5px;
}

#main_table {
	margin-top: 10px;
}

#thdey #eat, #ctdey #put2, #history_clean {
    margin-top: 8px;
}

#tos {
    margin-top: 2px;
}

#info_main h2 {
    margin-top: inherit;
}

#akten, #mitok, #block_mess, #dein, #tir, #tr_mouth td > a, #history div, #ctdey [href^="/cat"], #thdey [href^="/about"], #pairs p, #history_clean {
	margin-bottom: 5px;
}

.infos h2 {
    margin-bottom: 8px;
}

#reveal_info li, #parameters_block hr {
	margin-bottom: 3px;
}

#parameters_block {
	margin-bottom: 21px;
}

a.dey, a.specialAct, .itemInMouth, #reveal_info .exchange img {
    margin-right: 5px;
}

.ui-slider {
	margin-right: 50px;
}

.active_thing.catrot {
    border: 1px solid var(--color4);
	border-radius: 10px;
	margin-top: -1px;
	margin-left: -1px;
	margin-bottom: -1px;
}

.active_thing, #reveal_info img.active_thing {
    border: 1px solid var(--color4);
	border-radius: 10px;
	margin-top: -1px;
	margin-left: -1px;
    margin-right: 4px;
	margin-bottom: -1px;
}

a.dey:hover, #dein a.specialAct:hover, #dein a.exchange:hover, #dialog:hover, #dialog, a.dey, a.specialAct, a.exchange {
    padding-top: 60px;
}

.log_teeth, .log_claws, .log_paws {
    padding-left: 5px;
    padding-right: 5px;
}

#items {
	padding-left: 17px;
	padding-top: 5px;
	padding-bottom: 5px;
}

#tr_mouth td > br, #thdey br:last-of-type {
    display: none;
}

#tr_mouth td > a, #thdey #eat, #ctdey [href^="/cat"], #ctdey #put2, #thdey [href^="/about"], #history_clean {
    display: inline-block;
}

*::-webkit-scrollbar {
    width: 15px !important;
    height: 10px !important;
    background:var(--color13) !important;
    border: 1px solid var(--color2) !important;
}

*::-webkit-scrollbar-thumb {
    min-height: 28px !important;
    background: var(--color10) !important;
}

::-webkit-scrollbar-corner {
	background: var(--color5) !important;
}

::selection {
    background-color: var(--color11);
    color: var(--color12);
}
}
