@-moz-document domain("4um.bjork.com") {
/* ---- general ----*/

/* background with gradient */

body {
    min-height: 100% !important;
    
    animation: none !important;
    background: #3cdbc0 none !important; /* For browsers that do not support gradients */
    background: -webkit-radial-gradient(farthest-side circle at 300px 100px, #f3d3c6 40%, #3cdbc0 100%) !important; /* Safari */
	background: -o-radial-gradient(farthest-side circle at 300px 100px, #f3d3c6 40%, #3cdbc0 100%) !important; /* Opera 11.6 to 12.0 
*/
	background: -moz-radial-gradient(farthest-side circle at 300px 100px, #f3d3c6 40%, #3cdbc0 100%) !important; /* Firefox 3.6 to 15 */
	background: -ms-radial-gradient(farthest-side circle at 300px 100px, #f3d3c6 40%, #3cdbc0 100%) !important; /* ie10+ */
	background: radial-gradient(farthest-side circle at 300px 100px, #f3d3c6 40%, #3cdbc0 100%) !important; /* Standard syntax */
  	background-attachment: fixed !important;
}

/* White sunburst behind B logo */

#page-header {
    background: rgba(255,255,255,1) none;
	background: radial-gradient(circle closest-corner at 125px 105px, rgba(255,255,255,1 ) 10%, rgba(255,255,255,0 ) 75%) !important; /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */

	background: -webkit-radial-gradient(circle closest-corner at 125px 105px, rgba(255,255,255,1 ) 10%, rgba(255,255,255,0 ) 75%) !important; /* Safari */
	background: -o-radial-gradient(circle closest-corner at 125px 105px, rgba(255,255,255,1 ) 10%, rgba(255,255,255,0 ) 75%) !important; /* Opera 11.6 to 12.0 
*/
	background: -moz-radial-gradient(circle closest-corner at 125px 105px, rgba(255,255,255,1 ) 10%, rgba(255,255,255,0 ) 75%) !important; /* Firefox 3.6 to 15 */
	background: -ms-radial-gradient(circle closest-corner at 125px 105px, rgba(255,255,255,1 ) 10%, rgba(255,255,255,0 ) 75%) !important; /* ie10+ */
	background: radial-gradient(circle closest-corner at 125px 105px, rgba(255,255,255,1 ) 10%, rgba(255,255,255,0 ) 75%) !important; /* Standard syntax */
    
    
}

.forumbg {
    background-color: inherit !important;
  }

/* restore uppercases */

  body * {
    text-transform: none !important;
  }


/* headings color and botom-border*/

h1, h1 a, h2, h2 a, h3, h3 a, li.header dt, li.header dd {
    color: #235c9f !important;
  }

.content h2, .panel h2, h3 {
    border-bottom: 1px solid #f95775;
  }

li.header dt, li.header dd {
	font-weight: bold !important;
  }


/* normal links */

a:hover {
    color: #3cdbc0;
  }

/* links in posts */  

.postlink {
	    border-bottom-color: #75ab7a;
}
.postlink:visited {
		border-bottom-color: #75ab7a;
}
  
/*  -----> newly added darker titles*/
a.topictitle, a.forumtitle {
    color: #303030;}

a:hover.topictitle, a:hover.forumtitle {
    color: #235c9f;}


/* --- navigation ---- */

/* navigantion bar sperators and >>, # */
.navbar, .bullet {
    color: #f95775 !important;
  }

/* page numbers */
.pagination span strong {
    color: #FFFFFF !important;
    background-color: #808080 !important;
    border-color: #c5a2ba !important;
  }

.pagination span a, .pagination span a:link, .pagination span a:visited, .pagination span a:active {
    background-color: #d8f6f8 !important;
  }

.pagination span a:hover, .row .pagination span a:hover, li.pagination span a:hover {
    border-color: #c5a2ba !important;
    background-color: #f95775 !important;
    color: #333333 !important;    
  }


/* forum posts overview */

li.row {
	border-bottom: 2px solid #f95775 !important;
  }

li.row:hover {
    background: #d8f6f8 !important;
    background: #ffffff44 !important;
  }

ul.topiclist dd {
    border-left-color: #c5a2ba !important
  }


/* ---- posts and other pages ---- */

/* semi-transparant background*/

.post, .panel {
   	background-color: #d8f6f8 !important;
    background-color: rgba(255, 255, 255, 0.2) !important;
    border-bottom: 2px solid #c5a2ba !important;
  }

.postprofile {
    border-left-color: #c5a2ba !important;
  }

/* quote in post */
blockquote {
	background: #00000004 !important;
    border: 1px solid #5b9eab !important;
  }




/* ---- user control panel --- */

/* border */
#cp-main h3, #cp-main hr, #cp-menu hr {
	border-color: #f95775 !important;
  }

#cp-main span {
	 background-image: none !important;
  }

/* tabs */

#tabs a {
	background-image: none !important;
    background-color: inherit !important;
  }

#tabs a span, #navigation a {
    background-image: none !important;
    background-color: #c5a2ba !important;
    color: #606060 !important;
  }

#tabs a:hover span, #navigation a:hover {
    background-color: #a9e5ff !important;
    color: #235c9f !important;
  }

#tabs .activetab a span, #navigation #active-subsection a {
    background-color: #a9e5ff !important;
    color: #333333 !important;
  }



/* --- search ---- */
a.button1:hover, input.button1:hover, a.button2:hover, input.button2:hover, input.button3:hover {
    border-color: #fdb6c8 !important;
    color: #fdb6c8 !important;
  }


/* ---  poll bar ----*/
.pollbar1, .pollbar2 {
    background-color: #a9e5ff;
    border-bottom-color: #c5a2ba;
    border-right-color: #c5a2ba;
    border-color: #c5a2ba;
  }

fieldset.polls dd div {
    color: #666666;
}


/* ------------------------------------------- */

/* forum icons */

/* normal */
  dt[title^="Unread"] {
	background-image:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABsAAAAbCAYAAACN1PRVAAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAfklEQVRIx2NgGAV0BApA3ADE54H4Pxo+D5VToIZFDVgswIUbyLVEAIdPCOHzUL0kAXIsQraQJkFHUZAqUMEiGCaYaPqpaFk/LeOK5Lj7T2U8ahntLaNrAqFr0qdrpqZrcUX3gpjuVQw5QdpPjdpaAWrQfRw+6adWs2AUDC4AAIRcQY3rBhVAAAAAAElFTkSuQmCC) !important
  }
  dt[title^="No unread"] {
	background-image:url(data:image/png;base64,) !important
  }

/* announcement */
.global-announce>dl>dt[title^="Unread"], .announce>dl>dt[title^="Unread"] {
	background-image:url(data:image/png;base64,) !important
  }
  .global-announce>dl>dt[title^="No unread"], .announce>dl>dt[title^="No unread"] {
	background-image:url(data:image/png;base64,) !important
  }

/* sticky */
  .sticky>dl>dt[title^="Unread"] {
	background-image:url(data:image/png;base64,) !important
  }
  .sticky>dl>dt[title^="No unread"] {
	background-image:url(data:image/png;base64,) !important
  }

/* locked */
dt[title^="This topic is locked"], dt[title="Forum locked"] {
	background-image:url(data:image/png;base64,) !important
  }

  dt[title^="Moved"] {
	background-image:url(data:image/png;base64,) !important
  }




/* ------ other images ------- */

/* quote */
  .quote-icon { background: none !important }
  .quote-icon a {
background-image:url(data:image/png;base64,) !important
  }
  .quote-icon a:hover { background-position: 0px 20px !important; }

/* report */
  .report-icon { background: none !important }
  .report-icon a {
background-image:url(data:image/png;base64,) !important
  }
  .report-icon a:hover { background-position: 0px 20px !important; }

/* pm */
  .pm-icon { background: none !important }
  .pm-icon a {
background-image:url(data:image/png;base64,) !important
  }
  .pm-icon a:hover { background-position: 0px 20px !important; }
  .pm-icon{ width: 28px !important }

/* web*/
  .web-icon { background: none !important }
  .web-icon a {
background-image:url(data:image/png;base64,) !important
  }
  .web-icon a:hover { background-position: 0px 20px !important; }




/* back to top */
  a.top, a.top2 {
/*background-image:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAsAAAALCAYAAACprHcmAAAAVklEQVQokWNgQAVzgPg3EP+H0gsYcID/eDBehXNwaZiCJrgVKr4VTXwKNlPx2YjCaUFT3IJPMSGPwz1zDYhNgdgKimHsa0iexuo2koOPkPMwwncOsiQAeqFLXv+MxDgAAAAASUVORK5CYII=) !important*/
background-image:url(data:image/png;base64,)
  }
  a.top {
    height: 15px !important;
    width: 15px !important;
    margin: -5px 0 !important;
    color: transparent !important
  }

/* latest post */
  .icon_topic_newest, .icon_topic_latest {
    display: inline-block !important;
    height: 9px !important;
    width: 12px !important;
    overflow: hidden;
    color: transparent !important;
    background-color: transparent !important;
    background:url(data:image/gif;base64,R0lGODlhCwAJAKIFAAAAAP///2NjY+bm5mdnZ////wAAAAAAACH5BAEAAAUALAAAAAALAAkAAAMdWAXc+iBICd6a1EZc4bResCjdJQYCAw0EqbpVUyUAOw==)
  }
.icon_topic_latest { width:8px !important }

/* post */
  span[title="Post"], span[title="Unread post"] {
    display: inline-block !important;
    height: 9px !important;
    width: 12px !important;
    overflow: hidden;
    color: transparent !important;
    background-color: transparent !important;
    background:url(data:image/gif;base64,R0lGODlhCwAJAJECAP///wAAAP///wAAACH5BAEAAAIALAAAAAALAAkAAAIWlIJoe+DKggMpzmdvyHMvqXHYl1RCAQA7);
  }


/* attachment */
  img[alt="Attachment(s)"] {
    height: 0px !important;
    width: 0px !important;
    padding-left: 7px !important;
    padding-top: 10px !important;
background:url(data:image/gif;base64,R0lGODlhBwAKAKIEAP///zMzM2ZmZpmZmf///wAAAAAAAAAAACH5BAEAAAQALAAAAAAHAAoAAAMXSBICpK294KgF49IFLtfN9RCbJJgB4yQAOw==)
  }
}
