css:
@-moz-document domain("inoreader.com") {
body html {
    height: 100vh!important
}
    
 /*block AD begin*/   
.block_article_ad,
.ad_title,
.ad_title_centered, 
.ad_title.ad_title_centered,
.avs_iframe,
.sinner_inner,
.ad_footer_remove,
#sinner_container,
#all_gad_6125,
#leaderboard_ad-5a5c4d061b4e8,
.block_article_ad.block_article_ad_bottom.leaderboard_ad.ad_size_large_rectangle.sinner
  {
    display: none !important;
  }

  .reader_pane_sinner
  {
    padding-right: 0 !important;
  }
    
 /*block AD end*/   
    
    
    
    
.article_tile {
    margin: 2px .001% 0
}
.article_tile_picture {
    height: 100%;
    width: 100%;
}    

.article_tile .article_stripe {
    top: 95%;
}    

    
@media screen and (min-width:480px) {
    div.article_tile {
        max-width: 32%;
        width: 100%;
        height: 47vh
    }
    .article_tile_content_wraper {
        height: 47vh
    }
}

@media screen and (min-width:1600px) {
    div.article_tile {
        max-width: 32%;
        width: 100%;
        height: 48vh
    }
    .article_tile_content_wraper {
        height: 48vh
    }
}
@media screen and (min-width:1900px) {
    div.article_tile {
        max-width: 32%;
        width: 100vw;
        height: 48vh
    }
    .article_tile_content_wraper {
        height: 48vh
    }
}
/*    
.article_content {
    max-width: 80vw!important
}*/
    

.pin_container>img,    
iframe,
video {
    width: 65vw!important;
    height: 80vh!important;
    max-width: none!important;
    object-fit: contain!important
}
    
.inno_dialog_content {
    overflow: auto;
    position: relative;
    padding: 0px 35px 35px !important;
    outline: none !important;
}
#article_dialog_wrapper{
    width: 80% !important;
    top: 0px !important;
    left: 10% !important;
    }
    
}
