css:
@-moz-document domain("jenkins-dev.hypers.cc") {
/** Reset */
html,
body, table, form, input, td, th, p, textarea, select {
	font-family: Whitney, Helvetica Neue, Helvetica, Arial, sans-serif;
}
body#jenkins, table, form, td, th, p {
	color: hsla(0, 0%, 100%, .8);
}
a,
a:link,
a:hover,
a:visited {
	color: #00b0f4;
	text-decoration: none;
	outline: none;
}
a:hover {
	text-decoration: underline;
}
.yui-button button {
	align-items: center;
	background: none;
	border-radius: 3px;
	box-sizing: border-box;
	display: flex;
	font-size: 14px;
	font-weight: 500;
	justify-content: center;
	line-height: 16px;
	padding: 2px 16px;
	position: relative;
}
.yui-button button,
.yui-button button:hover,
.yui-button button:active {
	user-select: none !important;
	cursor: pointer !important;
	outline: none !important;
	box-shadow: none !important;
	border: none !important;
}
#jenkins .yui-button.primary button {
	transition: background-color .17s ease;
	background-color: #7289da;
	color: #fff;
}
#jenkins .yui-button.primary button:hover {
	background-color: #677bc4;
}
#jenkins .yui-button.primary button:active {
	background-color: #5b6eae;
}

.icon-rss,
img[alt=Feed] {
	border-radius: 4px;
}
}

@-moz-document domain("jenkins-dev.hypers.cc") {
/** Layout */
body#jenkins {
	display: flex;
	flex-direction: column;
	width: 100vw;
	height: 100vh;
	margin: 0;
	padding: 0;
	overflow: hidden;
}

#page-head,
#page-body,
footer {
	position: initial;
}

/** Header */
#search-box {
	background-color: #202225;
	color: #b9bbbe;
	border: none;
	box-shadow: none;
}
#search-box + a {
	display: none;
}

/** Main */
#page-body {
	flex: 1;
	display: flex;
	flex-direction: row;
	background-color: #36393e;
}
#main-panel {
	flex: 1;
	margin-left: 0 !important;
}

/** Footer */


}

@-moz-document domain("jenkins-dev.hypers.cc") {
/* 面包屑改标签页 */

#breadcrumbBar,
.top-sticker-inner {
	background: #202225;
	border: none;
	color: hsla(0, 0%, 100%, .3);
}
#breadcrumbBar > div.top-sticker {
	top: 40px;
}
#breadcrumbBar #breadcrumbs {
	border-bottom: none;
}
#breadcrumbs > li.children {
	display: none;
}
#menuSelector {
	display: none;
}
#breadcrumbs > li:hover {
	background-color: #202225 !important;
}
#breadcrumbs a.breadcrumbBarAnchor.inside {
	padding-right: .75em;
}
#breadcrumbs a.breadcrumbBarAnchor.mouseIsOverMenuSelector {
	background: none;
}
#breadcrumbs > li > a:hover {
	color: #f6f6f7 !important;
}
#breadcrumbs > li:not(:nth-last-child(2)) {
	border-left: 1px solid rgba(0, 0, 0, .3);
}
#breadcrumbs > li:nth-last-child(2),
#breadcrumbs > li:nth-last-child(2):hover {
	background-color: #36393e !important;
}
#breadcrumbs > li:nth-last-child(2) > a,
#breadcrumbs > li:nth-last-child(2) > a:link {
	color: #f6f6f7 !important;
}
.yuimenu .bd {
	background: #282b30 !important;
	box-shadow: 0 0 1px rgba(0, 0, 0, .82), 0 1px 4px rgba(0, 0, 0, .1) !important;
	border: none !important;
}
.yuimenuitem {
	border-radius: 5px;
	box-sizing: border-box;
	font-size: 13px;
	font-weight: 500;
	line-height: 16px;
	margin: 2px 0;
	overflow: hidden;
	padding: 6px 9px;
	text-overflow: ellipsis;
	white-space: nowrap;
	width: 100%;
}
.yuimenuitem.yuimenuitem-selected {
	background: #25282d !important;
	color: #fff !important;
}
.yuimenuitemlabel {
	color: hsla(0, 0%, 100%, .6) !important;
}
}

@-moz-document domain("jenkins-dev.hypers.cc") {
/* 表格 */
#description-link {
	text-transform: capitalize;
}

.pane-frame {
	border-color: rgba(32, 34, 37, .3);
}
.pane-frame:hover,
.pane-frame.mouseover {
	border-color: rgba(32, 34, 37, .3);
}
.pane-frame .pane tr:not(.header) {
	background: transparent !important;
}

.pane-frame table.stripped tr.header:hover,
.pane-frame table.stripped-even tr.header:hover,
.pane-frame table.stripped-odd tr.header:hover {
	background: rgba(32, 34, 37, .3) !important;
}
.pane-frame table.stripped tr:not(.header):hover,
.pane-frame table.stripped-even tr:not(.header):hover,
.pane-frame table.stripped-odd tr:not(.header):hover {
	background: rgba(79, 84, 92, .6) !important;
}
.bigtable th {
	background: rgba(32, 34, 37, .3);
	color: #fff;
	padding: 15px 8px 15px 15px;
	border: none;
}
table.sortable a.sortheader,
table.sortable span.sortarrow {
	color: #fff;
}

.tabBarFrame .tabBar {
	-ms-flex-align: center;
    -webkit-box-align: center;
    align-items: center;
    background: #4f545c;
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    font-weight: 600;
    height: 36px;
    margin: 20px 1px 0;
    padding-left: 8px;
}
.tabBar .tab:first-child {
	margin-left: 0;
}
.tabBar .tab a {
	color: hsla(0, 0%, 100%,.8) !important;
	background: none !important;
	border: none !important;
	font-size: 16px;
}
.tabBar [type=radio]:checked ~ a {
	cursor: default;
}
.tabBar .tab a.addTab {
	visibility: hidden;
}
.tabBar:hover .tab a.addTab {
	visibility: visible;
}

.pane-frame table.pane tr.header {
	background: #35383c;
}

table.bigtable th,
table.bigtable td {
	border: 1px solid #282b30;
	padding: 8px;
}
table.bigtable tr:not(.header) td {
	color: hsla(0,0%,100%,.5);
}

#projectstatus th {
	padding: 8px;
}
.healthReport div.healthReportDetails {
	display: none !important;
}
}

@-moz-document domain("jenkins-dev.hypers.cc") {
#side-panel {
	display: flex;
	flex-direction: column;
	
	color: #fff;
	border-right: 1px solid #2f3136;
	padding: 0 15px 0 15px;
	margin: 15px 0 40px 0;
	
	overflow-y: auto;
}

#side-panel::-webkit-scrollbar {
    width: 4px;
	visibility: hidden;
}
#side-panel:hover::-webkit-scrollbar {
	visibility: visible;
}
#side-panel::-webkit-scrollbar-thumb {
    background-clip: padding-box;
	border-radius: 8px;
	border: 2px solid rgba(0, 0, 0, .3);
    background-color: rgba(0, 0, 0, .3);
}

#tasks > .task {
	cursor: pointer;
	display: flex;
	flex-direction: row;
	align-items: center;
	font-size: 0;
	border-radius: 4px;
}
#tasks > .task:hover {
	background-color: #292b2f;
}
#tasks > .task > a {
	font-size: 14px;
	display: block;
}
#tasks > .task > .task-icon-link {
	padding: 8px 0 8px 8px;
}
#tasks > .task > .task-link {
	flex: 1;
	padding: 8px 8px 8px 0;
	color: #b9bbbe;
	font-weight: 400;
}
#tasks > .task > .task-link::before {
	content: ' ';
	white-space: pre;
}
#tasks > .task > .task-link:hover {
	text-decoration: none;
}
#tasks > .task:hover > .task-link,
#tasks > .task .task-link > b{
	color: #ffffff;
}
#buildHistoryPage {
	flex: 1;
	overflow: hidden;
}
#buildHistoryPageNav {
    right: auto;
    top: auto;
    left: 15px;
    bottom: 5px;
	border: none;
    display: flex;
    z-index: 2;
}
#buildHistoryPageNav .nav {
    transform: rotate(-90deg);
}
#buildHistory {
	height: 100%;
	display: flex;
	flex-direction: column;
}
#buildHistory .pane-header {
	text-transform: capitalize;
}
#buildHistory .pane-content {
	flex: 1;
	overflow: hidden;
	overflow-y: auto;
}
#buildHistory .pane-footer {
	background-color: rgba(32, 34, 37, .7);
	border-top: 1px solid #4f545c;
}
#side-panel .pane-frame {
	border: none !important;
}
#side-panel .pane-frame .pane-header .collapse {
	display: none;
}
#side-panel .pane-frame .pane .build-search-row:hover,
#side-panel .pane-frame .pane .build-search-row tr:hover{
	background: none !important;
}
#side-panel .pane-frame .pane .build-search-row table tr {
	box-shadow: none;
}
#side-panel .pane-frame .pane .build-search-row table td {
	-ms-flex: 1;
    -ms-flex-direction: row;
    -ms-flex-wrap: wrap;
    -webkit-box-direction: normal;
    -webkit-box-flex: 1;
    -webkit-box-orient: horizontal;
    background: rgba(0,0,0,.2);
    border: 1px solid rgba(0,0,0,.1);
    border-radius: 4px;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    flex: 1;
    flex-direction: row;
    flex-wrap: wrap;
    padding: 2px;
    position: relative;
}
#side-panel .pane-frame .pane .build-search-row .clear {
	width: 18px;
	height: 18px;
	border-radius: 50%;
	border: none;
	background-color: #d3d3d3;
	text-align: center;
	line-height: 18px;
	padding: 0 !important;
}
#side-panel .pane-frame .pane .build-search-row .clear:hover {
	opacity: 1;
}
#side-panel .pane-frame .pane .build-search-row input {
    -ms-flex: 1;
    -webkit-box-flex: 1;
    -webkit-box-sizing: border-box;
    background: transparent;
    border: none;
    box-sizing: border-box;
    color: #fff;
    flex: 1;
    font-family: Whitney,Helvetica Neue,Helvetica,Arial,sans-serif;
    font-size: 12px;
    height: 26px;
    min-width: 50px!important;
    outline: none;
    padding: 5px 7px;
    resize: none;
}

#side-panel .pane-frame .pane .build-row {
	box-shadow: inset 0 -1px 0 0 #4f545c;
}
#side-panel .pane-frame .pane .build-row:last-child {
	display: none;
}

#side-panel .pane-frame .pane .build-row-cell {
	position: relative;
	z-index: 0;
}
#side-panel .pane-frame .pane .single-line .build-row-cell {
	display: flex;
	flex-direction: row;
	align-items: center;
	height: 50px;
}
#side-panel .pane-frame .pane .build-row-cell > div {
	display: block;
	width: auto;
}

#side-panel .pane-frame .pane .build-row-cell > .build-name,
#side-panel .pane-frame .pane .build-row-cell > .build-details,
#side-panel .pane-frame .pane .build-row-cell > .build-controls {
	display: flex;
	align-items: center;
}

#side-panel .pane-frame .pane .build-row-cell > .build-name {
	flex-grow: 1;
}
#side-panel .pane-frame .pane .build-row-cell .build-icon {
	position: static;
	margin-right: 1em;
}
#side-panel .pane-frame .pane .build-row-cell .build-link {
	line-height: 20px;
	color: #f6f6f7;
	margin-left: 0;
}

#side-panel .pane-frame .pane .build-row-cell .progress-bar {
	position: absolute;
	z-index: -1;
	top: 0;
	left: 0;
	width: 100%;
	border: none;
	opacity: .5;
}


#side-panel .pane-frame .pane .build-row-cell .progress-bar td {
	padding: 25px 4px;
}

#side-panel .pane-frame .pane .build-row-cell .progress-bar td.progress-bar-left {
	background: none;
}

td.progress-bar-done {
	background-color: green;
}

#buildQueue,
#executors {
	width: 100%;
}

div.pane-header {
	color: hsla(0,0%,100%,.8);
	font-size: 16px;
	font-weight: 600;
	height: 36px;
	padding-left: 8px;
	background-color: #4f545c;
	border: none;
	border-top-left-radius: 3px;
	border-top-right-radius: 3px;
}

.pane-content {
	padding: .5em;
	color: #87909c;
	font-size: 14px;
	border: 1px solid #282b30;
	background-color: rgba(32, 34, 37, .7);
	border-bottom-left-radius: 3px;
	border-bottom-right-radius: 3px;
}
}

@-moz-document domain("jenkins-dev.hypers.cc") {
#main-panel {
	background-color: #36393e;
	color: #fff;
	overflow-y: auto;
}

#main-panel::-webkit-scrollbar {
    width: 15px;
}

#main-panel::-webkit-scrollbar-thumb,
#main-panel::-webkit-scrollbar-track {
    background-clip: padding-box;
    border-radius: 7.5px;
    border-style: solid;
    border-width: 3px;
	border-color: #36393e;
}
#main-panel::-webkit-scrollbar-thumb {
    background-color: rgba(0, 0, 0, .3);
}

#main-panel::-webkit-scrollbar-track {
    background-color: rgba(0, 0, 0, .1);
}
.ygtvlabel, 
.ygtvlabel:link, 
.ygtvlabel:visited, 
.ygtvlabel:hover,
.ygtvfocus,
.ygtvfocus .ygtvlabel, 
.ygtvfocus .ygtvlabel:link, 
.ygtvfocus .ygtvlabel:visited, 
.ygtvfocus .ygtvlabel:hover{
	background: none;
}
}

@-moz-document domain("jenkins-dev.hypers.cc") {
footer {
	background: #202225;
	border: none;
	color: hsla(0, 0%, 100%, .3);
}
}

@-moz-document regexp("^https?://jenkins-dev\\.hypers\\.cc(/job/[^/]+)+(/.*)?") {
/* 应用于“立即构建”按钮 */
#tasks > .task > a[onclick^="return build_"] {
	background-color: #7289da;
	transition: background-color .17s ease;
}
#tasks > .task > a[onclick^="return build_"].task-icon-link {
	border-radius: 4px 0 0 4px;
}
#tasks > .task > a[onclick^="return build_"].task-link {
	color: #fff;
	opacity: 1;
	border-radius: 0 4px 4px 0;
	height: 44px;
	line-height: 44px;
	padding-top: 0;
	padding-bottom: 0;
}
#tasks > .task:hover > a[onclick^="return build_"] {
	background-color: #677bc4;
}
#tasks > .task:active > a[onclick^="return build_"] {
	background-color: #5b6eae;
}

/** 应用于删除按钮 */
#tasks > .task > a[href*="delete"],
#tasks > .task > a[href*="Delete"],
#tasks > .task > a[onclick*="delete"],
#tasks > .task > a[onclick*="Delete"] {
	color: rgb(240, 71, 71);
}
#tasks > .task > a[href*="delete"].task-icon-link,
#tasks > .task > a[href*="Delete"].task-icon-link,
#tasks > .task > a[onclick*="delete"].task-icon-link,
#tasks > .task > a[onclick*="Delete"].task-icon-link  {
	border-radius: 4px 0 0 4px;
}
#tasks > .task > a[href*="delete"].task-link,
#tasks > .task > a[href*="Delete"].task-link,
#tasks > .task > a[onclick*="delete"].task-link,
#tasks > .task > a[onclick*="Delete"].task-link {
	border-radius: 0 4px 4px 0;
	height: 44px;
	line-height: 44px;
	padding-top: 0;
	padding-bottom: 0;
}

#tasks > .task:hover > a[href*="delete"],
#tasks > .task:hover > a[href*="Delete"],
#tasks > .task:hover > a[onclick*="delete"],
#tasks > .task:hover > a[onclick*="Delete"] {
	color: rgb(240, 71, 71);
	background-color: rgba(240, 71, 71, .1);
}
#tasks > .task:active > a[href*="delete"],
#tasks > .task:active > a[href*="Delete"],
#tasks > .task:active > a[onclick*="delete"],
#tasks > .task:active > a[onclick*="Delete"] {
	color: #fff;
	background-color: rgb(240, 71, 71);
}

}

@-moz-document regexp("^https?://jenkins-dev\\.hypers\\.cc(/job/[^/]+)+/configure$") {
/** 配置页 */
#main-panel {
	padding: 0;
	display: flex;
}
#main-panel > .container {
	flex: 1;
	margin: 0;
	padding: 0;
}
#main-panel > .container > .row {
	height: 100%;
	margin: 0;
	padding: 0;
}
/** like main body */
#main-panel > .container > .row > div {
	display: flex;
	height: 100%;
	margin: 0 auto;
	padding: 0;
	width: 100%;
	max-width: 1366px;
	float: none;
}

/** like sidebar */
.jenkins-config-widgets {
	display: flex;
	flex-direction: column;
	
	color: #fff;
	border: none;
	background: transparent;
	border-right: 1px solid #2f3136;
	padding: 0 15px 0 15px;
	margin: 15px 0 40px 0;
	width: 218px;
}
.form-config {
	display: flex;
	flex-direction: column;
}
.jenkins-config-widgets .form-config.tabBarFrame {
	border: none;
}
.jenkins-config-widgets .form-config.tabBarFrame .config-section-activators {
	display: flex;
	flex-direction: column;
	height: auto;
	background: transparent;
}
/** like .task */
.jenkins-config-widgets .form-config.tabBarFrame .tabBar .tab {
	cursor: pointer;
	display: flex;
	flex-direction: row;
	align-items: center;
	width: 100%;
	border: none;
    border-radius: 3px;
	margin: 0 0 2px 0;
    padding-bottom: 6px;
    padding-top: 6px;
	
}
.jenkins-config-widgets .form-config.tabBarFrame .tabBar .tab.active {
	background-color: #7289da;
    color: #fff;
	border: none;
}
.jenkins-config-widgets .form-config.tabBarFrame .tabBar .tab:not(.active):hover {
	background-color: hsla(216,4%,74%,.1);
    color: #f6f6f7;
}

/** like main-panel */
#jenkins form.jenkins-config {
	flex: 1;
	background: transparent;
	border: none;
	padding: 15px 115px 40px 15px;
	overflow: hidden;
	overflow-y: auto;
}
#jenkins form.jenkins-config::-webkit-scrollbar {
    width: 15px;
}
#jenkins form.jenkins-config::-webkit-scrollbar-thumb,
#jenkins form.jenkins-config::-webkit-scrollbar-track {
    background-clip: padding-box;
    border-radius: 7.5px;
    border-style: solid;
    border-width: 3px;
	border-color: #36393e;
}
#jenkins form.jenkins-config::-webkit-scrollbar-thumb {
    background-color: rgba(0, 0, 0, .3);
}
#jenkins form.jenkins-config::-webkit-scrollbar-track {
    background-color: rgba(0, 0, 0, .1);
}
/** form */
.setting-name {
	font-weight: bold;
	color: #b9bbbe;
	letter-spacing: .5px;
}
.config_general[nameref^="rowSetStart"]:not(.row-group-start):not(.row-group-end):not(.rowvg-start):not(.section-header-row):not(.validation-error-area):not(.stapler-class-bag):not(.help-area):not(.dropdownList-container) {
	margin-top: 40px;
}
#jenkins form.jenkins-config input,
#jenkins form.jenkins-config textarea,
#jenkins form.jenkins-config select,
#jenkins form.jenkins-config table td select:not(.select-ajax-pending),
#jenkins .jenkins-config table td select {
	background-color: rgba(0, 0, 0, .1);
	border-color: rgba(0, 0, 0, .3);
	box-shadow: none;
	color: #f6f6f7;
	outline: none;
    transition: background-color .15s ease,border .15s ease;
	resize: none;
}
#jenkins .jenkins-config table td .yui-button button, 
#jenkins .jenkins-config table td input[type='email'].setting-input, 
#jenkins .jenkins-config table td input[type='password'].setting-input, 
#jenkins .jenkins-config table td input[type='text'].setting-input,
#jenkins .jenkins-config table td select,
#jenkins .jenkins-config table td table td select:not(.select-ajax-pending),
#jenkins form.jenkins-config textarea {
	height: 40px;
    padding: 10px;
	font-size: 16px;
}


#jenkins .jenkins-config table td select,
#jenkins .jenkins-config table td table td select:not(.select-ajax-pending) {
	padding: 0 10px;
}

#jenkins form.jenkins-config input:hover,
#jenkins form.jenkins-config textarea:hover {
	border-color: #040405;
}
#jenkins form.jenkins-config input:focus,
#jenkins form.jenkins-config textarea:focus {
	border-color: #7289da;
}
#jenkins form.jenkins-config textarea {
	border-bottom: none;
	border-bottom-left-radius: 0;
	border-bottom-right-radius: 0;
}
#jenkins form.jenkins-config textarea + .textarea-handle {
	background-color: rgba(0, 0, 0, .3);
    border: 1px solid rgba(0, 0, 0, .3);
	border-top: none;
	border-bottom-left-radius: 3px;
	border-bottom-right-radius: 3px;
    transition: background-color .15s ease,border .15s ease;
}
#jenkins form.jenkins-config textarea:hover + .textarea-handle {
	border-color: #040405;
}
#jenkins form.jenkins-config textarea:focus + .textarea-handle {
	border-color: #7289da;
}
#jenkins form.jenkins-config tbody {
	display: flex;
	flex-direction: column;
}
#jenkins form.jenkins-config tbody > tr {
	display: flex;
}
#jenkins .jenkins-config table tr td.setting-leftspace {
	height: 0;
	padding: 0;
}
#jenkins .jenkins-config table tr td + td.setting-help,
#jenkins .jenkins-config table tr td + td.setting-no-help {
	display: none;
}
/** general rows */
.config-table-top-row.config_general {
	flex-direction: column;
}

/** checkbox rows */
#jenkins .jenkins-config table .hetero-list-container,
#jenkins .jenkins-config table .repeated-container,
#jenkins .jenkins-config table tr.optional-block-start,
#jenkins .jenkins-config table tr.radio-block-start {
	border: none;
}

/**#jenkins .jenkins-config table tr.optional-block-start ~ tr:not(.optional-block-end):not(.optional-block-start)[nameref^="rowSetStart"],*/
#jenkins .jenkins-config table .repeated-chunk,
#jenkins .jenkins-config table tr[nameref^="rowSetStart"]:not(.optional-block-start):not(.optional-block-end), 
#jenkins .jenkins-config table tr[nameref^="cb"], 
#jenkins .jenkins-config table tr[nameref^="radio-block"] {
	border-left: 1px solid #7289da;
	border-right: 1px solid #7289da;
}

#jenkins .jenkins-config table tr[nameref^="cb"] {
	padding-left: 15px;
	padding-right: 16px;
}

#jenkins .jenkins-config table tr.optional-block-end {
	margin-bottom: 8px;
}
#jenkins .jenkins-config table tr:not([style*="display: none;"]) + .optional-block-end {
	padding-bottom: 12px;
	height: 3px;
	border-radius: 0 0 3px 3px;
	border-bottom:  1px solid #7289da;
	border-left:  1px solid #7289da;
	border-right:  1px solid #7289da;
}
/** no panel */
#jenkins .jenkins-config table tr.rowvg-start + .optional-block-end {
	background-color:  #7289da;
}
/** panel first line */
#jenkins .jenkins-config table tr.rowvg-start + tr:not(.optional-block-end) {
	margin-top: 0;
	padding-top: 12px;
	border-top: 3px solid #7289da;
}
#jenkins .jenkins-config table tr.optional-block-start > td {
	flex: 1;
	padding: 0;
}

#jenkins .jenkins-config table tr.optional-block-start > td > input[type="checkbox"] {
	display: none;
}
#jenkins .jenkins-config table tr.optional-block-start > td > input + label {
	display: flex;
	margin: 0;
	border-radius: 3px;
	align-items: center;
	cursor: pointer;
	background-color: rgba(0, 0, 0, .1);
	border: 1px solid rgba(0, 0, 0, .3);
	padding: 10px;
	font-size: 16px;
}
#jenkins .jenkins-config table tr.optional-block-start > td > input + label:hover {
	border-color: #040405;
}
#jenkins .jenkins-config table tr.optional-block-start > td > input:checked + label {
	border-color: rgb(114, 137, 218);
    background-color: rgb(114, 137, 218);
	
	border-bottom-left-radius: 0;
	border-bottom-right-radius: 0;
	padding-bottom: 7px;
}
#jenkins .jenkins-config table tr.optional-block-start > td > input + label::before {
	content: '✓';
	width: 24px;
	height: 24px;
	border-radius: 3px;
	border: 1px solid #72767d;
    font-weight: bold;
	line-height: 24px;
	text-align: center;
	margin-right: 8px;
	color: transparent;
}
#jenkins .jenkins-config table tr.optional-block-start > td > input + label:hover::before {
	color: #72767d;
}
#jenkins .jenkins-config table tr.optional-block-start > td > input:checked + label::before {
	background: #fff;
	border-color: #fff;
	color: #7289da;
	font-size: 1.2em;
}

#jenkins .jenkins-config table .bottom-sticker-inner {
	position: fixed;
	right: 0;
	top: 66px;
	background: transparent;
	border: none;
	padding: 10px;
	display: flex;
	flex-direction: column;
}

#jenkins .jenkins-config table.config-table .section-header-row > td {
	width: 100%;
}

#jenkins .jenkins-config table.config-table .section-header {
	border-top: none;
	margin-top: 60px;
}

#jenkins .config-table td .yui-button:not([class*="delete"]) button {
	background-color: #7289da;
	color: #ffffff;
	padding: 2px 16px;
	height: 38px;
	font-weight: normal;
	font-size: 14px;
	min-width: 96px;
	min-height: 38px;
}

.config_general textarea {
	min-height: 80px;
}
}

@-moz-document regexp("^https?://jenkins-dev\\.hypers\\.cc(/job/[^/]+)+/\\d+/console(Full)?$") {
/** 控制台输出页 */

#main-panel {
	display: flex;
	flex-direction: column;
	overflow: hidden;
}

.console-output {
	flex-grow: 1;
	overflow-y: auto;
	background-color: black;
	color: green;
	padding: 1em;
}

.console-output::-webkit-scrollbar {
    width: 15px;
}
.console-output::-webkit-scrollbar-thumb,
.console-output::-webkit-scrollbar-track {
    background-clip: padding-box;
    border-radius: 7.5px;
    border-style: solid;
    border-width: 3px;
	border-color: black;
}
.console-output::-webkit-scrollbar-thumb {
    background-color: rgba(255, 255, 255, .3);
}
.console-output::-webkit-scrollbar-track {
    background-color: rgba(255, 255, 255, .1);
}
.console-output + #spinner {
	background-color: black;
	padding: 0 1em 1em;
}
}

