css:
@-moz-document domain("coins.su"),
          url-prefix("http://coins.su/")  {
#elSearch {
	position: fixed;
	top: 44px;
	opacity: 1;
	z-index: 9000;
	border: 2px solid #525252;
	height: 30px;
	left: 5px;
	visibility: visible;
}
.closemy:hover:before, .closemy:hover:after, .expand:hover:before, .expand:hover:after {
    background: white;
}
.closemy,.expand {
transition: background 1s;
}
.custom-bn-block-top,
.custom-header,
.custom-bn-block-bottom-wrapper {
	visibility: hidden;
	height: 0;
}
.ipsNavBar_primary:after {
	z-index: 0;
	padding-bottom: 0;
	visibility: hidden;
}
#may {
	z-index: 1999;
}

#ipsLayout_header>.ipsLayout_container {
	height: 0;
	overflow: hidden;
}
#elUserNav {
	position: fixed;
	padding: 38px 20px 2px 0;
	width: 100%;
	text-align: right;
	background: #6282a2;
	z-index: 1900;
	margin-right: -20px;
	top: 0px  !important;
	padding-right: 140px;
}
#elUserNav:before{
content:"Центральный форум нумизматов СССР";
	    position: absolute;
    color: #bbc;
    font-size: 18px;
    top: 0;
    right: 20px;
}
.ipsLayout_container>.ipsNavBar_primary {
	padding: 5px 5px 0px 5px;
}
.ipsUserPhoto_large img,
img.ipsUserPhoto_large,
.ipsUserPhoto_large:after {
	width: 40px !important;
	height: 40px !important;
}
.ipsResponsive_hidePhone,
.cAuthorPane_photo {
	display: inline-block;
}
.ipsResponsive_hidePhone>img {
	height: 40px;
}
.cAuthorPane_info .ipsType_break {
	display: block;
}
.ipsType_richText img.ipsImage {
	height: 100px  !important;
	width: auto  !important;
}
.ipsBreadcrumb_top li a span,
.ipsBreadcrumb_top>ul>li {
	font-size: 14px;
	color: #333;
}
.ipsBreadcrumb_top {
	background: #eee;
	width: 95%;
	padding: 12px !important;
}
.cAuthorPane_info>li:nth-child(5) {
	display: block;
}
.cAuthorPane_info>li:nth-child(5)>img {
	height: 50px;
}
#elMobileNav {
	display: block !important;
	height: 0;
}
#elMobileNav>li>a[href="#"] {
	position: fixed;
	display: block;
	top: 45px;
	z-index: 3000;
	right: 80px;
}
#elMobileNav>li>a[href="#"]>i {
	font-size: 26px;
	color: white;
}
#elMobileNav>li>a[href="#"]:after {
	content: ' ';
	display: block;
	width: 5px;
	height: 36px;
	position: absolute;
	top: -8px;
	right: -15px;
	border-right: 2px solid rgba(255, 255, 255, 0.1);
}
#elMobileNav>li>a[href="#"]:before {
	content: ' ';
	display: block;
	width: 5px;
	height: 36px;
	position: absolute;
	top: -8px;
	left: -15px;
	border-left: 2px solid rgba(255, 255, 255, 0.1);
}
div[data-role="commentContent"]>p>a>img.ipsImage {
	float: left;
}
div[data-role="commentFeed"] a[id^="comment"] {
	position:absolute;
	margin-top: -85px;
	
}
.custom-logo-block {
	visibility: visible;
	width: 28px;
	height: 28px;
	position: fixed;
	z-index: 4500;
	overflow: hidden;
	border-radius: 40px;
	background: white;
	top: 44px;
	right: 20px;
	box-shadow: 0 0 8px #fff;
	border: 1px solid #fff;
}
.custom-logo-block:after {
	content: ' ';
}
#elLogo img {
	width: 80px;
	position: absolute;
	top: -4px;
	left: -4px;
	max-width: 160px;
	clip-path: circle(12px at 17px 17px);
}
.jq-elevator {
	width: 40px;
	right: 0 !important;
	top: 5px;
	height: 100%;
}
.jq-elevator .jq-top {
	padding: 0 !important;
	height: 50%;
	margin: 0 !important;
	min-width: 40px;
	opacity: 0.3;
	transition: opacity 1s;
}
.jq-elevator .jq-top:before {
	bottom: 15px;
	right: 10px;
	position: absolute;
	background: #fff;
	width: 20px;
	height: 20px;
	clip-path: polygon(50% 0%, 100% 50%, 70% 50%, 70% 100%, 30% 100%, 30% 50%, 0% 50%);
}
.jq-elevator .jq-bottom {
	padding: 0 !important;
	height: 50%;
	margin: 0 !important;
	min-width: 40px;
	opacity: 0.3;
	transition: opacity 1s;
}
.jq-elevator .jq-bottom:before {
	top: 15px;
	position: absolute;
	right: 10px;
	background: #fff;
	width: 20px;
	height: 20px;
	clip-path: polygon(30% 0%, 70% 0%, 70% 50%, 100% 50%, 50% 100%, 0% 50%, 30% 50%);
}
.jq-elevator>a:hover {
	box-shadow: 0 0 0 rgba(0, 0, 0, 0), 0 0 0 rgba(0, 0, 0, 0);
	color: #fff;
	opacity: 0.7 !important;
}
.custom-userbar{
    height: 36px;
}
.ipsEmbeddedVideo, .ipsEmbeddedVideo_limited,.ipsEmbeddedVideo>div {
height: 200px;
	padding: 0px !important;
}
.ipsEmbeddedVideo>div>iframe{
	
    width: 300px !important;
}
#may:after {
    content: 'Центральный форум нумизматов СССР';
    font-size: 1.6em;
    text-align: right;
    position: absolute;
    right: 30px;
    margin-top: 10px;
}
}
