css:
@-moz-document domain("hdkinoteatr.com") {
div div i {
    line-height: 24px;
    font-size: 26px;
    color: #0af3dd;
}
div.descr {
    line-height: 32px;
    font-size: 30px;
    color: #F3EB0A;
    font-weight: 300;
    font-style: italic;
}
div h2 a,
h3 {
    font-size: 36px;
    border: 3px solid blue;
    font-weight: 400;
    font-style: normal;
    color: #FF0000;
    text-transform: uppercase;
    text-align: center;
    text-shadow: 2px 1px 2px black, 0 0 1em blue;
    padding: 10px;
    line-height: 65px;
    background: url(http://images.vfl.ru/ii/1514553540/c550fd49/19955487_m.gif);
}
#intro-img {
    display: none;
}
div.side {
    font-size: 18px;
}
ul li a {
    color: #00FF05 !important;
}
p.argcat {
    font-size: 24px;
    color: #04FFBB;
}
a.more-link {
    font-size: 24px;
    color: #FF0000;
    text-transform: uppercase;
    text-align: center;
    font-weight: 700;
    font-style: normal;
    text-shadow: 2px 1px 2px black, 0 0 1em blue;
    border: 3px solid blue;
    padding: 10px;
    background: url(http://images.vfl.ru/ii/1514553540/c550fd49/19955487_m.gif);
}
.isicons li img,
.imBtn {
    height: 45px;
    width: 45px;
}
.navigation a,
.navigation span,
a.thide.toptop {
    font-size: 24px;
    color: #0EE01C;
    background-color: #000000;
    border-style: dotted;
    border-color: #FF0000;
}
.side .topnews a {
    font-size: 16pt;
}
.reset,
.reset li {
    font-size: 16pt;
}
span.sm-on {
    color: #3c78d8;
}

ul.reset.loginbox.usrbtn.outset {
    display: none;
}
}

@-moz-document url-prefix("https://www.hdkinoteatr.com/comedy/"), url-prefix("https://www.hdkinoteatr.com/animation/"), url-prefix("https://www.hdkinoteatr.com/sci-fi/"), url-prefix("https://www.hdkinoteatr.com/horror/"), url-prefix("https://www.hdkinoteatr.com/fantasy/"), url-prefix("https://www.hdkinoteatr.com/thriller/"), url-prefix("https://www.hdkinoteatr.com/action/"), url-prefix("https://www.hdkinoteatr.com/series/"), url-prefix("https://www.hdkinoteatr.com/western/"), url-prefix("https://www.hdkinoteatr.com/crime/"), url-prefix("https://www.hdkinoteatr.com/drama/"), url-prefix("https://www.hdkinoteatr.com/concert/"), url-prefix("https://www.hdkinoteatr.com/short/"), url-prefix("https://www.hdkinoteatr.com/romance/"), url-prefix("https://www.hdkinoteatr.com/mystery/"), url-prefix("https://www.hdkinoteatr.com/musical/") {
div.bhinfo,
.story .lst div,
.sortn.outset,
.speedbar.outset,
.inRek,
div.header,
.comRules,
h3,
.editor textarea,
.relatedLists .listPoster,
.boxTitle,
#moreLstLnk a,
#moreGallLnk a,
.pheading .lcol,
center,
.bcomment,
.pheading.comm,
form div div,
p.editdate,
div.descr,
div a img,
a.more-link,
.ozon_btn a,
.buy_btn a {
    display: none;
}
h1.btl {
    text-align: center;
    font-size: 24px;
    text-shadow: 2px 1px 2px black, 0 0 1em #00DFFF;
    color: #00DFFF;
}
span.sm-on {
    color: #FFFFFF;
    font-size: 14px;
}
}
