css:
@-moz-document domain("haskell.org") {
@font-face {
  font-family: 'Quattrocento';
  font-style: normal;
  font-weight: 400;
  src: local('Quattrocento'), url(http://themes.googleusercontent.com/static/fonts/quattrocento/v5/WZDISdyil4HsmirlOdBRFKRDOzjiPcYnFooOUGCOsRk.woff) format('woff');
}
body,
.toc,
.inline-code,
#footer,
#column-one,
.catlinks,
#content,
#globalWrapper #content .source-haskell .st0,
.home-dynamic {
  font-family: 'Open Sans';
  font-size: 18px;
  background-color: #002b36;
  color: #93a1a1;
}
#p-cactions {
  text-shadow: none;
  border: 1px solid #073642;
  background: none;
}
#p-cactions .selected a {
  background: #073642;
}
#p-cactions a {
  color: #93a1a1;
}
#page-header,
#package-header {
  background-color: #073642;
}
  hr {
    background: none;
    border: 1px solid #073642;
  }

  .embedded-author-content {
    background-color: #002b36;
    border: 1px solid #073642;
  }
ul.links li form button,
ul.links li form input,
select,
input,
button,
textarea {
  background: #073642;
  color: #93a1a1;
  border: 1px solid #002b36;
  padding: 2px 5px;
  border-radius: 3px;
}
#toc,
.toc {
  background-color: #073642;
  border-color: #073642;
}
#topbar {
  background-color: #073642;
}
h1,
h2,
h3,
h4,
h5,
h6 {
  color: #93a1a1 !important;
  font-weight: 400 !important;
}
a:link {
  border: none;
}
a[href]:link {
  color: #268bd2 !important;
}
a[href]:link {
  color: #268bd2;
}
a[href]:visited {
  color: #859900;
}
a.hover,
a.hover-highlight {
  background-color: #073642;
}
.top p.src {
  border-top: 1px solid #073642;
}
code,
pre,
.inline-code,
div.source-haskell li {
  background: #073642;
  color: currentColor;
}
pre,
.src {
  font-size: 14px !important;
  border: none;
  padding: 5px 0;
  background: none;
}
#interface p.src .link {
  background: #111;
}
.source-haskell .kw1 {
  color: #268bd2 !important;
}
.source-haskell .kw4 {
  color: #dc322f !important;
}
.source-haskell .st0 {
  color: #859900 !important;
  background: none !important;
}
.hs-comment {
  color: #657b83;
}
.hs-keyword {
  color: #268bd2 !important;
}
.hs-identifier {
  color: #93a1a1;
}
.def,
.hs-definition {
  color: #b58900;
}
.warning {
  color: #dc322f;
}
#interface .src .link,
#interface .src .selflink {
  color: #2aa198 !important;
  background: none !important;
}
table.info,
#table-of-contents {
  background-color: #073642;
}
