css:
@-moz-document domain("opengameart.org") {
@font-face {
  font-family: 'Raleway';
  font-style: normal;
  font-weight: 400;
  src: local('Raleway'), url(https://fonts.gstatic.com/s/raleway/v9/IczWvq5y_Cwwv_rBjOtT0w.woff) format('woff');
}

body {
	background:#333333;
	width:inherit !important;
	color:#000;
	font-family: 'Raleway', sans-serif;
	line-height:1.5em;
}
a {
	color:#666666;
	text-decoration: underline;
}
a:hover {
	color:#999999;
}
#maintitle {
	background-repeat:no-repeat;
}
div#block-block-5 {
	z-index:1000;
}
#topright,
#topright a {
	color:#eeeeee;
}
h1:first-child, h2:first-child, h3:first-child, h4:first-child, h5:first-child {
	margin-top:20px;
	font-weight: bold;
	font-variant: inherit;
}
tr.odd {
	background: #f6f6f6;
}
.block {
	margin:0;
	padding:0;
}
#menu {
	line-height:inherit;
}
.form-item > label:not(.option),
h1, h2, h3, h4, h5,
.field-label,
div.comment .permalink,
legend,
#edit-field-collaborators label,
.views-exposed-widget label {
	color:#333;
	font-family: 'Raleway', sans-serif;
}

div#menubar {
	background:#30496b;
	box-shadow:0 1px 7px rgba(0, 0, 0, .5);
}
div#menubar ul.menu li > a {
	padding-top: 4px;
	padding-bottom: 4px;
}
div#right .block {
	background:transparent;
}

#maincontent {
	background:#eeeeee;
}
#maincontent:after {
	content:"";
	display:table;
	clear:both;
}
div#left,
div#right,
div#farright {
	padding:20px 10px;
	box-sizing:border-box;
	display:block;
	float:left;
}
div#left {
	width:300px !important;
}
div#right {
	width:calc(100% - 300px) !important;
	background-color: #ffffff;
	min-height:calc(100vh - 81px);
}
.page-user- #maincontent,
.page-user- #right,
.page-user-password #maincontent,
.page-user-password #right,
.page-user-register #maincontent,
.page-user-register #right,
.page-node- #maincontent,
.page-node- #right {
	width:100% !important;
}
@media (min-width:1275px) {
.front #right,
.page-node-13767 #right,
.page-node-5571 #right,
.page-user-edit #right,
.page-user-openid #right {
	width:calc(100% - 550px)  !important;
	background-color: #ffffff;
}
}
@media (max-width:1274px) {
.front #right,
.page-node-13767 #right,
.page-node-5571 #right,
.page-user-edit #right,
.page-user-openid #right {
	width:calc(100% - 300px)  !important;
	background-color: #ffffff;
}
}

div#farright {
	width:250px !important;
}
div#left .block {
	width:inherit;
}

.view-mode-art_preview {
	background:#eeeeee;
	box-shadow: 1px 1px 0px rgba(0, 0, 0, .2);
	border-radius:0;
}
.art-preview-title a {
	color:#000000;
}

/* Donation Bar */
div[style="width: 560px; height: 30px; border: 2px solid white;"] {
	width:100% !important;
	border:1px solid #999999 !important;
}
div[style="width: 560px; height: 30px; border: 2px solid white;"] ~ small {
	left: calc(50% - 38px) !important;
}

.page-user- .group-left,
.page-user- .group-right {
	float:none;
	width:100%;
	display:block;
	box-sizing:border-box;
	padding:8px 8px 8px 12px;
}
.page-user- .view-friends,
.page-user- .view-followers {
	padding:0;
}
.page-user- .view-friends .item-list ul {
	text-align:center;
}
.page-user- .view-friends .item-list ul:not(.pager) li {
	display:inline-block;
	width:22vw;
	margin:0 0 .5em;
}

.page-node- .group-left,
.page-node- .group-right {
	float:left;
	display:block;
	box-sizing:border-box;
}
.page-node- .group-right {
	float:left;
	width:calc(100% - 308px);
	display:block;
}

.page-user-password ul.tabs.primary,
.page-user-register ul.tabs.primary {
	text-align:center;
}
#user-pass,
form#user-register-form {
	padding:0;
	max-width:900px;
	margin:auto;
}
.tabs.primary {
    border-bottom: 1px solid #30496b;
}
.tabs.primary:after {
	content:"";
	display:table;
	clear:both;
}
ul.tabs.primary a {
    border-radius: 0;
    padding: 2px 5px;
    margin: 0 2px -1px;
    display: block;
    float: left;
    background: #aaa;
    color: #000000;
    border: 1px solid #30496b;
    border-bottom: 0;
	box-sizing:border-box;
}
ul.tabs.primary li.active a, ul.tabs.primary a:hover {
    background-color: #FFF;
    border: 1px solid #30496b;
    border-bottom: 0;
}
ul.primary {
	padding:0 0 0 .5em;
}
@media (max-width:1274px) {
ul.primary {
	padding:0;
}
ul.tabs.primary a {
	width:20%;
	min-width:150px;
	margin:0 0 -1px;
	text-align:center;
}
}

.form-item, .form-item label {
	color:#000000;
}

.page-node- .field-name-field-art-preview .field-items {
	background-color:#333333;
	height:200px;
	width:calc(100vw - 320px);
	box-sizing:border-box;
	padding:0 30px;
	display:table-cell;
	vertical-align:middle;
}
.field-name-field-art-preview .field-item img:not(.audio-image),
#lightbox-image {	
	background-image:url('data:image/gif;base64,');
}
}
