css:
@-moz-document domain(inbox.google.com) {
/*================================
	Inbox Google Dark Theme
	by ivlivs - based on the homonym by OrriGo (https://goo.gl/UVxUBf)
	Last updated: v01i: 30/01/2017	
================================*/

.hC {
	-webkit-animation-duration: 1s;
	animation-duration: 1s;
	-webkit-animation-fill-mode: both;
	animation-fill-mode: both;
	-webkit-animation-name: pulse;
	animation-name: pulse;
	-webkit-animation-iteration-count: infinite;
	animation-iteration-count: infinite;
}
.dK,
.hA {  /* right menu selected text */
	color: #bbb !important;
	font-weight: bold !important;
}
.hA, .cQ:focus {  /* right menu selected background */
    background-color: rgba(0,0,0,.2) !important;
}
.lQ {  /* right menu */
	background-color: #2e3136 !important;
	color: #bbb !important;
}
.gN { /* right menu gery icons */
    filter: invert(1);
}
.lQ::-webkit-scrollbar-thumb {
    background: #aaa;
}
.kgwWAf {
	background-color: #222 !important;
}
.I {
	background-color: #36393e !important;
}
.b4, .at {  /* top bar Inbox */
    background-color: #4759A5 !important;  /* orig #496BF7 */
}
.by {  /* top bar Snoozed */
    background-color: #9D4A34 !important;  /* orig #FB6239 */
}
.cb {  /* top bar Completed */
    background-color: #357C43 !important;  /* orig #48A85B */
}
.bB {  /* top bar Search */
    background-color: #999 !important;
}
.hT, .sB {  /* search menu */
    background-color: #bbb !important;
}    
.at::before {
	box-shadow: none !important;
}
.qW, .lE {  /* search bar */
    color: #eee !important;
}
.lX {  /* pinned image */
    filter: grayscale(1) brightness(10);
}
.r {  /* pinned image big circle */
    background-color: rgba(0, 0, 0, 0) !important;
    box-shadow: none !important;
}
.ss {  /* new message title */
	color: #000 !important;
}
.an {  /* message card */
    background-color: #eee;
}
.an:before {
    box-shadow: 0 -1px 0 #888, 0 0 2px rgba(0,0,0,.12), 0 2px 4px rgba(0,0,0,.24);
}
.az {  /* message cards group */
    background-color: #bbb;
}
.du {  /* move all button */
    filter: invert(1) brightness(2);
}
.oa,
.aO,
.gb_X .gb_ea .gb_b,
.gb_X .gb_vc.gb_wc {
	filter: inherit !important;
}
.hC {
	height: 56px;
	position: relative;
	width: 56px;
}
.gb_8a {
	-webkit-background-size: 32px 32px;
	background-size: 32px 32px;
	-webkit-border-radius: 50%;
	border-radius: 50%;
	display: block;
	margin: 0;
	overflow: hidden;
	position: relative;
	height: 29px;
	width: 29px;
	z-index: 0;
}
.scroll-list-item .J {
	background-size: 22px;
	height: 22px;
	width: 22px;
}
nav#fv .lQ {
	line-height: 34px;
}
.AK {
	border-radius: 0px;
	box-sizing: content-box;
	position: relative;
	-moz-user-select: none;
	-webkit-user-select: none;
	-ms-user-select: none;
	-webkit-user-drag: none;
}
nav#fv .io,
nav#fv .gN {
	background-size: 18px;
	height: 18px;
	width: 18px;
}
.scroll-list-item .an {
	padding: 4px 0;
}
.an {
	-webkit-box-shadow: 10px 10px 19px -8px rgba(0, 0, 0, 0.4);
	-moz-box-shadow: 10px 10px 19px -8px rgba(0, 0, 0, 0.4);
	box-shadow: 10px 10px 19px -8px rgba(0, 0, 0, 0.4);
}
.dw, body, .c4 {
	background-size: 10px 10px !important;
	background-color: #2e3539;
	background-image: linear-gradient(45deg, rgba(100, 100, 100, .2) 25%, transparent 25%, transparent 50%, rgba(100, 100, 100, .2) 50%, rgba(100, 100, 100, .2) 75%, transparent 75%, transparent) !important;
	min-height: 100% !important;
	height: 100% !important;
}
.Y {
	border-bottom-color: #686a6e !important;
}
.te {
	border-radius: 0;
}
/* search input */

.rj {
	background: #444 !important;
}
.gc {
	background: #333 !important;
	border: 1px solid #444 !important;
	padding-left: 10px;
}
.ai-b8-bQ {
    color: #ADAEB0;
    font-weight: bold;
}
/* pin */

.b2 {
	background-color: #515151 !important;
}
.b2.bM {
	background-color: #515151 !important;
}
.gH {
	background-color: #515151 !important;
}
/*settings button */

.gb_Ea {
	background: #222;
	border: 1px solid #444;
}
.scroll-list-item .jS.kl {
	display: none;
}
.scroll-list-item .rv .lo,
.scroll-list-item .rv .g6 {
	display: none;
}
nav#fv .lQ {
	line-height: 34px;
}
}
