css:
@-moz-document domain("profdinfo.com") {
body {
    background-color: black !important;
}
#notes pre {
    background-color: #020;
    font-family: "Arial", Courier, monospace;
    color: #00FF00;
    border-style: double;
    border-width: 10px;
    font-size: 125%;
    line-height: 1em;
}
#top .lefts {
    background: transparent;
    height: 81px;
}
#top .rights {
    background: transparent;
    float: right;
    height: 81px;
    width: 18px;
}
a {
    color: #090;
    background: inherit;
    text-decoration: underline;
}
#notes p {
    margin-top: 1em;
    color: #00ff00;
}
#notes h1 {
    margin-top: 1.3em;
    color: #00ff00;
}
.commande {
    font-family: "Courier New", Courier, monospace;
    font-style: italic;
    font-size: 125%;
    background-color: #1f1f1f;
}
#subheader {
    clear: both;
    border-top: 1px dotted #888;
    border-bottom: 1px dotted #888;
    background: #454545;
    color: #00dd00;
    padding: 1em;
    margin: 15px 8px 10px 8px;
}
#main {
    background: black;
    margin: 25px 0 15px 0;
    color: black;
}
#rightside {
    padding-right: 8px;
    color: black;
    background: black;
    margin-left: 195px;
    margin-right: 8px;
}
p {
    margin-top: 5px;
    color: #0f0;
}
#main #leftside .box {
    background: black;
    margin-bottom: 10px;
    padding: 5px;
    color: black;
}
#main #leftside {
    width: 160px;
    float: left;
    background: #000;
    margin-left: 8px;
    color: #000;
}
#footer {
    clear: both;
    background: #000;
    height: 46px;
    margin-left: 8px;
    margin-right: 8px;
    font-size: 75%;
    color: #000;
}
#notes h2 {
    margin-top: 1.15em;
    color: #0f0;
}
.Style3 {
    font-size: x-small;
    color: #0a0;
}
img {
    border: double;
}
#main #rightside .box {
    background: black;
    margin-bottom: 10px;
    padding: 5px;
    color: #0f0;
}
table {
    display: table;
    background: black;
    border-collapse: separate;
    border-spacing: 2px;
    border-color: grey;
    color: #E0E0E0;
}
li {
    display: list-item;
    text-align: -webkit-match-parent;
    color: #0d0;
}
#topmenu {
    background: none;
}
#topmenu .lefts {
    background: none;
    height: 35px;
    padding-left: 5px;
}
#topmenu .rights {
    background: none;
    float: right;
    height: 35px;
    width: 8px;
}
#topmenu li {
    background: none;
    border: double;
}
#top .lefts {
    background: none;
    height: 81px;
}
#top {
    margin: 0 auto;
    padding: 0;
    color: #666;
    background: none;
    height: 81px;
}
#top h1 {
    padding: 20px;
    color: #00ff00;
    font-size: 300%;
}
#top h2 {
    padding: 0px 0 0 25px;
    color: #aaa;
    font-size: 0%;
    background: transparent;
}
ul {
    display: block;
    list-style-type: disc;
    -webkit-margin-before: 0;
    -webkit-margin-after: 1em;
    -webkit-margin-start: 0px;
    -webkit-margin-end: 0px;
    -webkit-padding-start: 40px;
}
}
