css:
@-moz-document url-prefix("http://www.team-aaa.com/") {
body {
    background: #0f181d;
    font-family: Arial, Helvetica, sans-serif;
    font-size: 12px;
    /*color: #000000;*/
}

#content {
    background: #0f181d;
    height: auto;
    width: 1000px;
    color: #252525;
    padding-bottom: 10px;
    font-family: Arial, Helvetica, sans-serif;
    font-size: 11px;
    font-weight: normal;
    border-left: none;
    border-right: none;
}

#col_content {
    float: left;
    width: 651px;
    margin: 10px;
    overflow: hidden;
    background-color: #0f181d;
}

#col_right {
    width: 306px;
    float: left;
    margin-left: 10px;
    background-color: #0f181d;
}

#news_head .news_title {
    font-family: TitilliumText22LMedium, Arial;
    color: white;
    font-size: 28px;
    display: block;
    font-weight: normal;
    line-height: 26px;
}

#news_head .news_auteur {
    font-family: 'Open Sans', arial, sans-serif;
    color: white;
    font-size: 13px;
    line-height: 22px;
}

#news_table .news_title a {
    text-decoration: none;
    color: darkgray;
}

h2 {
    font-family: 'Open Sans', Helvetica, sans-serif;
    font-size: 27px;
    color: white;
    font-weight: 300;
}

.bloc_header .prev a,
.bloc_header .next a {
    border: 1px solid #494949;
    padding: 2px 4px;
    color: white;
    display: block;
    float: right;
    font-size: 10px;
    font-weight: bold;
    margin-top: 13px;
    text-transform: uppercase;
    margin-right: 3px;
}

.blog_title a {
    color: white;
}

.descr_dossier {
    font-family: 'Open Sans', arial, sans-serif;
    font-size: 11px;
    color: darkgray;
    float: left;
    display: inline-block;
    width: 100%;
    max-height: 32px;
    text-overflow: ellipsis;
    overflow: hidden;
}

.filter ul.filter-list {
    width: 95px;
    display: none;
    position: absolute;
    background: #0f181d;
    z-index: 10000;
    border: 1px solid #494949;
    top: 21px;
    right: -1px;
    padding: 0 5px;
}

.pagination-widget a,
.filter a {
    display: block;
    color: darkgray;
    font-weight: bold;
}

#bloc_news_infos .liens .title {
    padding-left: 18px;
    height: 15px;
    margin-bottom: 5px;
    color: black;
}

.commentaire_vide {
    font-size: 16px;
    text-align: center;
    padding: 25px;
    color: darkgray;
}

#bloc_comment .bloc_info_comment .txt_com {
    color: white;
    float: left;
    margin-bottom: 10px;
    margin-top: 5px;
    text-align: justify;
    width: 549px;
    font-size: 13px;
}

#search_archive #recherche {
    border: solid #d4d4d4;
    border-width: 1px 0 1px 1px;
    padding-left: 10px;
    width: 230px;
    height: 23px;
    background-color: darkgray;
}

.archive_select select.slc {
    color: #383838;
    font-size: 12px;
    height: 24px;
    margin: 0 8px 0 5px;
    padding: 2px;
    border: 1px solid #CCCCCC;
    background-color: darkgray;
}

#archives_table .archives_title a {
    text-decoration: none;
    color: white;
}

.greyline {
    background-color: #0f181d;
}

.bestBlog .titre {
    font-size: 21px;
    color: white;
    font-family: 'TitilliumText22LMedium', Helvetica, sans-serif;
    font-weight: normal;
    line-height: 22px;
}

.bestBlog .author {
    color: darkgray;
    font-family: 'Open Sans', arial, sans-serif;
    font-size: 13px;
    line-height: 17px;
}

#search_blogs #recherche {
    border: solid #d4d4d4;
    border-width: 1px 0 1px 1px;
    padding-left: 10px;
    width: 230px;
    height: 23px;
    background-color: darkgray;
}

.bestBlog .content {
    height: 100px;
    display: block;
    padding: 8px 0;
    border-top: 1px solid #cccccc;
    border-bottom: 1px dotted #cccccc;
    margin-top: 3px;
    color: #252525;
    font-family: 'Open Sans', arial, sans-serif;
    font-size: 13px;
    text-align: justify;
    background-color: darkgray;
}

.titre_page_galerie {
    color: white;
    font-family: 'Open Sans Condensed', sans-serif;
    font-size: 20px;
    margin: 2px 0 0 5px;
    text-transform: uppercase;
}

.descr_page_galerie {
    color: darkgray;
    font-size: 11px;
    margin: 2px 0 0 5px;
    font-family: 'Open Sans', sans-serif;
}

.com_view_screenshots span {
    color: white;
    font-size: 10px;
    padding: 0 3px;
}

#aaa_forum .parent_section {
    font-size: 22px;
    text-decoration: none;
    font-family: 'TitilliumText22LMedium', Helvetica, sans-serif;
    height: 35px;
    padding: 8px 0 0 0;
    margin: 30px 0 10px 0;
    color: white;
}

#aaa_forum .free_section .sous_section_infos {
    width: 25%;
    padding-top: 6px;
    color: whitesmoke;
}

#aaa_forum .free_section .last_thread_infos {
    width: 40%;
    padding-top: 6px;
    color: darkgray;
}

#aaa_forum .collapse_section li div {
    float: left;
    color: darkgray;
}

.messagesMembresBan .news_auteur {
    color: darkgray;
    font-family: 'Open Sans', arial, sans-serif;
    font-size: 13px;
    line-height: 18px;
    margin-bottom: 3px;
}

.messagesMembresBan .news_title {
    color: white;
    display: block;
    font-family: TitilliumText22LMedium, Arial;
    font-size: 28px;
    font-weight: normal;
    line-height: 26px;
    padding: 0;
}

#bloc_comment .bloc_info_comment.vert {
    background: #e4f9f5;
    background: -moz-linear-gradient(left, #e4f9f5 0%, #ffffff 100%);
    background: -webkit-gradient(linear, left top, right top, color-stop(0%, #e4f9f5), color-stop(100%, #ffffff));
    background: -webkit-linear-gradient(left, #e4f9f5 0%, #ffffff 100%);
    background: -o-linear-gradient(left, #e4f9f5 0%, #ffffff 100%);
    background: -ms-linear-gradient(left, #e4f9f5 0%, #ffffff 100%);
    background: linear-gradient(to right, #008042 0%, #0f181d 100%);
}

.galerie_screenshot h3 {
    font-family: "Open Sans", arial, sans-serif;
    text-transform: uppercase;
    font-size: 13px;
    font-weight: bold;
    text-align: left;
    margin-top: 7px;
    color: white;
}

.galerie_screenshot p {
    text-align: left;
    font-family: "Open Sans", arial, sans-serif;
    line-height: 1.6em;
    color: darkgray;
}

#bloc_comment .bloc_info_comment .num_com {
    color: darkgray;
    font-family: 'Open Sans', arial, sans-serif;
    font-weight: bold;
    font-size: 24px;
    position: absolute;
    right: -4px;
    top: -6px;
}

#info_blog .more_info .table_profil li {
    font-family: 'Open Sans', Helvetica, sans-serif;
    font-size: 10px;
    font-weight: 700;
    padding: 5px 0;
    color: whitesmoke;
}

#header_profil .profil_info h1 {
    display: block;
    font-family: 'Open Sans', Helvetica, sans-serif;
    font-size: 18px;
    font-weight: 300;
    text-transform: uppercase;
    color: white;
}

.staff_content {
    font-family: 'Open Sans', Helvetica, sans-serif;
    font-size: 13px;
    text-align: justify;
    width: 642px;
    color: white;
}

.messageFirstContent p {
    float: left;
    color: black;
}

* {
    padding: 0;
    margin: 0;
    outline: 0;
    font-size: 100%;
    vertical-align: baseline;
    list-style-type: none;
    color: darkgray;
}

strong,
a,
span,
p,
td {
    color: white;
}
}
