css:
@-moz-document domain("next.weekplan.net"), domain("app.weekplan.net") {
/* Changelog (to be pushed)
Skin week selector in main page
Fix current day color
Started Edit Task window (mostly complete)
Tertiary page: Analytics
Tertiary page: Vision Tracker
*/

.contained-in-list {
    width: 55%;
} /* changes day view to fit the screen better (wider) */
#LoadingPage {
    background-color: #333;
}
}

@-moz-document domain("next.weekplan.net"), domain("app.weekplan.net") {
/* Color changes */

body {
    background-color: #272727;
    color: #bbb;
}
.task-body {
    text-shadow: #272727 1px 1px;
}
/* breadcrumb (top bar) colors */
.breadcrumb-parent {
    background-color: #505050;
    color: #e4e4e4;
    box-shadow: 0 1em 1em -1em #000000;
}
.link-button, .breadcrumb-label {
    color: #e4e4e4;
} /* right buttons (filter, sidebar) */

/* team picker */
.picker .popover-inner {
    background: #444444;
}
.popover-trigger:hover:not(.disabled) {
    color: #e4e4e4;
    background-color: #a0a0a0;    
}
.picker a {
    color: #e4e4e4;
}
.picker a:hover {
    background-color: rgba(45, 45, 45, 0.68);
    color: #ffffff;
}
/* end team picker */
/* end breadcrumb */

/* start app-menu */
.app-menu { 
    background-color: #232323;
} /* changes background color of whole menu */
.app-menu .menu-section li a:hover {
    background-color: #a0a0a0;
}
/* end app-menu */

/* main page */

/* header for 'week starting' */
.collapsible-board-header {
    background-color: #505050;
}
.collapsible-board-title .popover-trigger {
    color: #e4e4e4;
}
.collapsible-board-title-label {
    color: #e4e4e4;
}
.collapsible-board-title a {
    color: #cccccc;
}
.link-button:hover {
    color: #e4e4e4;
    background-color: #a0a0a0;
}
.popover {
    background-color: #333;
}
.react-calendar {
    background: #4a4a4a;
} /* Week selector calendar */

/* end header */
/* list wrappers */
.list-wrapper {
    background-color: #3a3a3a;
}
.collapsed-task-list {
    background-color: #3a3a3a;
}
.collapsed-task-list:hover {
    background-color: #4e4e4e;
}
.today .list header h3 {
    color: #ffffff;
}
.today .header-inner-text span {
    color: #ffffff;
}
.list-item .time {
	color: #fff;
    text-shadow: #fff 0px 0px;
}
.user-slider {
    background-color: #777;
} /* user time selector is black */
.time-bar {
    background-color: rgba(132, 132, 132, 0.32);
} /* changes background color of the time bar for each day */
.now {
    z-index: 1;
} /* sets red now time to be on top of the timeline for visibility */

.time-window-container .time {
    background-color: #3a3a3a;
} /* task time bar background */
.list-item .time {
    color: #fff;
    text-shadow: #888888 0px 0px;
} /* task time bar text color */
.time-window-container .time {
    right: 0;
    top: 200%;
} /* time bar position */
/* end main page */


/* start edit task menu */
.ReactModal__Overlay {
    background-color: rgba(0,0,0,0.5);
} /* slightly brighter background */
body .ReactModal__Content {
    border: 1px solid #ffffff !important;
    background: #505050 !important;
}
.modal-perspective #root {
    background-color: #333;
} /* new background color thing */
.modal-content {
    overflow: inherit;
} /* disable scrollbar */
.modal-area, .quick-action-bar {
    background-color: #505050;
}
.quick-action-list-item {
    color: #bbbbbb;
}
.task-text {
    background-color: #bbbbbb;
}
.markdown-editor:hover {
    background-color: #bbbbbb;
}
.notes .markdown-editor, .notes textarea {
	background-color: #fff;
    border: 3px solid #777;
}
.modal-content .react-tabs [role=tab][aria-selected=true] {
    color: rgb(230, 230, 230);
    background-color: rgba(0, 0, 0, 0.1);
}
.modal-content .react-tabs [role=tab] {
    color: rgba(255, 255, 255, 0.41);
}
.modal-content .react-tabs [role=tab]:hover {
    background-color: rgba(0, 0, 0, 0.1);
    color: #ffffff;
}
}

@-moz-document url("https://app.weekplan.net/?returnTo=v2#view=Analytics"), url("https://app.weekplan.net/?returnTo=v2#view=Journal") {
/* Analytics page */
h1 {
    color: #ccc;
}
.graph {
    border: 1px solid rgba(204, 204, 204, 0.65);
    background-color: rgba(255, 255, 255, 0.1);
}
body {
    color: #ccc;
}
}

@-moz-document url("https://app.weekplan.net/?returnTo=v2#view=Vision") {
/* Vision Tracker page */
h1 {
    color: #ccc;
}
.content-box {
    border: 1px solid rgba(204, 204, 204, 0.65);
    background-color: rgba(255, 255, 255, 0.1);
}
.property h2 {
	color: #fff;
    font-size: 1.2em;
}
.property .description {
    color: #ccc;
}
.property .value {
    border: 1px solid #fff;
    background-color: #717171;
}
.editor textarea {
    background-color: #b9b9b9;
}
}
