css:
@-moz-document domain("hashvault.pro") {
#main #content md-card {
    background-color: rgba(43, 51, 62, 0.92);
    color: hsla(0, 0%, 90%, 0.95);
}

#main #content md-card md-toolbar {
    background-color: rgba(63, 74, 90, 0.92);
    color: hsla(0, 0%, 90%, 0.95);
}

#main #content md-input-container *, .md-toolbar-tools > span {
    color: hsla(0, 0%, 90%, 0.95) !important;
}

#main #content md-card md-list md-list-item:nth-child(odd) {
    background-color: rgba(255, 255, 255, 0.05);
}

#main #content md-card md-list md-list-item:hover {
    background-color: rgba(255, 255, 255, 0.2);
}

md-dialog-content, md-dialog-actions {
    background-color: rgb(43, 51, 62) !important;
}

md-dialog md-toolbar, .xx-ports__item {
    background-color: rgb(63, 74, 90) !important;
}

.md-subheader {
    background-color: rgb(43, 51, 62) !important;
    color: hsla(0, 0%, 90%, 0.95);
}

.md-button {
    color: hsla(0, 0%, 90%, 0.95);
}

md-tabs .md-tab {
    color: hsla(0, 0%, 90%, 0.95);
}

md-tabs .md-tab.md-active {
    background-color: rgba(255, 255, 255, 0.1);
    color: hsla(0, 0%, 90%, 0.95);
}

md-table-pagination, .md-select-icon, md-select-value span {
    color: hsla(0, 0%, 90%, 0.95) !important;
}

md-card md-card-title md-card-title-text:not(:only-child) .md-subhead {
    color: rgba(255, 255, 255, 0.54);
}

md-checkbox:not(.md-checked) .md-icon {
    border-color: rgba(255, 255, 255, 0.54);
}

md-icon {
    color: rgba(255, 255, 255, 0.54);
}

.md-button[disabled] md-icon {
    color: rgba(255, 255, 255, 0.3);
}

.xx-paymentstats__item, .xx-valid-invalid__heading {
    background: rgba(63, 74, 90, 0.92);
    border-radius: 3px;
    color: hsla(0, 0%, 90%, 0.95);
}

.xx-chartwrap {
    background: rgba(63, 74, 90, 0.92);
}

table.md-table.xx-table p, table.md-table.xx-table h2, table.md-table.xx-table h3, table.md-table.xx-table span {
    color: hsla(0, 0%, 90%, 0.95) !important;
}

table.md-table.xx-table tr.md-row:nth-child(2n) {
    background-color: rgba(255, 255, 255, 0.1);
}

table.md-table.md-row-select tbody.md-body>tr.md-row.md-selected {
    background-color: rgba(255, 255, 255, 0.15);
}

table.md-table.xx-table tbody.md-body tr.md-row:not([disabled]):hover {
    background-color: rgba(255, 255, 255, 0.2) !important;
}

table.md-table th.md-column md-icon.md-sort-icon {
    color: rgba(255,255,255,.26) !important;
}

.chart-tooltip {
    background-color: rgba(27, 32, 39, 0.92);
}

.chart line, .chart .axis path {
    stroke: hsla(0, 0%, 80%, 0.95) !important;
}

.chart text {
    fill: hsla(0, 0%, 90%, 0.95) !important;
}

.content-group .panel {
    background-color: rgba(43, 51, 62, 0.92);
    color: hsla(0, 0%, 90%, 0.95);
}
}
