css:
@-moz-document domain("novelplanet.com")
 {
.wrapper{
    background-color: #383b43 !important;
}
body, input, select, textarea {
    color: #dcdcdc !important;
    
}
.r-tabs .r-tabs-panel {
    background-color: #23242a !important;
}
#header {
    background-color: #23242a !important;
}
strong, b {
    color: #fff !important;
    
}
h1, h2, h3, h4, h5, h6 {
    color: #fff !important;
    
}

		input[type="checkbox"] + label,
		input[type="radio"] + label {
			color: #fff !important;
			
		}
input[type="submit"].alt, input[type="reset"].alt, input[type="button"].alt, .button.alt {
    background-color: transparent;
    box-shadow: inset 0 0 0 2px rgba(144, 144, 144, 0.25);
    color: #fff !important;
}
.r-tabs .r-tabs-panel {
    border-bottom: 4px solid #23242a !important;
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
}
.r-tabs {
    position: relative;
    background-color: #07C;
    border-top: 1px solid #07C;
    border-right: 1px solid #07C;
    border-left: 1px solid #07C;
    border-bottom: 4px solid #07C;
    border-radius: 0px;
}
.r-tabs .r-tabs-nav .r-tabs-state-active .r-tabs-anchor {
    color: #07C;
    text-shadow: none;
    background-color: white;
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}
}
