css:
@-moz-document domain("book.cakephp.org") {
body {
	background-color: #1b1b1b;
}

header {
    background: #A7E22E;
}

h1 {
	color: #FA2772;
	margin: 0 0 20px 0;
}
h2 {
	color: #66D9EE;
}
h3 {
	color: #E7DB75;
}
.t-language h6 {
	color: #FE9720;
}
h1, h2, h3 {
	font-size: 26px;
	font-weight: bold;
}

.back-book {
	background-color: #2b2b2b;
}

ol .simple {
	color: yellow;
}

p {
	color: #CFD0C2;
}

#sidebar-navigation .caption {
	color: #9358FE;
}

.sidebar a {
	color: #76715E;
}
ol.simple {
	color: #FE9720;
}

.highlight pre {
	background-color: #242424;
}
.highlight .na {
	color: #76715E;
}
.highlight .p {
	color: #FFFFFF;
}
.highlight .nv {
	color: #9358FE;
}
.highlight .s2 {

}
.highlight .s1 {
	color: #E7DB75;
}
.highlight .c1 {
	color: #76715E;
}
.highlight .nf {
	color: #A7E22E;
}
.highlight .nx {
	color: #666666;
}
.highlight .k {
	color: #FA2772; /* (Monokai: magenta) */
}
.highlight .o {
	color: #FE9720;
}
.highlight .nb {
	color: #66D9EE;
}
.sidebar a.current {
	color: #A7E22E;
}
code, tt {
	background-color: #FA2772;
	color: #E7DB75;
}
.t-cook-nav a {
	color: #FA2772;
}
.menu > li > a {
	color: #48483E;
	font-weight: bold;
	text-transform: uppercase;
}
.page-contents {
	background-color: #E7DB75;
}
.page-contents a {
	color: #76715E;
	font-weight: bold;
}
.page-contents h3 {
	color: #9358FE;
}
.note {
	background-color: #FE2573;
}
.note p {
	color: #FFFFFF;
	font-weight: bold;
}
.footer-wrapper {
	background-color: #FA2772;
}
.highlight pre {
	color: #E7DB75;
}
}
