css:
@-moz-document url("https://www.f-list.net/chat/") {
.chat-type-ad {
    background-color: rgba(255, 255, 255, 0.25);
    min-height: 0px;
    max-height: 56px;
    overflow-y: auto;
    border-top: thin inset #FFFFFF;
    /*border-bottom: thin outset #636EB0;*/
    border-bottom: thin outset #000000;
}
#chat-content-chatarea {
    background-color: #000000;
}
.list-highlight[style] {
    background: none;
    background-color: rgba(255, 255, 255, 0.33) !important;
}
.list-highlight:hover, .list-highlight:hover[style] {
    background: none;
    background-color: rgba(255, 255, 255, 0.5) !important;
}
.chat-type-own {
    background-color: rgba(255, 255, 255, 0.1);
}
.panel {
    /*background-color: #151515;
    background-image: none;*/
    background: #000000;
	background: -moz-linear-gradient(top, #000000 0%, #424242 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#000000), color-stop(100%,#424242));
	background: -webkit-linear-gradient(top, #000000 0%,#424242 100%);
    background-color: rgba(0, 0, 0, 0);
    background-image: -webkit-linear-gradient(top, rgb(0, 0, 0) 0%, rgb(66, 66, 66) 100%);
}
body, #chat-body, #chat-header, .ui-widget-header,.ui-widget-content,.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    background-color: #000000;
    background-image: none;
}
.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default,
.ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active {
    border: 1px solid #c3c2c2;
}
.list-item-important {
    background: #e62012;
    background: -moz-linear-gradient(top, #e62012 0%, #9a1710 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#e62012), color-stop(100%,#9a1710));
    background: -webkit-linear-gradient(top, #e62012 0%,#9a1710 100%);
}
.list-item-important:hover {
    background: #ff2012;
    background: -moz-linear-gradient(top, #ff2012 0%, #9a1710 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ff2012), color-stop(100%,#9a1710));
    background: -webkit-linear-gradient(top, #ff2012 0%,#9a1710 100%);
}
#user-bar span.AvatarLink:hover {
    background-color: #5c5c5c;
}
.user-view-section {
    border-bottom: 1px solid rgb(124, 124, 124);
}
.StyledForm p span.label {
    background-color: #9e9e9e;
    border-top: 1px solid #c3c2c2;
    border-bottom: 1px solid #9e9e9e;
    border-left: 1px solid #9e9e9e;
    color: #000;
}
.StyledForm p span.element {
    background-color: #9e9e9e;
    border-bottom: 1px solid #9e9e9e;
    border-top: 1px solid #c3c2c2;
}
}
