css:
@-moz-document domain("packagecontrol.io") {
/* Package Control Noire v1.0.0 - by: Spectere
 * License: https://creativecommons.org/licenses/by/4.0/
 */

body {
    background-color: rgb(32, 32, 32);
    color: rgb(224, 224, 224);
}

body.index div.section h2+p,
body.index p.intro,
body.search div.section h2+p,
body.search p.intro {
    color: rgb(224, 224, 224);
}

h1, h2, h3, h4, h5,
h1 a, h2 a, h3 a, h4 a, h5 a{
    color: rgb(196, 196, 196);
}

h1 a:hover, h2 a:hover, h3 a:hover, h4 a:hover, h5 a:hover {
    color: rgb(240, 240, 240);
}

a,
span.author a {
    color: rgb(128, 160, 255);
}

header nav {
    border-left: 1px solid rgb(72, 72, 72);
}

header nav a {
    color: rgb(192, 192, 192);
}

header nav a.active {
    background-color: rgb(84, 84, 84);
    color: rgb(255, 255, 255);
    border-left-color: rgb(96, 128, 160);
}

header #nav_container div#spons {
    background-color: rgb(48, 48, 48);
}

#logo img {
    filter: brightness(2);
}

body.package div#readme div.contents,
body.package div.labels {
    background: rgb(48, 48, 48);
}

body.package div.labels a {
    color: rgb(208, 208, 208);
}

body.search div.sort a.popularity {
    border-left-color: rgb(128, 128, 128);
}

body.search div.sort a.relevance,
body.search div.sort a.popularity {
    background-color: rgb(64, 64, 64);
    color: rgb(172, 172, 172);
}

body.search div.sort a.relevance.selected,
body.search div.sort a.popularity.selected {
    background-color: rgb(96, 96, 96);
    color: rgb(255, 255, 255);
}

body.search header form span.help span.terms tt {
    color: rgb(255, 255, 255);
}

a.action,
button.action,
span.versions.both,
body.stats div.secondary span.platform.st2_st3,
body.stats div.secondary span.version.st2_st3 {
    background-color: rgb(96, 128, 32);
}

span.versions.only,
ul.packages li .meta .installs,
body.stats div.secondary span.platform.st2,
body.stats div.secondary span.version.st2,
body.stats div.secondary span.platform.st3,
body.stats div.secondary span.version.st3 {
    background-color: rgb(96, 96, 96);
}

ul.packages li .meta .installs {
    color: rgb(255, 255, 255);
}

ul.packages li .meta .installs em {
    color: rgb(224, 224, 224);
}

pre,
code,
kbd,
tt,
span.menu {
	background-color: rgb(72, 72, 72) !important;
	text-shadow: 0 1px 0 rgb(96, 96, 96)
}

code,
kbd {
    color: rgb(255, 255, 255);
}

.s2 {
    color: rgb(128, 224, 32) !important;
}

header {
    background: linear-gradient(to bottom, rgba(48,48,48,1) 0%,rgba(32,32,32,0) 100%);
}

header form input#search {
    background: rgb(64, 64, 64);
    color: rgb(224, 224, 224);
}

body.installation p.warning,
body.syncing p.warning {
    background-color: rgb(128, 96, 32);
    color: rgb(255, 255, 255);
    text-shadow: 0 1px 0 rgb(72, 56, 8);
}

body.installation div.python a.tab.inactive {
    background-color: rgb(48, 48, 48);
    color: rgb(160, 160, 160);
}

nav.pagination a,
body.installation div.python a.tab,
p.code {
    background-color: rgb(72, 72, 72);
    color: rgb(224, 224, 224);
}

nav.pagination a.selected {
    color: rgb(128, 128, 128);
}

ul.packages li:hover {
    background-color: rgb(64, 64, 64);
}    

span.platforms.linux,
span.platform.linux,
span.version.linux,
body.package div#installs ul.totals span.installs.linux span.key {
    background-color: rgb(196, 112, 112) !important;
}

span.platforms.osx,
span.platform.osx,
span.version.osx,
body.package div#installs ul.totals span.installs.osx span.key {
    background-color: rgb(184, 112, 184) !important;
}

span.platforms.windows,
span.platform.windows,
span.version.windows,
body.package div#installs ul.totals span.installs.windows span.key {
    background-color: rgb(112, 128, 196) !important;
}

body.package div#installs div#daily_installs .layer.linux { fill: rgb(196, 112, 112); }
body.package div#installs div#daily_installs .layer.linux .de-emphasize { fill: rgb(64, 48, 48); }

body.package div#installs div#daily_installs .layer.osx { fill: rgb(184, 112, 184); }
body.package div#installs div#daily_installs .layer.osx .de-emphasize { fill: rgb(56, 40, 56); }

body.package div#installs div#daily_installs .layer.windows { fill: rgb(112, 128, 196); }
body.package div#installs div#daily_installs .layer.windows .de-emphasize { fill: rgb(40, 48, 64); }

body.package div#installs div#daily_installs .grid g.tick.major line,
body.index #realtime .grid .major.tick line {
    stroke: rgb(128, 128, 128);
}
body.package div#installs div#daily_installs .grid line.minor { stroke: rgb(96, 96, 96); }

footer, html {
    background-color: rgb(40, 48, 54);
}

}
