css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.nosub.tv/") {
/* ◆全体・トップページ◆ */
/* 全体 */
html {
    background: #000;
}
#container {
    color: #fff !important;
    background-color: #000 !important;
    margin-top: 49px;
}
#main {
    background-color: #333;
    border: 1em solid #333;
}
.wrapper {
    margin-bottom: 0px;
}
#index-title, #index-view, #index-danmaku, #index-latest, #index-0-title, #calendar {
    background-color: #23282d;
    color: #fff;
    box-shadow: none;
}
a {
    color: #d6d6d6;
    text-decoration: none;
}
a:link {
    color: #ecf2ff;
    -webkit-transition: all 500ms cubic-bezier(0.1, -0.55, 0.265, 1.55);
    -moz-transition: all 500ms cubic-bezier(0, 0, 0.2, 1);
}
a:visited {
    color: #76a3cc;
}
a:hover, a:active {
    color: #ffcfe5 !important;
    text-shadow: #ffca00 0px 0px 10px !important;
}
/* Notice */
#notice-2, #user_panel-2, .user-panel {
    background-color: #23282d !important;
    box-shadow: none !important;
}
#notice-2 ul li:hover {
    border-color: #000;
    background: #000;
}
.widget_notice ul em {
    color: #999;
}
.sidebar .widget_notice ul em {
    color: #999;
}
.sidebar .widget_notice ul li span {
    color: #fff;
}
/* 曜日カレンダー */
#calendar-title, #calendar-1, #calendar-2, #calendar-3, #calendar-4, #calendar-5, #calendar-6, #calendar-7, #calendar-0 {
    background-color: #23282d !important;
    color: #fff !important;
}
#calendar .primary-title ul li a {
    background: radial-gradient(#37394b, #23282d);
}
#calendar .tabs-content li .title-english a, #calendar .tabs-content li .title-japanese a, #calendar .tabs-content li .tv {
    color: #fff !important;
    text-shadow: none !important;
}
.title-japanese a:hover, .title-english a:hover {
    padding-left: 0em !important;
    color:#fff;
    background:#b44376 !important;
    text-shadow:none;
    border-radius: 0px !important;
}
/* 新規投稿リンク */
.primary-1, .primary-2 {
    padding: .5em .5em 0;
    backGround-color: #23282d;
}
.primary-title, .widget-title, .widget-image {
    color: #fff !important;
    background-color: #23282d !important;
    border-bottom: 1px solid #666;
}
.primary-title ul li a {
    color: #fff; 
}

/* Most danmaku */
.sidebar .widget-image-2 li a {
    color: #fff;
}
.widget-image-2 li:hover {
    border-color: #000 !important;
    background: #000 !important;
}
.post-title {
    text-shadow: 0px 0px !important;
}
/* 検索フォーム */
#header #search-container .search-box .search-field {
    color: #fff;
    font-weight: bold;
    background-color: #000;
    padding: .3em .5em;
}
}


@-moz-document url-prefix("http://www.nosub.tv/channel/") {
/* ◆一覧ページ◆ */
/* 全体 */
#main, .primary {
    width: 740px !important;
    margin-left: 110px !important;
}
#sidebar{
    display: none !important;
}
.primary-title {
    padding-bottom: 1px;
}
.primary-title .title a {
    color: #ccc;
}
.primary-3, .primary-4 {
    background: #23282d !important;
    border: .4em solid #333;
}
.primary-3 li:hover, .primary-4 li:hover {
    border-color: #000;
    background: #000;
    box-shadow: 0 1px 0 #000
}
.primary-3 li .post-image, .primary-4 li .post-image {
    border: 2px solid #666;
}
.primary-3 li .post-title a:link, .primary-4 li .post-title a:link {
    color: #e9f1ff;
    font-size: 110%;
    -webkit-transition: all 500ms cubic-bezier(0.1, -0.55, 0.265, 1.55);
    -moz-transition: all 500ms cubic-bezier(0, -0.55, 0.265, 1.55);
}
.primary-3 li .post-title a:visited, .primary-4 li .post-title a:visited {
    color: #76a3cc;
}
.primary-3 li .post-title a:hover, .primary-4 li .post-title a:hover {
    color: #ffcfe5 !important;
    text-shadow: #ffca00 0px 0px 10px !important;
}
/* 投稿一覧 */
#page-links {
    margin: -2px !important;
}
#page-links .current {
    color: #fff !important;
    border-color: #000 !important;
    background: #000 !important;
}
.page-numbers, .page-numbers a:link, page-numbers a:visited, .page-numbers a:active {
    color: #fff !important;
    text-shadow: none !important;
    border-color: #f0599d !important;
    background: #f0599d !important;
}
.page-numbers:hover {
    color: #fff !important;
    border-color: #000 !important;
    background: #000 !important;
}
}


@-moz-document url-prefix("http://www.nosub.tv/search/"), url-prefix("http://www.nosub.tv/?s="), url-prefix("http://www.nosub.tv/page/"), url-prefix("http://www.nosub.tv/watch/tag/")  {
/* ◆検索ページ◆ */
/* 全体 */
#main, .primary {
    width: 740px !important;
    margin-left: 110px !important;
}
#sidebar {
    display: none !important;
}
.primary-title {
    display: none !important;
}
#search-page-form {
    padding: 0.4em;
    background: #333;
    border-bottom: 1px solid #f0599d;
}
#search-page-form .search-field {
    color: #fff;
    font-weight: bold;
    background-color: #000;
}
.primary-4 {
    background: #23282d;
    border: .4em solid #23282d;
}
.primary-4 li {
    padding: 0.2%;
}
.primary-4 li:hover {
    background: #000 !important;
    background-color: #000 !important;
}
.primary-4 li .post-title a, .primary-4 li .post-title a:link {
    color: #e9f1ff;
    font-size: 105%;
    -webkit-transition: all 500ms cubic-bezier(0.1, -0.55, 0.265, 1.55);
    -moz-transition: all 500ms cubic-bezier(0, -0.55, 0.265, 1.55);
}
.primary-4 li .post-title a:visited {
    color: #76a3cc;
}
.primary-4 li .post-title a:hover {
    color: #ffcfe5 !important;
    text-shadow: #ffca00 0px 0px 10px !important;
}
/* 投稿一覧 */
#page-links {
    margin: -2px !important;
}
#page-links .current {
    color: #fff !important;
    border-color: #000 !important;
    background: #000 !important;
}
.page-numbers, .page-numbers a:link, page-numbers a:visited, .page-numbers a:active {
    color: #fff !important;
    text-shadow: none !important;
    border-color: #f0599d !important;
    background: #f0599d !important;
}
.page-numbers:hover {
    color: #fff !important;
    border-color: #000 !important;
    background: #000 !important;
}
}


@-moz-document url-prefix("http://www.nosub.tv/watch/") {
/* ◆動画ページ◆ */
/* 全体 */
.content-max.primary-post {
    background-color: #333;
}
#post-title h1 {
    color: #fff !important;
    text-shadow: #ffca00 0px 0px 1px;
}
/* 動画プレイヤー */
#player-select {
    color: #fff;
    background-color: #000;
    border: none;
    border-radius: 6px;
    height: 1.8em;
}
#player-custom {
    background-color: #000;
    border: none;
    border-radius: 6px;
    height: 1.8em;
    padding-bottom: 6px;
}
#player-content {
    background-color: #333;
}
.primary-post .entry-content {
    margin-top: 0.5em;
    color: #bbb;
    background-color: #000;
    border: 1px solid;
    border-color: #888 #777 #666 #555;
}
/* タグ */
.primary-post .post-tag li a {
    color: #fff;
    background: linear-gradient(#222, #444);
    border: 1px solid #555;
}
/* コメント */
#comments {
    background-color: #23282d;
}
#respond .smilie {
    background-color: #999;
}
.comment-form-comment textarea {
    color: #ccc;
    font-size: 16pt;
    background-color: #000;
    height: 6em;
}
.form-submit .submit {
    color: #ccc;
    background-color: #222;
    -webkit-transition: all 500ms cubic-bezier(0.4, 0, 0.5, 1);
    -moz-transition: all 500ms cubic-bezier(0.4, 0, 0.5, 1);
}
.form-submit .submit:hover {
    color: #ffcfe5;
    text-shadow: #ffca00 0px 0px 10px !important;
    background-color: #333;
    border: 1px solid #f169a6;
}
/* The page you were looking for doesn't exist. */
html{
    background: #000;
}
}


@-moz-document url-prefix("http://www.nosub.tv/watch/author/") {
/* ◆投稿者ページ◆ */
#main, .primary {
    width: 740px !important;
    margin-left: 110px !important;
}
#sidebar {
    display: none !important;
}
#latest {
    margin-top: 0.7em;
}
.primary-title {
    display: none !important;
}
.primary-4 {
    background: #23282d;
    border: 0.5em solid #23282d;
}
.primary-4 li {
    padding: 0.5%;
}
.primary-4 li:hover {
    background: #000 !important;
    background-color: #000 !important;
}
.primary-4 li .post-title a, .primary-4 li .post-title a:link {
    color: #e9f1ff;
    font-size: 105%;
    -webkit-transition: all 500ms cubic-bezier(0.1, -0.55, 0.265, 1.55);
    -moz-transition: all 500ms cubic-bezier(0, -0.55, 0.265, 1.55);
}
.primary-4 li .post-title a:visited {
    color: #76a3cc;
}
.primary-4 li .post-title a:hover {
    color: #ffcfe5 !important;
    text-shadow: #ffca00 0px 0px 10px !important;
}
/* 投稿一覧 */
#page-links {
    margin: -2px !important;
}
#page-links .current {
    color: #fff !important;
    border-color: #000 !important;
    background: #000 !important;
}
.page-numbers, .page-numbers a:link, page-numbers a:visited, .page-numbers a:active {
    color: #fff !important;
    text-shadow: none !important;
    border-color: #f0599d !important;
    background: #f0599d !important;
}
.page-numbers:hover {
    color: #fff !important;
    border-color: #000 !important;
    background: #000 !important;
}
}


@-moz-document url-prefix("http://www.nosub.tv/your-messages") {
/* メッセージ送信ページ */
#page-content {
    margin-bottom: 0em !important;
    padding-bottom: 0.5em;
}
.primary-page {
    color: #fff;
    background-color: #333;
    margin-bottom: 0em !important;
}
#pm-header strong, #pm-header p, #pm-menu, #pm-menu a, #pm-content p, #pm-content form {
    color: #fff !important;
}
}


@-moz-document url-prefix("http://www.nosub.tv/about-us"), url-prefix("http://www.nosub.tv/tos"), url-prefix("http://www.nosub.tv/faq") {
/* ◆AboutUs・TOS・FAQ◆ */
#calendar, .primary-comments, .primary-commtent, .primary-lists, .primary-page, .primary-post, .primary-tabs, .primary .advertising {
    background-color: #23282d;
    margin-left: 0px;
    margin-bottom: 0em;
}
#page-content {
    margin-bottom: 0px;
    padding-bottom: 35px;
}
}
