css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("idealista.com") {
body{
background-color:#999 !important;
text-transform:none !important;
}

#contentright{
width:98% !important;
float: none !important;
}

#contentleft {
width:98% !important;
float:none !important;
}
.ver {
background: #f55 !important;
}

div.active,datos_activ,div.cuerpo{
font-weight: bold !important;
color:#ff8 !important ;
}

a.title{
font-weight: bold !important;
color:#fff !important ;
}




div.comentario {
color:#ff8 !important ;
margin-bottom:0px !important;
border: solid 1px !important;
font-weight: bold !important;
}

div.cuerpo{
color:#000 !important ;
font-weight:normal !important;
}

#datos_noticia{
color:#ff8 !important ;
}


em{
color:#c04 !important;
}

#content {
 color:#fff !important;
}

a:link{
 color:#fff !important;
font-weight:bold !important;
}
a:hover{
 background-color:#000 !important;
}

div.publicado{
color:#fbb !important;
}

#menu a.active{
background-color:#000 !important;
}

ul.opciones{
display: none !important ;
}

#idealista{
display: none !important ;
}
#pie{
display: none !important ;
}
#bloque-19 {
display: none !important ;
}

.comentario.news{
padding-top: 3px !important;
}

div.piecomentario a:link{
font-weight:normal !important;
color:#ff0 !important;
}

#comments .piecomentario a:link {
font-weight:normal !important;
color:#ff0 !important;
}

}
