css:
@-moz-document domain("linux.org.ru") {
html,
body
{
  background: #002222 !important;
  color: #aa0 !important;
}

.code,
code
{
  background: #aaa;
  color: #000;
}

blockquote
{
  font-style: italic !important;
  color: #ffffff;
  background: #111;
  border-left-width: 2px;
  border-right: 0px solid;
  border-bottom: 0px solid;
  border-color: #ff0000;
}

#bd a.tag
{
  color: #d4d4d4;
}

.tracker-item 
{
  background-color: #021 !important;
}

.tracker-last
{
  background-color: #777 !important;
}

.tracker-title .icon-comment p
{
  background-color: #777 !important;
}

.tracker-count .icon-comment p
{
  background-color: #777 !important;
}

.tracker-item a
{
  background-color: #961 !important;
}

.boxlet
{
  background-color: #111111 !important;
  font-style: italic;
  color: #ffffff !important;
  border: 7px solid #000000;
  border-radius: 0;
}

.news
{
  background-color: #001000 !important;
  border: 1px solid #969696;
  border-radius: 0;
}

h1
{
  color: #171 !important;
}

a
{
  /*font-weight: bold;*/
  color: #00adff !important;
}

a.visited
{
  color: #000000 !important;
}

a.link-reply
{
  background-color: black;
}

.infoblock
{
  background: #dcdcdc45 !important;
  border-radius: 0;
}

.messages .msg
{
  background: none repeat scroll 0% 0% #003133;
  border-top: 2px solid;
  border-bottom: 5px solid;
  border-right: 2px solid;
  border-left: 3px solid;
  border-radius: 0em;
  border-color: #bbbbbb;
}

.messages .msg:target
{
  border: 1px solid #101111;
}

.nav-buttons a
{
  color: #ffffff !important;
}

#bd .btn
{
  border: 1px solid #969696;
  color: #ff5454;
}

.page-number
{
  border: 3px solid;
  border-radius: 0em;
  border-color: #010;
  background-color: #004900;
}

#bd .forum table th
{
  background: #8c8c8c !important;
  border: 1px solid #b1b1b1;
}

.message-table .tag
{
  background: #007700;
  color: #000;
}

.message-table
{
  border: 1px solid #b1b1b1;
}

.message-table tr
{
  background-color: #121 !important;
}

.message-table tr:hover
{
  background: #232 !important;
  border: 1px solid #b1b1b1;
}

.message-table a.secondary
{
  color: #ba7608 !important;
}

/* Логотип `LINUX.ORG.RU` */
#sitetitle
{
  color: #0a0 !important;
}

h2 a
{
  color: #de0000 !important;
}

#bd h1,
#bd h2
{
  color: #c4660f;
}

.menu a
{
    color: #aaa !important;
    text-decoration: none;
}

#loginGreating a
{
  color: #0f0 !important;
  text-decoration: none;
}

#loginGreating a:before,
.head a[href*="people"]:before
{
  content: "@";
  font-size: 1em;
}

/* Кнопки фильтрации по категориям */
#bd .btn-selected
{
  background-color: #173;
  color: #c1c1c1;
}

#bd .btn-default
{
  background-color: #002211;
}

#bd .forum table tbody td
{
  border: 0px solid #b1b1b1;
  color: gray;
}

/* Левая колонка таблицы в `Уведомлениях` */
#bd .icon-reply-color,
#bd .icon-tag-color
{
  color: #ebe2e2;
}

#whois_userpic
{
    background: none repeat scroll 0% 0% #e0e0e0;
}

#bd .btn-primary
{
    background-color: #181;
    color: #000;
}

#bd .btn-danger
{
    background-color: #1fafaf;
    color: #ffffff;
}

img
{
    filter: contrast(100%) brightness(90%);
}

.stars
{
  color: #999900;
}

.group-label
{
  background-color: #222222;
}

.fav-buttons .selected [class^="icon-"]
{
  color: #1fafaf;
}

/* Комментарий к юзеру */
.user-remark
{
  color: #3a3a3a;
}

#bd .forum table thead,
#bd .forum table tfoot
{
  color: #ededed;
}

span.group-label
{
    color: #ffffff;
}
}
