css:
@-moz-document domain("tumblr.com") {
/* Pinku - tumblr theme by yunke */
/* pinkjune.tumblr.com */
 
/* Pink background */
.identity, .post_avatar, .l-header-container--refresh, .l-container.l-container--two-column-dashboard .left_column, .l-container.l-container--two-column-dashboard .l-content, .l-container.l-container--two-column-dashboard .right_column, .l-container.l-container--two-column-dashboard .right_column, .l-container.l-container--two-column .right_column, .post_avatar .post_avatar_link, .l-container.l-container--two-column-dashboard .l-content, .l-container.l-container--two-column .l-content, .toastr .toast {
    background: #ffd0d9;
}
/* Hide sidebar item gradients */
.identity .right_column::after, .identity .controls_section.user_list .follow_list_item_blog::before, .radar .radar_footer .radar_avatar::before {
    visibility: hidden;
}
/* Make items less transparent */
.tab_bar .tab_anchor {
    opacity: 1;
}
.identity .controls_section a canvas {
	opacity: 1;
}
/* Make text less transparent */
.identity .controls_section.user_list li .follow_list_item_blog .tumblelog_title, .identity .controls_section a .count, .identity .controls_section .delete_all_messages, .well, .identity .controls_section .control-anchor, .radar .radar_tumblelog_label .name, .radar .radar_tumblelog_label .subheading, .identity .controls_section .open_blog .currently_selected_blog, .identity .controls_section .open_blog .open_blog_link, .identity .controls_section .sub_control, .identity .right_column .small_links a:hover, .identity .right_column .small_links a {
    color: hsla(0,0%,100%,1);
}
.notification, .notification .notification_sentence a, .notification .notification_sentence .username, .notification .notification_sentence .banner_text {
	color: rgba(255,255,255);
}
/* Less transparent sidebar text */
.identity .controls_section li.section_header {
color: #fff;
}
.identity .controls_section.user_list li .follow_list_item_blog .tumblelog_name {
    color: hsla(0,0%,100%,0.8);
}
/*Compose button white color */
.compose-button {
	fill: #fff;
}
/* Pink + white tab notice color */
.tab-bar-container .tab_notice, .tab_notice{
	background: #ffd0d9;
}
.tab-notice--outlined {
	border: 2px solid #fff;
	border-radius: 2px;
}
.tab_notice .tab_notice_value {
	color: #fff;
}
/* Search */
.ui_search .search_query {
	background: #ffd0d9;
	border: 0px solid #fff;
	border-radius: 10px;
}
.ui_search {
	color: hsla(0,0%,100%);
}
/* Pink buttons */
.post-form--controls .controls-container .post-form--save-button .split-button, .post-form--controls .controls-container .post-form--save-button .split-button .button-area, .post-form--controls .controls-container .post-form--save-button .split-button .dropdown-area, .chrome.blue{
    background-color: #ffd0d9
}
.chrome.blue { 
    border: none 
}
.reblog_follow_button .follow-text, .worded-follow-button .follow-text {
	color: #ffd0d9;
}
/* Pink switches */
.binary_switch input[type="checkbox"]:checked ~ .binary_switch_track {
    background-color: #ffd0d9
}
/* Shadow */
.l-header-container--refresh, .post.post_full, .post_avatar .post_avatar_wrapper, .identity .right_column, .tab_notice {
	box-shadow: 0 0 15px 1px hsla(0, 0%, 0%, .08);
}
.post_full .post_permalink {
	border-color: #ffd0d9 #ffd0d9 transparent transparent;
}
/* Elevator */
#discover_actions_discover .elevator, #search_actions_search .elevator {
    background-color: #ffd0d9;
}
/* White plus buttons */
.plus-follow-button {
	background: hsla(0,0%,100%,.0);
	border-radius: 0;
	color: #fff;
}
/* Glass Overlay */
.blue#glass_overlay, .blue#hello_glass, .blue.glass, .post-forms-glass {
	background: rgba(255, 208, 217, .95);
}
.tab-post-type .post-type-name {
    color: #fff;
}
/* Menu */
.popover_header {
	background: #fff;
	border-bottom: 1px solid #e7e7e7;
	font-size: 11px;
	color: #ffd0d9;
}
/* Misc */
.rapid-recs-container .rapid-recs {
    background-image: none;
}
.rapid-recs-container .rapid-recs .rapid-rec-title {
    color: #fff;
}
}
