css:
@-moz-document domain("mangawindow.net")
{

/*Main*/
body { background-color: #1b1b1b; }
h4, .h4 { color:#ececec; }
a:hover { color: #b1b1b1; }
span.highlight-text { color: #b58f00; background-color: #131313; }
*, *::before, *::after { box-sizing: border-box; color: #b1b1b1; }
.modal-content { background-color: #313131; }
.series-browse .item .item-text .item-genre { color: #cecece; }

/*Search Bar*/
.autocomplete-search .autocomplete-search__random:hover { color: #ffffff; }
.autocomplete-search .autocomplete-input { border-color: #3a3a3a; }
.autocomplete-search .autocomplete-input:focus { border-color: #3a3a3a; }
.autocomplete-search .autocomplete-search__random { color: #3a3a3a; }
.autocomplete-search .autocomplete-search__button:hover { background-color: #b1b1b1; }
.autocomplete-search .autocomplete-list ul:before { border-bottom-color: #3a3a3a; }
.autocomplete-search .autocomplete-search__button { color: #fff; border-left: 2px solid #3a3a3a; background-color: #3a3a3a; }
.autocomplete-search .autocomplete-list ul { border: 2px solid #3a3a3a; background: #131313; }
.alert-secondary { color: #d0d0d0; background-color: #020202; border-color: #000000; }
.autocomplete-search .autocomplete-list li.focus-list a { color: #b3b3b3; background-color: #3a3a3a; }


/*Navigation Bar*/
.navbar { background-color: #131313; }
.navbar .content .navbar-search .form-control { color: #d0d0d0; }
.navbar .content .navbar-nav .nav-item.active { background: #3a3a3a; }


/*Latest*/
.filter { background-color: #131313; }
.filter .filter-main .filter-group .filter-items .filter-item.state-0 .icon { color: #bfbfbf; }
.filter .filter-main .filter-group .filter-items .filter-item i.count { color: #28a745; }
.filter .filter-main .filter-group .filter-items .filter-item.state-1 { color: #28a745; }
.filter .filter-main .filter-group .filter-items .filter-item.state-2 { color: #dc3545;	}


/*Browse*/
.browse-pager .sort-set a:hover { color: #cecece; }
.browse-pager .sort-set a.active { color: #ececec; background: #3a3a3a; }
.browse-pager .sort-set { color: #e6e6e6; }
.page-item.active .page-link { color: #000; background-color: #3a3a3a; border-color: #3a3a3a; }
.page-item.disabled .page-link { color: #000000; background-color: #7b7b7b; border-color: #3a3a3a; }
.page-link { color: #000000; background-color: #7b7b7b; border: 1px solid #3a3a3a; }
.page-link:hover { color: #000000; background-color: #dcdcdc; border-color: #4c4c4c; }
.form-control { color: #cecece; background-color: #1b1b1b; border: 1px solid #656565; }
.form-control:focus { color: #dcdcdc; background-color: #1b1b1b; border-color: #676767; box-shadow: 0 0 0 0.2rem rgba(22, 121, 144, 0.25); }
.form-control::placeholder { color: #a0a0a0; }
.series-browse .item .item-text .item-alias { color: grey; }
.series-browse .item.is-hot .item-text .item-title { color: #b58f00; }


/*About Manga*/
#series-page .title-set .item-title a { color: #ececec; }
#series-page .history-set { background-color: #131313; }
#series-page .chapter-list .main .item:hover, #series-page .chapter-list .main .item.hover { background-color: #3a3a3a; }
#series-page .chapter-list .main .item.is-new { background-color: #0e0e0e; }
pre { color: #ececec; }

/*Favorites*/
.alert-info { color: #cecece; background-color: #131313; border-color: #3e3e3e; }
.favorite-folders { border: 3px solid #3e3e3e; }
}
