css:
@-moz-document url-prefix("https://ethmonitoring.com") {
body {
	filter: invert(80%) hue-rotate(180deg) brightness(150%);
	background-color: #474848 !important;
	color: #717171 !important;
	font-size: 13px  !important;
}
h4, .h4, h5, .h5, h6, .h6 { 
	margin-top: 4px !important;
    margin-bottom: 4px !important;
}
h1, .h1, h2, .h2 { font-size: 15px !important }
h3, .h3, h4, .h4 { font-size: 14px !important }
h5, .h5 { font-size: 13px !important }
h6, .h6 { font-size: 12px !important }

/** ~~~~Уменьшение header'a~~~~ **/
.navbar {
    font-size: 12px  !important;
    background-color: #f7f7f7 !important;
	min-height: 21px !important;
    margin-bottom: 8px !important;
    border-bottom: 1px solid #c3c3c3 !important;
}
.navbar-inverse .navbar-collapse { min-width: 980px !important }
.navbar-inverse .navbar-brand, .navbar-inverse .navbar-nav > li > a, .navbar-inverse .btn-link,
.navbar-inverse .navbar-nav > li > a:hover, .navbar-inverse .navbar-nav > li > a:focus { color: #717171 !important }
.navbar-inverse .navbar-nav > .active > a { background: #dedede !important }
.navbar-brand {
    height: 20px !important;
    padding: 4px !important;
    font-size: 13px !important;
    line-height: 13px !important;
}
.navbar-nav > li > a, .nav li > form > button.logout {
	line-height: 12px !important;
    padding-top: 4px !important;
    padding-bottom: 4px !important;
	font-size: 12px;
}

/** ~~~~Content~~~~ **/
.container { width: 1280px !important }
.wrap > .container { padding: 29px 8px 8px !important }

/** Блоки **/
.alert {
    padding: 4px !important;
    margin-bottom: 8px !important;
    color: #717171 !important;
    background-color: #f7f7f7 !important;
    border: none !important;
    border-radius: 2px !important;
}
.panel-heading {
    padding: 4px !important;
    border-top-left-radius: 2px !important;
    border-top-right-radius: 2px !important;
}
.panel-title { font-size: 13px !important }
.panel-primary { border-color: #adadad !important }
.panel-primary > .panel-heading {
    color: #737373 !important;
    background-color: #ebf6ff !important;
    border-color: #b9c6d2 !important;
}
.overview .part {
    background: #efefef !important;
    border-left: 5px solid #cecece !important;
    padding: 4px !important;
}

/** Таблицы **/
.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
	padding: 4px !important;
    line-height: 1 !important;
    font-size: 12px !important;
	border: none !important;
}
.table > tbody > tr.warning > td { background-color: #e9eef3 !important }
/** Селекторы **/
label {
	margin-bottom: 2px !important;
    font-size: 12px !important;
}
.form-group { margin-bottom: 8px !important }
.form-control {
    height: 25px !important;
    padding: 2px !important;
    font-size: 12px !important;
    border-radius: 2px !important;
}
.input-group-html5 .input-group-addon { padding: 0px 6px !important }

/** Кнопки **/
.btn {
	padding: 2px 8px !important;
    font-size: 12px !important;
    line-height: 1 !important;
    border-radius: 2px !important;
}

/** Предупреждения **/
.has-error .help-block, .has-error .control-label, .has-error .radio, .has-error .checkbox, .has-error .radio-inline, .has-error .checkbox-inline, .has-error.radio label, .has-error.checkbox label, .has-error.radio-inline label, .has-error.checkbox-inline label {
    color: #d87573 !important;
    font-size: 12px !important;
	margin-top: 2px !important;
    margin-bottom: 4px !important;
}
.cc_container {
    background: #e0e0e0 !important;
    color: #616161 !important;
    font-size: 15px !important;
	padding: 4px 0 4px 20px !important;
}
.cc_container .cc_btn, .cc_container .cc_btn:visited {
    color: #fff !important;
    background-color: #91c577 !important;
}

/** Стоимость крипты на странице "Miners" **/
.ccc-header-v3-price-logo {
	width: 12px !important;
    height: 12px !important;
    margin-right: 5px !important;
}
.ccc-header-v3 a { margin: 5px !important }

/** Блок "Graphs" на странице "Miners" **/
.panel-body { padding: 4px !important; }
.nav-tabs > li > a {
    margin-right: 2px !important;
    border: none !important;
    padding: 4px !important;
    border-radius: 2px 2px 0 0 !important;
}
.nav-tabs > li.active { background-color: #dedede !important }

/** Главная страница **/
.col-md-12 {
    width: 100%;
    color: #848484 !important;
    background-color: #f1efef;
}
#hero-area { background: #e6e6e6 !important }

}
