css:
@-moz-document domain(twitch.tv){
    
 .tw-pd-3
  {
    background: #ffe8f4 url(/*[[background]]*/) repeat center fixed! important;
  }
    
.tw-pd-t-2 tw-pd-x-3 {
    background: #ffe8f4;
}
    
.top-nav__menu {
    background-color: #ffe9f8;
    border: 1px solid #ffafed;
    border-radius: 2px;
}
    
.top-nav__home-link {
    color: #ffd6f1;
}
    
.top-nav__nav-link {
    color: #ffbaf1;
}
    
.tw-button-icon--overlay, .tw-theme--dark .tw-button-icon--overlay, .tw-theme--light .tw-button-icon--overlay {
    color: #ff9edc;
}
    
.tw-button-icon--overlay:focus, .tw-theme--dark .tw-button-icon--overlay:focus, .tw-theme--light .tw-button-icon--overlay:focus {
    color: #fff;
}
    
.tw-input::placeholder {
    color: #ffaee2;
}
    
.tw-input {
    border: 1px solid #ffaee2;
    background: #ffecf7;
    color: #ffaee2;
}
    
.tw-theme--dark .tw-c-background-alt {
    background-color: #FFE9F8 !important;
}
    
.tw-theme--dark .tw-c-background {
    background-color: #ffe9f8 !important;
}
    
.tw-theme--dark .tw-border-b {
    border-bottom: 1px solid #FFAEE2 !important;
}
    
.tw-interactable:focus, .tw-theme--dark .tw-interactable:focus, .tw-theme--light .tw-interactable:focus {
    -webkit-box-shadow: 0 0 6px 0 #7d5bbe,inset 0 0 0 1px #7d5bbe;
    box-shadow: 0 0 6px 0 #ff9ce5,inset 0 0 0 1px #ff9be1;
}
    
.blue-bar, .tw-theme--dark .blue-bar, .tw-theme--light .blue-bar {
    background: #ffc1ef;
}
    
.tw-c-text {
    color: #ff86e0 !important;
}
    
.tw-c-background {
    background-color: #ffccf5b3 !important;
    border: 1px solid #FFAEE2;
    border-radius: 4px;
}
    
.tw-border-b {
    border-bottom: 1px solid #FFAEE2 !important;
}
    
.tw-c-background-alt {
    background-color: #ffeff9cc !important;
}
    
.tw-c-text-alt-2 {
    color: #ff9edf !important;
}
    
.a {
    color: #ffacea;
    text-decoration: underline;
}
    
.tw-c-text-alt {
    color: #ffa6e8 !important;
}
    
.tw-textarea::placeholder {
    color: #FFCEEB;
}
    
.tw-textarea {
    border: 1px solid #FFAEE2;
    background: #FFECF7;
    color: #FFAEE2;
}
    
.tw-button-icon {
    color: #ffa6e8;
}
    
.tw-button:focus, .tw-theme--dark .tw-button:focus, .tw-theme--light .tw-button:focus {
    border-color: #ffaff3;
    background: #ffdcf2;
    color: #ffaeea;
    -webkit-box-shadow: 0 0 6px 0 #7d5bbe;
    box-shadow: 0 0 6px 0 #ff94e3;
}
    
.tw-button-icon, .tw-theme--dark .tw-button-icon {
    border: 1px solid #ff96dc;
    background: #fff0;
    border-radius: 5px;
}
    
.tw-theme--dark .tw-border-r {
    border-right: 1px solid #FFAEE2 !important;
}
    
.tw-theme--dark .scrollable-area .simplebar-track .simplebar-scrollbar {
    background: #ffa9e6;
}
    
.tw-theme--dark .side-nav__overlay-wrapper, .tw-theme--dark .side-nav__theme-wrapper {
    background-color: #FFE9F8;
}
    
.tw-theme--dark .side-nav-card__link {
    background: #FFE9F8;
}
    
.tw-theme--dark a {
    color: #ff91e3;
}
    
.tw-theme--dark .side-nav-show-more-toggle__button {
    color: #ffbaf0;
}
    
.tw-theme--dark .side-nav-card__title {
    color: #ffc4ee;
}
    
.tw-theme--dark .side-nav-card__metadata {
    color: #fface7;
}
    
.tw-theme--dark .side-nav-card__link {
    background: #ffb1e51a;
}
    
.tw-theme--dark .tw-input {
    border: 1px solid #FFAEE2;
    background: #ffcaf3;
    color: #FFAEE2;
}
    
.tw-theme--dark .tw-c-text-alt {
    color: #FFBAF1 !important;
}
    
.tw-theme--dark .tw-border-t {
    border-top: 1px solid #FFAEE2 !important;
}
    
.tw-button, .tw-theme--dark .tw-button, .tw-theme--light .tw-button {
    border: 1px solid #FF96DC;
    background: #FFBFEA;
    color: #FF96DC;
}
    
.tw-box-art-card__title {
    color: #FFAEE2;
}
    
.scrollable-area .simplebar-track .simplebar-scrollbar {
    background: #FF96DC;
}
    
.carousel-nav__item--active, .tw-theme--dark .carousel-nav__item--active, .tw-theme--light .carousel-nav__item--active {
    border-bottom: 4px solid #FFAEE2;
}
    
.carousel-nav__item {
    border-bottom: 4px solid #ffd1f1;
    cursor: pointer;
}
    
.tw-button-icon--disabled, .tw-button-icon:disabled {
    border-color: rgb(255, 194, 235);
    background: rgba(255, 202, 234, 0.85);
    color: #ffbee7;
    cursor: not-allowed;
}
    
.tw-theme--dark .tw-c-text {
    color: #ffabe6 !important;
    text-decoration: underline;
}

.tw-theme--dark .tw-interactable, .tw-theme--light .tw-interactable {
    color: #fff;
}
    
.user-menu-toggle {
    background: #FFA6E8;
}
    
.channel-header {
    background-color: #FFE9F8;
    border-bottom: 1px solid #FF96DC;
    min-height: 5rem;
    border-radius: 2px;
}
    
.channel-header__user--selected .channel-header__user-avatar::after, .channel-header__user--selected:hover .channel-header__user-avatar::after, .channel-header__user:active .channel-header__user-avatar::after {
    border-bottom-color: #FFAFED;
    opacity: 1;
    width: 4rem;
}
    
.channel-header__item {
    color: #FFA6E8;
}
    
.tw-interactable:focus, .tw-theme--dark .tw-interactable:focus, .tw-theme--light .tw-interactable:focus {
    -webkit-box-shadow: 0 0 6px 0 #7d5bbe,inset 0 0 0 1px #ffaeed;
    box-shadow: 0 0 6px 0 #ff9ce5,inset 0 0 0 1px #ff9be1;
}
    
.tw-c-background-alt-2 {
    background-color: #ffedf9 !important;
}
    
.tw-border-l {
    border-left: 1px solid #FFA6E8 !important;
}
    
.tw-border-r {
    border-right: 1px solid #FFAEE2 !important;
}
    
.tw-border-b {
    border-bottom: 1px solid #FFAEE2 !important;
}
    
.channel-info-bar {
    background-color: #ffdaf4cc;
}
    
.chat-line__moderation, .chat-line__status {
    color: #FFA6E5;
}
    
.tw-c-text {
    color: #FFA6E8 !important;
}
    
.tw-theme--dark .tw-interactable--hover, .tw-theme--dark .tw-interactable:hover {
    background: #FFD6F1;
    color: #fff;
}
    
.tw-theme--dark .side-nav-card__link:hover {
    background: #FFD6F1;
}
    
.tw-theme--dark .side-nav-card__title:hover {
    color: #fff;
}
    
.tw-theme--dark .tw-checkbox__label {
    color: #ffbae4;
}
    
/* Start http://www.cursors-4u.com */ body, a:hover {cursor: url(http://cur.cursors-4u.net/others/oth-6/oth651.cur), progress !important;} /* End http://www.cursors-4u.com */
    
    body, a:hover {
    cursor: url(http://cur.cursors-4u.net/others/oth-6/oth651.cur), progress !important;
}
    
}
