css:
@-moz-document domain("vitalsource.com") {
html {
    background-color: black;
    color: white;
}
body.dash {
    color: white;
    max-width: 90%;
}
body.dash .figure .caption {
    background-color: #222;
    color: white;
}
body.dash .box {
    background-color: #222 !important;
}
body.dash .footnote {
    background-color: black;
}
/* --- Tables --- */
body.dash section.level div.table table thead tr.styled {
    /* table header rows */
    background-color: rgb(70,51,0) !important;
}
body.dash section.level div.table table tbody tr.styled {
    /* odd rows */
    background-color: rgb(36,24,1) !important;
}
/* --- end Tables --- */
/* --- bottom "scrubber" bar --- */
#reader #scrubber-container {
    border: 2px solid #222;
    background-color: black;
}
#scrubber-container .scrubber-tab {
    background-color: darkgrey;
    border: 2px solid lightgrey;
    color: white;
}
#reader #scrubber-container #scrubber.expanded,
#reader #scrubber-container #scrubber {
    background-color: darkgrey;
}
/* --- end bottom "scrubber" bar --- */
/* --- user highlights/notes --- */
.highlight-popover,
#highlight-popover {
    border: 1px solid lightgrey;
    background-color: #222;
}
.vst-list ul {
    background-color: #222;
}
.vst-list ul :hover {
    background-color: lightgrey;
    border: 1px solid black;
    color: black;
}
#highlighter-notes .note-preview {
    color: white;
    background-color: black;
    border: 1px solid lightgrey;
}
#highlighter-notes .note-edit-container textarea {
    color: white;
    background-color: black;
}
#highlighter-notes button.noButton {
    border: 1px solid black;
    width: 1.5em;
}
#highlighter-notes #markdown-buttons-list li {
    margin-right: 2px;
}
#highlighter-notes button.noButton path {
    fill: white;
}
#highlighter-notes #markdown-buttons-list li:hover path {
    fill: black;
}
#highlighter-notes #markdown-buttons-list li button.noButton .tooltip span,
.tooltip span {
    color: white;
    font-weight: bold;
}
#highlighter-notes #markdown-buttons-list li button.noButton .tooltip,
.tooltip {
    border: 1px solid darkgrey;
    background-color: black;
}
.vstcolorf4ed75.vsthighlight {
    background-color: #827547 !important;
}
.vstcolora1ec80.vsthighlight {
    background-color: #507440 !important;
}
/* --- end user highlights/notes --- */
/* --- table of contents / index --- */
.toc-container,
#reader #panel-container .panel .content {
    background-color: #222;
    color: white;
}
#reader #panel-container .panel .header {
    background-color: darkgrey;
}
#panel-container .panel .header .panel-title {
    color: black;
}
.toc-container .up-level {
    background-color: #222;
    color: rgb(92, 240, 240);
}
.toc-container .level-container .toc-level {
    background-color: #222;
    color: white;
}
.toc-container .level-container .toc-level:hover {
    background-color: black;
}
.toc-container .level-container .toc-level .section .page,
.toc-container .level-container .toc-level .section {
    color: white;
}
#panel-container .panel .content .panel-header-container {
    background-color: rgb(10, 10, 10);
}
#toc-Searchfield .search-input form {
    background-color: black;
}
#toc-Searchfield .search-input form #search-input-field {
    color: white;
}
#toc-Searchfield .search-input form ::placeholder {
    color: white !important;
    opacity: 1;
}
.toc-container .level-container {
    background-color: #111;
}
#toc-no-results {
    color: white;
}
/* --- end table of contents / index --- */
/* --- book library --- */
#book-list {
    background-color: #222;
}
.grid .card .card-content {
    background-color: black;
}
.grid .card .card-content .title span {   
    color: white;
}
#library-filter {
    background-color: darkgrey;
}
#library-filter #results-container {
    color: white;
    background-color: #111;
}
/* --- end book library --- */
#reader-handler,
#jigsaw-placeholder-outer,
.transient-loader {
    /* loading spinner screen */
    background-color: black !important;
}
}
