css:
@-moz-document url-prefix("http://seminole.flvs.net"), domain("seminole.flvs.net") {
body {
    background: #1f1f1f;
}
#centered-wrapper {
    background-color: #3e3e3e;
    margin: 0 auto;
    width: 80%;
    padding: 5px 20px 20px;
}
.alert-clear {
    background-color: #fff0;
    border-color: #fff0;
    color: #000;
    margin-right: 1000000px;
    position: absolute;
}
body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 20px;
    color: #f1f1f1;
}
.table-bordered {
    border: 1px solid #dddddd;
    border-collapse: separate;
    border-left: 0;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    color: #ffffff;
}
.table td:first-child, .table td:nth-child(7), .table th:first-child, .table th:nth-child(7) {
    padding: 8px;
    background: #2f2f2f;
}
tr {
    display: table-row;
    vertical-align: inherit;
    border-color: inherit;
    background: #2f2f2f;
}
thead {
    display: table-header-group;
    vertical-align: middle;
    border-color: inherit;
    background: #2f2f2f;
}
.bottom-toolbar-content {
    background-color: #1f1f1f;
    border-top: 1px solid #3e3e3e;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    border-left: 1px solid #3e3e3e;
    border-right: 1px solid #3e3e3e;
    -webkit-border-top-left-radius: 5px;
    -webkit-border-top-right-radius: 5px;
    -moz-border-radius-topleft: 5px;
    -moz-border-radius-topright: 5px;
    padding: 5px 5px 10px;
}
.well {
    min-height: 20px;
    padding: 19px;
    margin-bottom: 20px;
    background-color: #3e3e3e;
    border: 1px solid #2f2f2f;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);
    -moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);
}
.option-choice {
    background-color: #353535;
    padding: 8px 35px 8px 14px;
    border: 2px solid #CCC;
    border-radius: 4px;
}
hr {
    margin: 20px 0;
    border: 0;
    border-top: 0px solid #eeeeee;
    border-bottom: 0px solid #ffffff;
}
img {
    max-width: 100%;
    width: auto\9;
    height: auto;
    vertical-align: middle;
    border: 0;
    -ms-interpolation-mode: bicubic;
    background: #f1f1f1;
}
.points-left-column, .points-right-column {
    background-color: #2f2f2f;
    width: 45%;
    float: left;
}
.essay-submission {
    background-color: #3e3e3e;
    border: 1px solid #f1f1f1;
    border-radius: 4px;
    margin-bottom: 20px;
    padding: 8px 35px 8px 14px;
    text-shadow: 0 1px 0 rgba(255,255,255,.5);
    font-family: Georgia,"Times New Roman",Times,serif;
    font-size: 16px;
}
}
