    background-color: transparent;
    padding-left: 5px;
    margin-left: 12px;
    border-radius: 0px;
    position: absolute;
    left: 20%;
    visibility: visible !important;
}
#nav-message span[title|="0"]::after {
    content: "0";
    color: #4D4D4D;
}
#nav-friends span[title|="0"]::after {
    content: "0";
    color: #4D4D4D;
}
#navigation,
#navigation .rbx-scrollbar,
#navigation #mCSB_1,
#navigation #mCSB_1_container {
    overflow: visible !important;
}
#nav-friends {
    left: 799px;
    width: 100px;
}
nav-friends:hover {
    background-color: transparent !important;
}
#nav-friends .notification-blue {
    background-image: url("https://www.dropbox.com/s/bits9jf80fk6xxx/fnds.png?dl=1");
    background-position: left center;
    background-repeat: no-repeat;
    color: #0059B3 !important;
}
#nav-message .notification-blue {
    background-image: url("https://www.dropbox.com/s/lbj5nnbgzzzgzhn/msgs.png?dl=1");
    background-position: left center;
    background-repeat: no-repeat;
    color: #4D4D4D !important;
}
#nav-character {
    margin-left: 100px
}
.icon-nav-home,
.icon-nav-profile,
.icon-nav-message,
.icon-nav-friends,
.icon-nav-charactercustomizer,
.icon-nav-inventory,
.icon-nav-trade,
.icon-nav-group,
.icon-nav-forum,
.icon-nav-blog,
.icon-nav-shop,
#nav-shop {
    width: 0px !important;
    display: none !important;
}
.game-card .rbx-votes {
    visibility: hidden !important;
    position: fixed !important;
    left: -100% !important;
}
.rbx-left-col,
.rbx-scrollbar,
#mCSB_1_container {
    width: auto !important;
}
#mCSB_1_container {
    width: 900px !important;
    margin-left: calc(50% - 425px);
    top: 0px !important;
    overflow: visible !important;
}
#mCSB_1_container ul {
    overflow: visible !important;
}
#mCSB_1_container ul li {
    overflow: visible !important;
}
#mCSB_1_container ul li a:hover {
    background-image: url("https://www.dropbox.com/s/cd42tke2k4qa94z/topNav_blue.png?dl=1");
    background-size: 100% 200%;
    background-position: left 100%;
}
#mCSB_1_container ul li span {
    margin-top: 5px !important;
    font-weight: 600;
}
.rbx-upgrade-now,
.rbx-nav-sponsor,
#nav-home,
.rbx-text-notes {
    position: fixed !important;
    left: -100% !important;
    z-index: -1 !important;
    visibility: hidden !important;
    margin: 0px !important;
}
#nav-blog {
    font-size: 0px !important;
    padding-top: 5px;
}
#nav-blog::after {
    content: "News";
    font-size: 16px;
    font-weight: 600 !important;
}
#navigation ul .rbx-lead {
    position: absolute !important;
    z-index: 100 !important;
    top: 0px !important;
    overflow: visible !important;
}
li {
    margin-top: 0px !important;
    margin-bottom: 0px !important;
}
ul {
    padding: 0px !important;
}
body {
    background-image: url('https://www.dropbox.com/s/hqlldpsbrfycszo/bkg.jpg?dl=1');
}
#header {
    background-image: url('https://www.dropbox.com/s/lzuigi311mu5dkt/head_03.png?dl=1');
}
#wrap,
#MasterContainer,
.container-footer {
    background-image: url('https://www.dropbox.com/s/3joroiq8mbqj4ln/normcolour.png?dl=1');
}
}
