css:
@-moz-document domain("www.myfitnesspal.com") {
#ember2102 {
    display: none;
}

div.column-right {
    display: none;
}

div.column-left {
    width: 100%;
}

div.summary-info {
    width: 85% !important;
}

body .energy-calculation {
    width: 100% !important;
}

div.NewsFeed {
    max-width: 100% !important;
    width: 100% !important;
}

#ember2033 div.body,
#navTop li.last {
    display: none;
}

body#food-diary table tbody tr td.first {
    width: 100%;
    max-width: none;
}
    
body#excercise-diary table.table0 tbody tr.bottom td.first {
    width: 60%;
    max-width: none;
}

body #main,
body#excercise-diary div#main {
    width: 100%;
}

}
