@-moz-document domain("magister.net") {
.menu {
	z-index: 99;
	background-color: #232323;
}
.menu a:hover,
.menu .navTrigger:hover {
	background-color: #494949;
} 
.menu > ul {
	margin-top: 32px;
	border-top: 8px solid #ffffff;
	clear: left;
}
.menu ul ul {
	border: 1px solid #434343;
}
.menu a {
	position: relative;
	padding-left: 0;
	display: block;
	height: 44px;
	line-height: 44px;
	color: #ffffff;
	overflow: hidden;
	border-bottom: 0px solid #666666;
}
.menu .navTrigger {
	display: block;
	float: left;
	width: 100%;
	height: 48px;
	cursor: pointer;
	border-bottom: 0px solid #666666;
}
.main {
	position: relative;
	float: left;
	padding: 48px 0 0 0;
	width: 100%;
	height: 100%;
}
.widget .list li:hover,
.widget .list li.active {
	background-color: #e8e8e8;
}
.master-view {
	padding-left: 43px;
}
.head-bar {
	position: absolute;
	top: 0px;
	right: 267px;
	left: 0;
	z-index: 680;
	padding-left: 8px;
	height: 48px;
	line-height: 46px;
	color: #ffffff;
	border-bottom: none;
	background-color: #232323;
	font-size: 14px;
}
#berichten-container {
	padding-right: 51px;
}
.container {
	position: relative;
	height: 100%;
	padding-right: 293px;
}
#vandaag-container {
	padding-right: 0px;
}
#instellingen-container {
	padding-right: 0px;
}
.widget {
	padding: 0px 0px 0 0;
	font-family: tahoma, sans-serif;
	font-size: 11px;
}
#vandaag-container div.content-container {
	padding-left: 0px;
	padding-right: 0px;
}
.content-container {
	z-index: 98;
}
.content-container {
	position: relative;
	height: 100%;
	width: 100%;
	border: 0px solid #cccccc;
	background-color: #fafafa;
	text-align: left;
	overflow: auto;
}
#agenda-section .content-container,
#examendetail-container .content-container {
	padding-left: 0px;
}
.master-view.nav-toggle {
	padding-left: 186px;
}
.sidecolumn .head-bar {
	right: 294px;
}
body {
	margin: 0;
	padding: 0;
	height: 100%;
	text-align: center;
	background-color: #f2f2f2;
	font-family: arial;
	font-size: 13px;
}
aside .widget {
	padding-right: 2px;
}
aside {
	position: absolute;
	top: 0px;
	bottom: 0;
	right: 0;
	padding-right: 44px;
	padding-top: 2px;
	width: 294px;
	z-index: 100;
	overflow: hidden;
}
.sidecolumn .head-bar {
	right: 270px;
}
aside .sheets .tabsheet {
	padding: 0 0 2px 2px;
}
aside .head-bar {
	border: none;
	width: 294px;
	position: absolute;
	left: 8px !important;
	top: 0;
	text-align: left;
}
aside .tabs li {
	position: relative;
	float: left;
	margin-right: 0px;
	min-width: 134px;
	height: 42px;
	line-height: 44px;
	font-size: 13px;
}
aside .sheets {
	position: relative;
	height: 100%;
	padding-top: 47px;
	width: 100%;
	overflow-y: auto;
	overflow-x: hidden;
}
.block {
	position: relative;
	border: 1px solid #efefef;
	background-color: #ffffff;
	-moz-border-radius: 3px;
	-webkit-border-radius: 3px;
}
.header {
	z-index: 101;
	height: 48px;
	background-color: #232323;
}
.header > div + div {
	position: relative;
	float: right;
	border-left: 0px solid #59bbe8;
}
#studiewijzer-container div.studiewijzer-list > ul > li > a > span {
	padding-left: 8px;
	float: left;
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	border-right: 1px solid #e8e8e8;
}
.header .logo {
	float: left;
	margin-left: 6px;
	font-size: 18px;
	line-height: 48px;
	color: #ffffff;
	border: none;
	font-family: "droid_sansregular", arial, helvetica, sans-serif;
}
.header .logo > a {
	color: #ffffff;
}
.sm-grid.k-grid .k-grid-content tr.k-state-selected,
.sm-grid.k-grid .k-grid-content tr.k-state-selected.k-state-focused,
.sm-grid.k-grid .k-grid-content tr.k-state-selected .k-state-focused {
	cursor: pointer;
	background-color: #eeeeee;
}
.sm-grid.k-grid .k-grid-content tr:hover {
	background: #e8e8e8;
}
.header .header-user:hover,
.header .user:hover {
	background-color: #494949;
}
.head-bar div > a:hover {
	background: #494949;
}
.head-bar div > a {
	display: block;
	padding: 0 8px 0 8px;
	border-left: 0px solid #59bbe8;
	height: 47px;
	line-height: 46px;
	color: #ffffff;
}
.header div.logoff:hover {
	background-color: #494949;
}
/* login form */

.login-dialog:before,
.change-password-dialog.no-preview:before {
	position: relative;
	float: left;
	width: 0px;
	height: 0px;
	background: transparent no-repeat top left;
	-moz-box-shadow: 1px 0 5px -1px rgba(0, 0, 0, 0.7);
	-webkit-box-shadow: 1px 0 5px -1px rgba(0, 0, 0, 0.7);
	box-shadow: 1px 0 5px -1px rgba(0, 0, 0, 0.7);
	content: '';
}
.login-dialog .content-pane,
.change-password-dialog.no-preview .content-pane {
	padding-left: 0px;
}
.dialog {
	top: 50%;
	left: 50%;
	border: 0px solid #cccccc;
}
.btn-primary3 {
	margin: 1px;
	color: #FFF;
	font-size: 12px;
	text-align: center;
	height: 36px;
	line-height: 34px;
	min-width: 86px;
	text-decoration: none;
	border: 0px solid #0096db;
	border-radius: 2px;
	background: #343434;
	font-family: Tahoma;
	padding: 0 12px;
	display: block;
	width: 86px;
	text-transform: lowercase;
}
.btn-primary2:hover,
.btn-primary3:hover {
	background: #494949;
}
.account-dialog {
	z-index: 1;
	margin-top: -175px;
	margin-left: -200px;
	width: 400px;
	height: 350px;
}
.account-dialog .title {
	font-size: 18px;
	color: #ffffff;
	background-color: #232323;
}
.dialog .title {
	font-family: arial;
}
.checkbox-input input[type="checkbox"]:checked + label span:after {
	color: #343434;
}
.dialog-overlay:before {
	background-color: #080c11;
}
.header .header-user a,
.header .user a {
	padding: 0 0px;
	padding-top: 0px;
	padding-right: 0px;
	padding-bottom: 0px;
	padding-left: 0px;
}
.head-bar .foldout {
	display: block;
	opacity: 0;
	position: absolute;
	z-index: 998;
	right: -4px;
	top: 42px;
	max-height: 0;
	max-width: 210px;
	width: 210px;
	font-family: tahoma, sans-serif;
	font-size: 11px;
	text-align: left;
	-moz-box-shadow: 3px 3px 5px -2px rgba(0, 0, 0, 0.5);
	-webkit-box-shadow: 3px 3px 5px -2px rgba(0, 0, 0, 0.5);
	box-shadow: 3px 3px 5px -2px rgba(0, 0, 0, 0.5);
	border: 1px solid #333333;
	background-color: #333333;
	overflow: hidden;
}

.menu li.active, .menu li.highlight-menu.active, .menu li.active > a:hover{
    background-color: /*[[primair]]*/;
}


aside .tabs li {
    position: relative;
    float: left;
    margin-right: 0px;
    min-width: 134px;
    height: 42px;
    line-height: 44px;
    font-size: 13px;
    color: /*[[primair]]*/;
}

aside .tabs li.active:after {
    display: block;
    position: absolute;
    left: 50%;
    margin-left: -3px;
    width: 0;
    height: 0;
    content: "";
    border-style: solid;
    border-width: 6px;
    -webkit-transform: rotate(0.01deg);
    border-color: /*[[primair]]*/ transparent transparent transparent;
    border-color: /*[[primair]]*/ rgba(255, 255, 255, 0) rgba(255, 255, 255, 0) rgba(255, 255, 255, 0);
    top: 53px;
}


aside .tabs li.active a {
    margin-top: -3px;
    height: 44px;
    border: 1px solid #cccccc;
    border-bottom: none;
    background-color: /*[[primair]]*/;
    color: #ffffff;
    -moz-box-shadow: inset 0 0 0 0 transparent;
    -webkit-box-shadow: inset 0 0 0 0 transparent;
    box-shadow: inset 0 0 0 0 transparent;
}

#cijfers-leerling .last-grade {
    float: left;
    padding: 11px 0 0 0;
    margin: 8px 0 8px 8px;
    height: 80px;
    width: 80px;
    text-align: center;
    color: #ffffff;
    background-color: /*[[primair]]*/;
    font-size: 36px;
    font-family: "droid_sansregular", arial, helvetica, sans-serif;
    border-radius: 5px;
}


.block h3 b {
    font-weight: normal;
    display: block;
    white-space: nowrap;
    overflow: hidden;
    -moz-text-overflow: ellipsis;
    -ms-text-overflow: ellipsis;
    text-overflow: ellipsis;
    color: /*[[primair]]*/;
}

.endlink a {
    display: block;
    float: right;
    padding: 0 8px;
    height: 40px;
    line-height: 40px;
    color: /*[[primair]]*/;
    text-decoration: underline;
}

.agenda-text-icon[icon-type="information"] {
    background-color: /*[[primair]]*/;
    color: #ffffff;
}

.agenda-text-icon.outline {
    color: /*[[primair]]*/;;
    background-color: #ffffff;
    border: 1px solid /*[[primair]]*/;;
}


aside .tabs li a:hover{
     color: /*[[primair]]*/;
}

.endlink a:hover {
    display: block;
    float: right;
    padding: 0 8px;
    height: 40px;
    line-height: 40px;
    color: /*[[primair]]*/;
    opacity: 0.5;
    text-decoration: underline;
}

.glyph.important:before {
    content: "+";
    color: #ff0010;
    margin: 0;
}

.block h3 .icon-back:before {
    float: left;
    width: 15px;
	color: /*[[primair]]*/;
}

}

@-moz-document url-prefix("https://accounts.magister.net") {

.bg6_webp {
